import{_ as l,c as a,a as e,o as n}from"./app-C7fJT9JS.js";const r={};function s(i,t){return n(),a("div",null,[...t[0]||(t[0]=[e("p",null,"  绿水青山就是金山银山。生态环境保护关系人民福祉，关系民族未来。近年来，我国坚持生态优先、绿色发展，持续推进生态文明建设，生态环境质量总体改善，但仍面临不少突出问题。为此，应坚持系统观念，统筹山水林田湖草沙一体化保护和修复，推动形成绿色发展方式和生活方式，共同建设美丽中国。",-1),e("p",null,"  统筹山水林田湖草沙一体化保护和修复，筑牢生态安全屏障。生态系统是自然界中生物与环境相互作用形成的复杂系统，山水林田湖草沙作为生态系统的重要组成部分，彼此联系、相互影响。要坚持系统观念，统筹谋划、整体推进，推动形成保护和修复合力，提升生态系统质量和稳定性。要加强重点生态功能区保护，实施重要生态系统保护和修复重大工程，持续推进天然林保护、退耕还林还草、湿地保护修复等专项行动，增强生态系统自我修复能力，筑牢生态安全屏障。",-1),e("p",null,"  推动形成绿色发展方式和生活方式，实现人与自然和谐共生。要坚持节约优先、保护优先、自然恢复为主，推动形成绿色发展方式。要加快建设资源节约型、环境友好型社会，推进能源生产和消费革命，发展循环经济，推动绿色低碳技术创新和应用，促进经济社会发展全面绿色转型。要倡导绿色生活方式，普及绿色消费理念，推动形成绿色生产和消费模式，让绿色成为社会文明风尚。",-1),e("p",null,"  共建美丽中国，需要全社会共同努力。政府要加强生态环境保护顶层设计，完善生态环境保护法律法规，强化生态环境监管执法，推动形成绿色发展政策体系。企业要践行绿色发展理念，加强绿色技术研发和应用，推动产业绿色转型升级。公众要增强生态文明意识，践行绿色生活方式，积极参与生态环境保护，共同建设美丽中国。",-1),e("p",null,"  绿水青山，永续发展。让我们坚持系统观念，统筹山水林田湖草沙一体化保护和修复，推动形成绿色发展方式和生活方式，共同建设美丽中国，让青山常在、绿水长流、蓝天永驻。",-1)])])}const o=l(r,[["render",s]]),d=JSON.parse('{"path":"/sl/igdtszec/","title":"生态","lang":"zh-CN","frontmatter":{"title":"生态","createTime":"2025/12/12 15:45:19","permalink":"/sl/igdtszec/"},"readingTime":{"minutes":2.3,"words":690},"git":{"createdTime":1765528551000,"updatedTime":1765528551000},"filePathRelative":"sl/4.例子/1.生态.md","headers":[]}');export{o as comp,d as data};
