import{_ as a,c as n,a as e,o as r}from"./app-wrW76zHf.js";const c={};function s(i,t){return r(),n("div",null,[...t[0]||(t[0]=[e("h2",{id:"重要会议",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#重要会议"},[e("span",null,"重要会议")])],-1),e("p",null,"在庆祝中华全国总工会成立100周年暨全国劳动模范和先进工作者表彰大 会上的讲话 1.100 年来的实践充分证明，我国工人阶级不愧是中国共产党最坚实最可靠的阶级基础，不愧是我们 社会主义国家的领导阶级，不愧是先进生产力和生产关系的代表，不愧是坚持和发展中国特色社会主义的 主力军。我国工会不愧是党联系职工群众的桥梁和纽带，不愧是社会主义国家政权的重要社会支柱，不愧 是 职工利益的代表者和维护者。不论时代条件和社会群体怎样发展变化，我国工人阶级的地位和作用不容 动摇 ，全心全意依靠工人阶级的根本方针不容动摇，我国工会的性质和职能不容动摇。 2.100 年来党的工运事业理论创新和实践发展的最重要成果，就是形成了中国特色社会主义工会发展 道路 。这条道路，坚持党对工运事业和工会工作的全面领导，坚持全心全意依靠工人阶级的根本方针，坚 持服从和服务于党的中心任务，坚持工会组织的政治性、先进性、群众性，充分发挥党联系职工群众的桥 梁和纽带作用；坚持以服务职工群众为生命线，坚持依法依章程开展工作，是党领导工运事业和工会工作 宝贵经验的深刻总结，必须长期坚持。 3.党的中心任务就是我国工人运动的时代主题。要深入践行社会主义核心价值观，大力弘扬劳模精神、 劳动精神、工匠精神 。 4.推进高质量就业，有序提高劳动、技能、知识、创新等要素在收入分配中的权重。 5.劳动模范和先进工作者要珍惜荣誉、保持本色，继续努力、再立新功。 6.要坚持以职工为中心的工作导向，竭诚服务职工群众、促进职工全面发展。推动完善劳动法律法规， 健全以职工代表大会为基本形式的企事业单位民主管理制度。 7.工运事业是党的事业的重要组成部分。各级党委要加强和改进对工会的领导，及时研究解决工会工 作中的重大问题，为工会履职创造良好条件",-1)])])}const o=a(c,[["render",s]]),d=JSON.parse('{"path":"/xc/xczyhy/","title":"重要会议","lang":"zh-CN","frontmatter":{"title":"重要会议","createTime":"2026/01/22 00:00:00","permalink":"/xc/xczyhy/"},"readingTime":{"minutes":2.26,"words":678},"git":{},"filePathRelative":"xc/5.政治理论/2.重要会议.md","headers":[]}');export{o as comp,d as data};
