import{_ as p,c as e,e as o,o as l}from"./app-Dkl8Q4TB.js";const d={};function b(h,t){return l(),e("div",null,[...t[0]||(t[0]=[o('<h1 id="花生十三高频成语800词" tabindex="-1"><a class="header-anchor" href="#花生十三高频成语800词"><span>花生十三高频成语800词</span></a></h1><div class="hint-container info"><p class="hint-container-title">注意</p><ol><li>本800词是花生十三讲义整理而成。</li><li>目的仅为方便查找。</li></ol></div><table><thead><tr><th>成语分类</th><th>个数</th><th>成语</th><th style="text-align:left;"></th></tr></thead><tbody><tr><td>【第一组】 中华文明 传统文化</td><td>形容中华文化</td><td>5</td><td style="text-align:left;">源远流长、连绵不绝、博大精深、历久弥新、兼收并蓄</td></tr><tr><td>【第二组】 文化传承</td><td>传承不间断</td><td>5</td><td style="text-align:left;">一脉相承、血脉相通、薪火相传、不绝如缕、陈陈相因</td></tr><tr><td></td><td>传承方式</td><td>5</td><td style="text-align:left;">口耳相传、难以为继、后继无人、继往开来、承上启下</td></tr><tr><td>【第三组】 改革创新</td><td>改良旧的 换新的</td><td>7</td><td style="text-align:left;">推陈出新、吐故纳新、革故鼎新、弃旧图新、去芜存菁 除旧布新、激浊扬清</td></tr><tr><td>【第四组】 做法、构思创新</td><td>形容创新、独特</td><td>5</td><td style="text-align:left;">另辟蹊径、标新立异、与时俱进、剑走偏锋、不拘一格</td></tr><tr><td></td><td>心思独特、创新</td><td>2</td><td style="text-align:left;">匠心独运、别出心裁</td></tr><tr><td></td><td>与众不同</td><td>4</td><td style="text-align:left;">独树一帜、自成一家、别开生面、不落窠臼</td></tr><tr><td>【第五组】 守旧不创新</td><td>形容守旧不创新</td><td>5</td><td style="text-align:left;">墨守成规、抱残守缺、因循守旧、固步自封、食古不化</td></tr><tr><td>【第六组】 按规矩办事</td><td>形容按规矩办事</td><td>4</td><td style="text-align:left;">按部就班、循规蹈矩、循规拘礼、照本宣科</td></tr><tr><td></td><td>有步骤有条理</td><td>2</td><td style="text-align:left;">循序渐进、有条不紊</td></tr><tr><td>【第七组】 处境困难</td><td>不敢前进</td><td>3</td><td style="text-align:left;">望而却步、望而生畏、畏葸不前</td></tr><tr><td></td><td>不好抉择</td><td>6</td><td style="text-align:left;">骑虎难下、进退维谷、进退失据、瞻前顾后、踟蹰不前 裹足不前</td></tr><tr><td></td><td>处境艰难</td><td>2</td><td style="text-align:left;">举步维艰、寸步难行</td></tr><tr><td>【第八组】 形势危险</td><td>形势危险</td><td>4</td><td style="text-align:left;">岌岌可危、危如累卵、命悬一线、风雨飘摇</td></tr><tr><td></td><td>侧重时间</td><td>1</td><td style="text-align:left;">危在旦夕</td></tr><tr><td>【第九组】 事物出现、 发展、衰落</td><td>出现、萌生</td><td>7</td><td style="text-align:left;">层出不穷、风起云涌、方兴未艾、雨后春笋、横空出世 异军突起、应运而生</td></tr><tr><td></td><td>发展势头好</td><td>5</td><td style="text-align:left;">高歌猛进、如火如荼、如日中天、势如破竹、突飞猛进</td></tr><tr><td></td><td>形容发展迅速</td><td>2</td><td style="text-align:left;">一日千里、日新月异</td></tr><tr><td></td><td>逐渐衰落</td><td>3</td><td style="text-align:left;">日渐式微、每况愈下、江河日下</td></tr><tr><td></td><td>其他</td><td>5</td><td style="text-align:left;">水涨船高、此消彼长、波澜壮阔、柳暗花明、拨云见日</td></tr><tr><td>【第十组】 竞争比较</td><td>和别人相比较</td><td>3</td><td style="text-align:left;">相形见绌、出类拔萃、无与伦比</td></tr><tr><td></td><td>表现突出</td><td>3</td><td style="text-align:left;">脱颖而出、崭露头角、大显身手</td></tr><tr><td></td><td>赶得上/赶不上</td><td>2</td><td style="text-align:left;">望其项背、望尘莫及</td></tr><tr><td></td><td>同等地位</td><td>4</td><td style="text-align:left;">相提并论、同日而语、等量齐观、一概而论</td></tr><tr><td>【第十一组】 能力不足</td><td>能力有限</td><td>5</td><td style="text-align:left;">力有未逮、爱莫能助、鞭长莫及、望洋兴叹、回天乏术</td></tr><tr><td></td><td>应付不了</td><td>5</td><td style="text-align:left;">捉襟见肘、顾此失彼、左支右绌、独木难支、应接不暇</td></tr><tr><td>【第十二组】 印象和效果</td><td>形容学习粗浅</td><td>6</td><td style="text-align:left;">浮光掠影、走马观花、蜻蜓点水、浅尝辄止、囫囵吞枣 不求甚解</td></tr><tr><td></td><td>形容学到一点 就能推知更多</td><td>3</td><td style="text-align:left;">举一反三、融会贯通、触类旁通</td></tr><tr><td>【第十三组】 搭档配合</td><td>合作配合</td><td>5</td><td style="text-align:left;">相辅相成、相得益彰、锦上添花、珠联璧合、互为表里</td></tr><tr><td></td><td>二者都好</td><td>3</td><td style="text-align:left;">交相辉映、相映成趣、双峰并峙</td></tr><tr><td>【第十四组】 工作开展顺序</td><td>工作开展顺序</td><td>4</td><td style="text-align:left;">并行不悖、齐头并进、双管齐下、并驾齐驱</td></tr><tr><td>【第十五组】 错误抉择和方向</td><td>错误抉择</td><td>5</td><td style="text-align:left;">本末倒置、舍本逐末、喧宾夺主、因小失大、因噎废食</td></tr><tr><td></td><td>相悖、相反</td><td>4</td><td style="text-align:left;">背道而驰、南辕北辙、分道扬镳、缘木求鱼</td></tr><tr><td>【第十六组】 错误做法(寓言故事)</td><td>错误做法</td><td>10</td><td style="text-align:left;">拔苗助长、越俎代庖、削足适履、闭门造车、刻舟求剑 守株待兔、掩耳盗铃、邯郸学步、东施效颦、与虎谋皮</td></tr><tr><td>【第十七组】 教育影响批评</td><td>影响感化</td><td>5</td><td style="text-align:left;">潜移默化、耳濡目染、如沐春风、润物无声、成风化人</td></tr><tr><td></td><td>教育和批评</td><td>6</td><td style="text-align:left;">耳提面命、苦口婆心、循循善诱、诲人不倦、春风化雨 和风细雨</td></tr><tr><td></td><td>醒悟、唤醒</td><td>2</td><td style="text-align:left;">醍醐灌顶、振聋发聩</td></tr><tr><td>【第十八组】 工作学习状态</td><td>工作谨慎认真</td><td>2</td><td style="text-align:left;">如履薄冰、兢兢业业</td></tr><tr><td></td><td>尽心钻研学习</td><td>8</td><td style="text-align:left;">殚精竭虑、呕心沥血、苦心孤诣、废寝忘食、皓首穷经 心无旁骛、乐此不疲、挖空心思（贬义）</td></tr><tr><td></td><td>抓紧时间、勤奋</td><td>4</td><td style="text-align:left;">夜以继日、焚膏继晷、夙兴夜寐、目不窥园</td></tr><tr><td></td><td>辛苦奔波</td><td>6</td><td style="text-align:left;">栉风沐雨、风餐露宿、筚路蓝缕、披星戴月、摩顶放踵 胼手胝足</td></tr><tr><td></td><td>勤勉不懈怠</td><td>2</td><td style="text-align:left;">孜孜不倦、孜孜以求</td></tr><tr><td>【第十九组】 正确做法</td><td>分析问题</td><td>3</td><td style="text-align:left;">条分缕析、抽丝剥茧、深入浅出</td></tr><tr><td></td><td>抓住问题关键</td><td>3</td><td style="text-align:left;">纲举目张、提纲挈领、以一持万</td></tr><tr><td></td><td>向他人学习</td><td>2</td><td style="text-align:left;">博采众长、见贤思齐</td></tr><tr><td></td><td>踏实的做法</td><td>2</td><td style="text-align:left;">量力而行、厚积薄发</td></tr><tr><td></td><td>勇于承担责任义务</td><td>3</td><td style="text-align:left;">当仁不让、义不容辞、责无旁贷</td></tr><tr><td>【第二十组】 错误做法</td><td>不能这样做</td><td>10</td><td style="text-align:left;">过犹不及、矫枉过正、于事无补、作茧自缚、扬汤止沸 隔靴搔痒、临渊羡鱼、有恃无恐、堂而皇之、明目张胆</td></tr><tr><td>【第二十一组】 某些国家的 错误做法</td><td>不能这样做</td><td>5</td><td style="text-align:left;">画地为牢、以邻为壑、穿凿附会、亦步亦趋、唯利是图</td></tr><tr><td></td><td>旁观、得利</td><td>6</td><td style="text-align:left;">隔岸观火、置身事外、袖手旁观、作壁上观、坐收渔利 坐享其成</td></tr><tr><td></td><td>过度开发</td><td>5</td><td style="text-align:left;">竭泽而渔、杀鸡取卵、寅吃卯粮、饮鸩止渴、急功近利</td></tr><tr><td>【第二十二组】 国家安定与动荡</td><td>生活不如意</td><td>3</td><td style="text-align:left;">颠沛流离、流离失所、背井离乡</td></tr><tr><td></td><td>社会安定</td><td>3</td><td style="text-align:left;">安居乐业、海晏河清、国泰民安</td></tr><tr><td></td><td>国人习惯</td><td>1</td><td style="text-align:left;">安土重迁</td></tr><tr><td>【第二十三组】 国际国内关系</td><td>同呼吸共命运</td><td>1</td><td style="text-align:left;">休戚与共</td></tr><tr><td></td><td>融洽和谐</td><td>5</td><td style="text-align:left;">同舟共济、同心同德、和衷共济、水乳交融、相濡以沫</td></tr><tr><td></td><td>和邻居的关系</td><td>4</td><td style="text-align:left;">守望相助、一衣带水、唇齿相依、以邻为壑</td></tr><tr><td></td><td>世界局势</td><td>3</td><td style="text-align:left;">波诡云谲、风云变幻、纵横捭阖</td></tr><tr><td></td><td>坏人坏事</td><td>2</td><td style="text-align:left;">沆瀣一气、同流合污</td></tr><tr><td>【第二十四组】 政府优秀做法</td><td>有针对性</td><td>4</td><td style="text-align:left;">因地制宜、量体裁衣、对症下药、有的放矢</td></tr><tr><td></td><td>发挥职能</td><td>3</td><td style="text-align:left;">因势利导、保驾护航、穿针引线</td></tr><tr><td></td><td>正确做法</td><td>3</td><td style="text-align:left;">广开言路、集思广益、正本清源</td></tr><tr><td>【第二十五组】 合理规划</td><td>节约成本</td><td>4</td><td style="text-align:left;">开源节流、精打细算、细水长流、量入为出</td></tr><tr><td>【第二十六组】 政策措施的评价</td><td>有效果</td><td>4</td><td style="text-align:left;">立竿见影、一劳永逸、行之有效、一蹴而就</td></tr><tr><td></td><td>解决问题</td><td>4</td><td style="text-align:left;">迎刃而解、事半功倍、釜底抽薪、标本兼治</td></tr><tr><td>【第二十七组】 成功与失败</td><td>轻松成功</td><td>5</td><td style="text-align:left;">触手可及、唾手可得、手到擒来、信手拈来、游刃有余</td></tr><tr><td></td><td>没能成功</td><td>6</td><td style="text-align:left;">束手无策、一筹莫展、功亏一篑、适得其反、功败垂成 失之交臂</td></tr><tr><td>【第二十八组】 领导力</td><td>眼界高考虑全</td><td>4</td><td style="text-align:left;">高屋建瓴、高瞻远瞩、深谋远虑、审时度势</td></tr><tr><td></td><td>筹划全面</td><td>2</td><td style="text-align:left;">运筹帷幄、统筹兼顾</td></tr><tr><td></td><td>以身作则</td><td>6</td><td style="text-align:left;">身体力行、亲力亲为、克己奉公、以身作则、上行下效 事必躬亲</td></tr><tr><td></td><td>始终坚持</td><td>2</td><td style="text-align:left;">一以贯之、矢志不渝</td></tr><tr><td>【第二十九组】 正义精神和做法</td><td>形容英勇无畏</td><td>3</td><td style="text-align:left;">前仆后继、视死如归、大义凛然</td></tr><tr><td></td><td>形容振作奋发</td><td>4</td><td style="text-align:left;">踔厉奋发、笃行不怠、赓续前行、奋楫争先</td></tr><tr><td></td><td>形容克服困难前进</td><td>2</td><td style="text-align:left;">砥砺前行、勇毅前行</td></tr><tr><td>【第三十组】 干部易犯错误</td><td>爱慕名利不务实</td><td>4</td><td style="text-align:left;">好高骛远、好大喜功、沽名钓誉、附庸风雅</td></tr><tr><td></td><td>只顾自己、 自扫门前雪</td><td>2</td><td style="text-align:left;">独善其身、明哲保身</td></tr><tr><td></td><td>虚伪敷衍谄媚</td><td>8</td><td style="text-align:left;">虚与委蛇、委曲求全、敷衍塞责、曲意逢迎、投其所好 阿谀奉承、趋炎附势、如蚁附膻</td></tr><tr><td></td><td>错误政策</td><td>3</td><td style="text-align:left;">兴师动众、劳民伤财、朝令夕改</td></tr><tr><td></td><td>不认真</td><td>3</td><td style="text-align:left;">粗枝大叶、大而化之、具体而微</td></tr><tr><td></td><td>其他错误</td><td>2</td><td style="text-align:left;">哗众取宠、凌空蹈虚</td></tr><tr><td>【第三十一组】 对待错误的态度</td><td>谦虚接受建议</td><td>4</td><td style="text-align:left;">从谏如流、从善如流、闻过则喜、虚怀若谷</td></tr><tr><td></td><td>遮掩错误</td><td>3</td><td style="text-align:left;">文过饰非、怙恶不悛、欲盖弥彰</td></tr><tr><td></td><td>不考虑别人建议</td><td>2</td><td style="text-align:left;">刚愎自用、固执己见</td></tr><tr><td></td><td>彻底改变</td><td>3</td><td style="text-align:left;">洗心革面、脱胎换骨、改头换面</td></tr><tr><td>【第三十二组】 改变与不变</td><td>不变</td><td>2</td><td style="text-align:left;">一成不变、亘古不变</td></tr><tr><td></td><td>改变</td><td>2</td><td style="text-align:left;">改弦更张、改弦易辙</td></tr><tr><td></td><td>变化多，快</td><td>2</td><td style="text-align:left;">变幻莫测、瞬息万变</td></tr><tr><td></td><td>长时间繁荣昌盛</td><td>2</td><td style="text-align:left;">经久不衰、长盛不衰</td></tr><tr><td></td><td>变化巨大</td><td>7</td><td style="text-align:left;">今非昔比、天翻地覆、时过境迁、沧海桑田、斗转星移 白云苍狗、恍如隔世</td></tr><tr><td></td><td>难以改变</td><td>3</td><td style="text-align:left;">根深蒂固、积习难改、积重难返</td></tr><tr><td>【第三十三组】 顺势造势</td><td>顺势</td><td>5</td><td style="text-align:left;">顺势而为、大势所趋、势在必行、随波逐流、因陋就简</td></tr><tr><td></td><td>造势</td><td>2</td><td style="text-align:left;">推波助澜、顺水推舟</td></tr><tr><td>【第三十四组】 自然会发生</td><td>事情发生合乎情理</td><td>5</td><td style="text-align:left;">理所当然、顺理成章、天经地义、水到渠成、顺其自然</td></tr><tr><td>【第三十五组】 提早准备和补救</td><td>前期准备</td><td>6</td><td style="text-align:left;">未雨绸缪、常备不懈、防患未然、防微杜渐、居安思危 曲突徙薪</td></tr><tr><td></td><td>准备得当没有忧虑</td><td>1</td><td style="text-align:left;">高枕无忧</td></tr><tr><td></td><td>出现问题进行补救</td><td>3</td><td style="text-align:left;">临渴掘井、江心补漏、亡羊补牢</td></tr><tr><td>【第三十六组】 办公、管理相关</td><td>形容工作开展的局 面、形势不好</td><td>3</td><td style="text-align:left;">尾大不掉、各行其是、各自为政</td></tr><tr><td></td><td>形容人工作的状态</td><td>3</td><td style="text-align:left;">如鱼得水、面面俱到、独当一面</td></tr><tr><td>【第三十七组】 空谈与虚幻</td><td>不靠谱的做法</td><td>5</td><td style="text-align:left;">纸上谈兵、无根之木、坐而论道、水中捞月、画饼充饥</td></tr><tr><td></td><td>实现不了</td><td>3</td><td style="text-align:left;">空中楼阁、镜花水月、海市蜃楼</td></tr><tr><td>【第三十八组】 来者众多</td><td>形容到来</td><td>4</td><td style="text-align:left;">接踵而至、络绎不绝、纷至沓来、蜂拥而至</td></tr><tr><td></td><td>形容人多、人少</td><td>5</td><td style="text-align:left;">门庭若市、无人问津、门庭冷落、门可罗雀、人迹罕至</td></tr><tr><td></td><td>偶遇</td><td>3</td><td style="text-align:left;">不期而遇、不期而至、萍水相逢</td></tr><tr><td></td><td>一同出现</td><td>1</td><td style="text-align:left;">如影随形</td></tr><tr><td>【第三十九组】 人多人少</td><td>形容人多，热闹</td><td>5</td><td style="text-align:left;">熙熙攘攘、人山人海、车水马龙、川流不息、万人空巷</td></tr><tr><td></td><td>形容孤独</td><td>4</td><td style="text-align:left;">孑然一身、茕茕孑立、形影相吊、形单影只</td></tr><tr><td>【第四十组】 好坏掺杂</td><td>好坏都有</td><td>6</td><td style="text-align:left;">参差不齐、泥沙俱下、良莠不齐、鱼龙混杂、鱼目混珠 滥竽充数</td></tr><tr><td></td><td>不分好坏</td><td>1</td><td style="text-align:left;">买椟还珠</td></tr></tbody></table><hr><h3 id="【第一组】中华文明传统文化-5-个" tabindex="-1"><a class="header-anchor" href="#【第一组】中华文明传统文化-5-个"><span>【第一组】中华文明传统文化（5 个）</span></a></h3><table><thead><tr><th>形容中华文化</th><th>源远流长、连绵不绝、博大精深、历久弥新、兼收并蓄</th></tr></thead></table><ol><li>成语解释</li></ol><blockquote><p>源远流长</p><p>源头远，水流长。也比喻历史悠久。</p></blockquote><p>【例】中国和印度尼西亚隔海相望，两国友好关系的历史源远流长。</p><p>【例】习近平指出，中斯友好源远流长，两国人民有着天然的亲近感。</p><p>【例】如果不从源远流长的历史连续性来认识中国，就不可能理解古代中国，也不可能理解现代中国,更不可能理解未来中国。</p><blockquote><p>连绵不绝</p><p>意思是连续而不中断。</p></blockquote><p>【例】中华民族精神之所以连绵不绝、代代相传、历久弥新，是因为其有着深厚的历史土壤和文化根脉。</p><p>【例】这些入选全国十大考古新发现的考古成果，描绘出神州大地上早期人类的起源历程，展示着中华文 明的灿烂成就，也一次次为五千年连绵不绝的中华文明提供实证。</p><p>【例】魏晋南北朝是中国历史上一段黑暗的“乱世”。在此期间，连绵不绝的军阀混战和地方割据， 持续不断的族群侵并、政权倾覆，频繁的人口迁徙和灾难。</p><blockquote><p>博大精深</p><p>博大：广大。精深：精湛深刻。形容学识、思想、理论广博丰富，精湛深刻。</p></blockquote><p>【例】有人惊叹，祖国的语言文字博大精深，过去却知之不多，真是惭愧。</p><p>【例】馆内设有基本陈列“中华农业文明”、专题陈列“中国传统农具”“中国土壤标本”“彩韵陶 魂——田士利捐赠彩陶展”，还有农业科普馆、二十四节气传统农事园等，生动展示着历史悠久、博大精 深的中华农业文明。</p><p>【例】中国茶种类繁多，中国茶文化博大精深。</p><blockquote><p>历久弥新</p><p>弥，更加。指经历长久的时间而更加鲜活，更加有活力，更显价值。</p></blockquote><p>【例】富强作为一种价值追求，不仅为当代中国社会普遍认同，而且深深积淀在中华传统文化中，堪 称历久弥新。</p><p>【例】新时代新征程，“枫桥经验”必将薪火相传，历久弥新，续写新的精彩华章。</p><p>【例】跨越一甲子，从乡村实践蝶变为国家治理之策，已成为“中国之治”金名片的“枫桥经验”， 闪耀着历久弥新的基层治理大智慧。</p><blockquote><p>兼收并蓄</p><p>并：一起。蓄：积聚，储存。对不同的人或事物都能同时收容、保留。</p></blockquote><p>【例】中国有厚重的文化底蕴，加上兼收并蓄的好学传统，经过时间的沉淀，这些优势渐渐显露出来。</p><p>【例】习近平总书记在文化传承发展座谈会上指出：“中华文明具有突出的包容性”“决定了中华文 化对世界文明兼收并蓄的开放胸怀”。</p><p>【例】中华文明始终在兼收并蓄中历久弥新，不仅为中华民族提供了丰厚滋养，而且为世界文明贡献了华彩篇章。</p><hr><h3 id="【第二组】-文化传承-10-个" tabindex="-1"><a class="header-anchor" href="#【第二组】-文化传承-10-个"><span>【第二组】 文化传承（10 个）</span></a></h3><table><thead><tr><th>传承不间断</th><th>一脉相承、血脉相通、薪火相传、不绝如缕、陈陈相因</th></tr></thead><tbody><tr><td>传承方式</td><td>口耳相传、难以为继、后继无人、继往开来、承上启下</td></tr></tbody></table><ol><li>成语解释</li></ol><table><thead><tr><th>传承不间断</th><th>一脉相承、血脉相通、薪火相传、不绝如缕、陈陈相因</th></tr></thead></table><blockquote><p>一脉相承</p><p>一脉：指一个血统或一个派系。相承：相续传承。由一个血统或一个派别世代相传承袭下来。比喻人 或事物间的传承关系。也作“一脉相传”“一脉相通”。</p></blockquote><p>【例】良渚遗址是中华五千年文明史的实证，是世界文明的瑰宝。在悠远的历史长河中，中华文明以 独树一帜的创新创造、一脉相承的坚持坚守，树立起一座座文明高峰。</p><p>【例】红旗渠精神同延安精神是一脉相承的，是中华民族不可磨灭的历史记忆，永远震撼人心。年轻 一代要继承和发扬吃苦耐劳、自力更生、艰苦奋斗的精神，摒弃骄娇二气，像我们的父辈一样把青春热血 镌刻在历史的丰碑上。</p><p>【例】在历史发展中淬炼而成的中华法系彰显了中华优秀传统法律文化自我发展、回应挑战的主体性 与生命力，也反映了中华文明一脉相承的连续性。中华文明具有突出的创新性，这是对中华文明内生动力 的高度概括。</p><blockquote><p>血脉相通</p><p>血脉：血统。原指有血缘亲属关系。也比喻极亲近的关系。</p></blockquote><p>【例】中泰两国毗邻而居，血脉相通。共建“一带一路”使两国关系好上加好、亲上加亲。我们要弘 扬传统友好，加强高层往来，引领好两国关系发展</p><p>【例】干部要把人民放在心中最高位置。同人民风雨同舟、血脉相通、生死与共，是我们党战胜一切 困难和风险的根本保证。离开了人民，我们就会一事无成。</p><p>【例】从甲骨文、金文到大篆、小篆，再到隶书、楷书，汉字历经数千年演变，使中国人得以突破时 空限制沟通思想。这种文化的创造与传承，承载着中华民族共同的文化记忆，成为具有强大民族凝聚力的 文化基因和中华儿女血脉相通的精神纽带。</p><blockquote><p>薪火相传</p><p>意思是比喻学问和技艺代代相传。比喻师生授受不绝，或种族、血统、文化精神的传承，绵延不尽。</p></blockquote><p>【例】少儿京剧表演《九龙杯》《定军山》、武戏《戏武英豪》各具特色，彰显传统艺术的薪火相传 与勃勃生机。</p><p>【例】今年 4 月，德班理工大学孔子学院的师生用中文给我写信，表达对中华文化的喜爱，感谢中国 为非洲青年追求梦想提供宝贵机会，让我感到十分亲切。丰富多彩的人文交流有力促进民心相通，使两国 友好薪火相传。</p><p>【例】从建科元老徐光炜，到季加孚、李子禹，再到如今的陕飞，在他们薪火相传的带领下，胃肠肿 瘤中心一病区以个体化治疗为宗旨，积极推广胃肠肿瘤的规范化手术，实现了胃癌领域的多个首创：率先 在全国推广胃癌D2 根治术的标准化手术。</p><blockquote><p>不绝如缕</p><p>绝：断。缕：细线。像一根细线那样，似断非断。也作“不绝若线”。比喻情势危急。</p></blockquote><p>【例】许多古老的民间艺术，代代有传人，不绝如缕。</p><p>【例】我国富民思想源远流长，古代典籍中关于裕民、惠民、富民的论述不绝如缕。比如，《尚书中记有“德惟善政，政在养民”；《周礼》中写道“以富邦国，以养万民，以生百物”；等等。</p><p>【例】我国传统书院的文化传统和精神内核有其旺盛的生命力，千百年来，书院精神始终不绝如缕。因此，理论上说，现代书院制承袭了我国传统书院制的名称并复归部分精神内涵。</p><blockquote><p>陈陈相因</p><p>粮仓里的米谷一年接一年地堆积起来。比喻沿袭老一套，没有改进。</p></blockquote><p>【例】延续民族文化血脉并不等于陈陈相因、泥古守旧，而是要与时俱进、推陈出新，让中华优秀传 统文化焕发出勃勃生机。</p><p>【例】止步于程序性的、领导交办的事务性工作，固守于旧有的工作方法、工作模式和工作路径，满 足于干好手头的、眼前的、具体负责的事情，其结果必然是陈陈相因无法突破、陷入事务主义之中无法自 拔，对新形势新任务新挑战缺乏信心。</p><p>【例】习近平总书记指出：“故步自封、陈陈相因谈不上传承，割断血脉、凭空虚造不能算创新。要 把握传承和创新的关系，学古不泥古、破法不悖法，让中华优秀传统文化成为文艺创新的重要源泉。</p><table><thead><tr><th>传承方式</th><th>口耳相传、难以为继、后继无人、继往开来、承上启下</th></tr></thead></table><blockquote><p>口耳相传</p><p>口说耳听，递相传授。</p></blockquote><p>【例】据《南郑县志》记载，汉中编织业历史悠久。当地民间口耳相传，藤编可追溯至汉代，楚汉相 争的关键一环“明修栈道，暗度陈仓”，汉军便用到了藤编。明清之际，汉中货栈业兴起，水陆码头流通 的货物中多有竹、木、棕、藤制品，足堪佐证当时藤编发展已有规模</p><p>【例】此外，西方童话之所以在我国大行其道，很大程度上在于综合运用了多种媒体，顺应了从口耳相传到听觉、视觉、触觉、生活日用相互影响的时代变化。</p><p>【例】这些人物为官清正的历史故事，原本就在当地群众中口耳相传，微电影上线后，人们“眼见为 实”，加深了对故事内涵的理解，民间廉洁文化的影响力由此得到放大和强化。</p><blockquote><p>难以为继</p><p>难以继续下去。</p></blockquote><p>【例】公司资金链断裂，经营已难以为继。</p><p>【例】没有了后续的支持，这项工程很快就难以为继。</p><blockquote><p>后继无人</p><p>继：继续，继承。没有继承的人。形容事业缺少接班人。</p></blockquote><p>【例】因后继无人，日本九州和冲绳地区出现企业并购潮。</p><p>【例】马向阳介绍，白族扎染技艺虽然早已被列入第一批国家级非物质文化遗产名录，但是目前注册 在案的国家级非物质文化遗产传承人仅剩一位。这样一种民族瑰宝，陷入了后继无人的局面。</p><p>【例】随着时代的变迁，娱乐方式的日益增多，川剧逐渐被新鲜事物扯离人们的视线，一度面临后继 无人的境况。近年来，随着加强文化遗产保护传承、弘扬中华优秀传统文化思想的深入，乐至川剧再次焕 发荣光。</p><blockquote><p>继往开来</p><p>继承前人的事业，开辟未来的道路。</p></blockquote><p>【例】林剑说，在中欧建交 50 周年这一承上启下的重要时间点，中方期待欧方同中方一道，秉持建 交初心，坚持伙伴定位，加强对话合作，妥处分歧差异，推动中欧关系继往开来，书写更多更加精彩成功 的中欧合作故事。</p><p>【例】“五一施政纲领”将新民主主义理论与陕甘宁边区局部执政实践相结合，聚焦“抗战”“建国” 两大目标，既是我党局部执政的基本方略，也是陕甘宁边区建设新民主主义共和国雏形的根本法，在我党 执政历史上具有继往开来的历史地位。</p><p>【例】持续深化以党史为重点的“四史”学习教育，加强全国职工爱国主义教育基地建设，因地制宜 开展参观瞻仰、纪念缅怀、现场教学、学习体验、主题党日、研讨交流等活动，教育引导广大职工知史爱 党、知史爱国，继往开来走好新时代长征路。</p><blockquote><p>承上启下</p><p>接续上面的，引起下面的。也指诗文中连接沟通文字的字句，使文字自然过渡。</p></blockquote><p>【例】在这篇论文中，第三章起到了承上启下的作用，它总结了前两章的研究成果，并为后续章节的 深入讨论打下了坚实的基础。</p><p>【例】这位领导者的发言在会议上起到了承上启下的作用，他不仅总结了之前的工作进展，还提出了 未来的发展规划，让所有人明确了方向。</p><hr><h3 id="【第三组】-改革创新-7-个" tabindex="-1"><a class="header-anchor" href="#【第三组】-改革创新-7-个"><span>【第三组】 改革创新（7 个）</span></a></h3><table><thead><tr><th>改良旧的换新的</th><th style="text-align:left;">推陈出新、吐故纳新、革故鼎新、弃旧图新、去芜存菁、 除旧布新、激浊扬清</th></tr></thead></table><ol><li>成语解释</li></ol><blockquote><p>推陈出新</p><p>推去旧的，产生新的。后多指在文化艺术方面去掉旧的糟粕，吸取其精华，创造出新的来。（多形容 文化但不绝对）</p></blockquote><p>【例】我们研究历史不是为了复古，而是要借鉴历史、推陈出新。</p><p>【例】春节假期，各地夜经济消费业态推陈出新，消费场景不断丰富，为人们带来物质、精神的双重满足。</p><p>【例】坚持高标准、高品质，在严控质量的基础上推陈出新，才能让名优特产品走向更广阔的农业大 市场。</p><blockquote><p>吐故纳新</p><p>纳：使进入，吸入。道家的养生术，吐出浊气，吸进新鲜空气。后多比喻扬弃旧的，吸收新的。（多形容事物在发展中呈现新的面貌）</p></blockquote><p>【例】换届，就要吐故纳新，换出新精神，新思路和新气象。</p><p>【例】把政策、文化、科技、卫生、信息送下乡，让美丽乡村吐故纳新，呈现出新时代新农村应有的 新气象。</p><p>【例】春天，万物更新，吐故纳新。于我们而言，既要从传统文化中汲取优秀因子，也当积极移风易 俗、摒弃陈规陋习。</p><blockquote><p>革故鼎新</p><p>革：除去。鼎新：立新。指革除旧的，建立新的。多指改朝换代或重大变革。（多政治角度）</p></blockquote><p>【例】太平天国定都南京后，革故鼎新，颁布《天朝田亩制》。</p><p>【例】教育家精神蕴含着“求是创新”的鲜明导向，体现革故鼎新、与时俱进的精神追求。</p><p>【例】广大工程师深入学习贯彻习近平新时代中国特色社会主义思想，坚持“四个面向”，以与时俱 进的精神、革故鼎新的勇气、坚韧不拔的定力，不断突破关键核心技术，铸造精品工程、“大国重器”。</p><blockquote><p>弃旧图新</p><p>意思是抛弃旧的，谋求新的。多指由坏的转向好的，离开错误的道路走向正确的道路。</p></blockquote><p>【例】他说，韩国政治应弃旧图新，推行改革，推翻独断专横的两党政治体系，创出新的多党政治体 系，从而迎接充满希望与活力的民主政治时代。</p><p>【例】在创作中，徐怀中尽力摆脱那些有形无形的思想禁锢，清除残留的概念化、公式化影响，实现 弃旧图新轻装上阵。</p><p>【例】上好弃旧图新“必修课”不断优化调整出监教育课程，认真开展形势政策、法律法规、社会美 德、心理健康、社会适应性等教育。</p><blockquote><p>去芜存菁</p><p>意思是除去杂质、保留精华。</p></blockquote><p>【例】从石库门到天安门，从兴业路到复兴路，党的百年奋斗是一座去芜存菁的历史熔炉，是一条大 浪淘沙的时间长河。坚持自我革命，这是中国共产党历经百年沧桑而青春依旧、长成参天巨树还岁岁发新 的基本逻辑。</p><p>【例】去芜存菁，才能更好地服务群众。不过，清理一部分账号，不等于“一关了之”。</p><p>【例】生活服务类平台覆盖的应用场景多而广，用户规模持续增长，每天产生海量信息，只有抓住关 键环节，进行手术刀式的清理，才能去芜存菁。</p><blockquote><p>除旧布新</p><p>意思是清除旧的，建立新的；以新的代替旧的。</p></blockquote><p>【例】一座城市就和一个人一样有新陈代谢，需要除旧布新，要梳洗打扮才能面目清新。</p><p>【例】中国年，以其久远的文化底蕴和精神内涵，凝结着中国人对除旧布新、纳福迎祥的良好希冀。</p><p>【例】封建社会上千年来的枷锁太严密，非进行彻底的社会改造不能除旧布新。</p><blockquote><p>激浊扬清</p><p>强调通过主动干预清除负面因素并弘扬正面价值，侧重点在于“激”与“扬”的联动作用，既包含对 浑浊、消极现象的批判与清除，又包含对清澈、积极价值的彰显与推广。</p></blockquote><p>【例】 党的十八大以来，我们深入贯彻中央八项规定精神，遏制“舌尖上的浪费”，整治“会所中的 歪风”，刹住“车轮上的铺张”，管住了那些以往有的党员干部习以为常、容易忽视的小事小节，迎来了 一场激浊扬清的风气巨变。</p><p>【例】要扛稳扛牢反腐惩恶的政治责任，保持一以贯之、一抓到底的决心和韧劲，围绕“聚焦转型发 展增动能、聚力攀高克难防风险”年度工作主线，靶向发力抓治理，激浊扬清鼓正气，坚定不移把反腐败 斗争推向纵深。</p><p>【例】近年来，国家通过制定和实施民法典等法律法规，运用法治手段解决道德领域突出问题，依法 惩处失德败德的违法行为，依法保护诚实守信、助人为乐行为，起到了激浊扬清、扶正祛邪的治理效果。</p><hr><h3 id="【第四组】做法、构思创新-11-个" tabindex="-1"><a class="header-anchor" href="#【第四组】做法、构思创新-11-个"><span>【第四组】做法、构思创新（11 个）</span></a></h3><table><thead><tr><th>形容创新、独特</th><th>另辟蹊径、标新立异、与时俱进、剑走偏锋、不拘一格</th></tr></thead><tbody><tr><td>心思独特、创新</td><td>匠心独运、别出心裁</td></tr><tr><td>与众不同</td><td>独树一帜、自成一家、别开生面、不落窠臼</td></tr></tbody></table><ol><li>成语解释</li></ol><table><thead><tr><th>形容创新、独特</th><th>另辟蹊径、标新立异、与时俱进、剑走偏锋、不拘一格</th></tr></thead></table><blockquote><p>另辟蹊径</p><p>意思是另外开辟一条路。比喻另创一种风格或方法。</p></blockquote><p>【例】在建设制造强国的新征程上，自行车企业该如何另辟蹊径，不断塑造自身品牌和优势，形成中 国自行车企业在全球市场的竞争力和影响力？这些都需要企业在创新求变中给出答案。</p><p>【例】出于对流量的畸形追求，有人编撰“爽文”，有人打造“爆文”，有人则另辟蹊径炮制“骂文” ——本着“黑红也是红，骂名也是名”的原则，有人甚至不吝于从骂声中积攒人气、转化商机。</p><p>【例】不过，随着乡村振兴的不断推进和城市中书店竞争越来越激烈，一些书店转而瞄准乡村另辟蹊径。</p><blockquote><p>标新立异</p><p>标：表明。异：独特，与众不同。原意为表明新颖的义理，提出与众不同的见解。后指故意提出新奇 的见解，表示自己与众不同。（主观上故意性；辨析感情色彩）</p></blockquote><p>【例】大舅母的话也不对，若是没有人标新立异，世界上哪还有进步？</p><p>【例】今天的我们应该从那些失败的设计中有所领悟，而不是重复失败。请记得：这些建筑虽然分布 于世界各地，但它们却拥有共同特点，一是耗费巨资，二是过于标新立异。</p><p>【例】时下这种文化保守主义仍然暗流涌动，许多看似是传统文化的重光，实际却是肤浅的标新立异，甚至有的妄图通过“立儒教为国教”来消弭马克思主义在意识形态领域的指导地位。</p><blockquote><p>与时俱进</p><p>时：时间。俱：一起，一同。进：前进。与时间一起前进。指不断进取，永不停滞。</p></blockquote><p>【例】马克思主义具有与时俱进的理论品格。</p><p>【例】如今，从脱贫攻坚到乡村全面振兴，中国乡村建设与时俱进，不断取得新成绩。</p><p>【例】与时俱进的创新品格，是中华民族创造博大精深的文化并形成数千年绵延不断的伟大文明的重 要保障，也是我们继续取得成功的精神财富。</p><blockquote><p>剑走偏锋</p><p>指不走常规，找一些新的、不同以往的办法来解决问题，以求出奇制胜。</p></blockquote><p>【例】种种前车之鉴，就是因为在文旅创新中，找到了好特色、也打磨出了好创意，但是没有把握好 细节与边界，剑走偏锋而遭到反噬。</p><p>【例】实践中也不乏剑走偏锋者，从俊男靓女的媚俗化表演，到搞怪扮丑的庸俗化套路，再到肤浅浮 夸的娱乐化宣传，画风逐渐走偏、举止愈加出格。</p><p>【例】和谐的医患关系是现代医院管理中非常重要的环节，对尊重患者，获得患者认可的医疗行为进 行表扬或者奖励，是应该的，但这不能“剑走偏锋”，混淆基本的是非观念。</p><blockquote><p>不拘一格</p><p>不拘一格指不拘泥于一种规格、方式，比喻打破常规。</p></blockquote><p>【例】近年来，南京破除“论资排辈”“以帽取人”，不拘一格评才荐才。从创业支持到企业成长， 从人才落户安居到家属就医求学，南京以顶格诚意广邀“创新合伙人”。千金买骨，良驹自来。</p><p>【例】茶叙，成为中越两党两国领导人不拘一格的交往方式。从北京人民大会堂的冬日茶叙，到河内胡志明故居的品茗畅谈，再到此次越共中央驻地茶叙……一杯清茶，见证交往佳话，传递深情厚谊。</p><p>【例】从高处俯瞰，丽江古城并不追求方正规整，城镇布局随形就势，高低错落的屋顶轮廓勾勒出不 NSUhEUgAAABMAAAAGCAYAAAAhS6XkAAAABHNCSVQICAgIfAhkiAAAAAlwSFlzAAAOxAAADsQBlSsOGwAAAIpJREFUGJWVzz0OAQEQBeCPZkW9hYRG4wwKiUIUEjeUuIHWEXQugSAISj8JminW2gQvecV7M/Nmhk+UglDOeAq8rP8mGuiijiZmGGKKNk5Yo4MJBphjhFs+rI8x7rggxQo1nJGggkMs3uCBHvb5sAQtXIMpFnHpMepV7CJsG3PLojeL9Dc8/+z/HS91QRkOhmNa8AAAAABJRU5ErkJggg==)拘一格的线条。</p><table><thead><tr><th>心思独特、创新</th><th>匠心独运、别出心裁</th></tr></thead></table><blockquote><p>匠心独运</p><p>匠心：精巧的心思。具有独到的创造性。多指文学艺术的创作构思。同“独具匠心”“匠心独运”。 （侧重文艺创作的心思巧妙）</p></blockquote><p>【例】博物馆任意门、博物馆翻翻看、文物卷轴……“龙江博物馆文物旅行箱”以匠心独运的形式将 各类互动创新体验与龙江文化相结合，通过自然、历史和艺术三个板块诠释龙江精品。</p><p>【例】开赛仪式上，赛事组委会匠心独运，将泳池用特殊材料制作的白布覆盖，利用投影方式在泳池 上投射出钟楼、大雁塔等西安古迹的影像。</p><p>【例】非遗传承，依赖传承人的匠心独运、继承创新，也依赖当地的文化资源规划和政策保护。</p><blockquote><p>别出心裁</p><p>心裁：内心的决断。另外想出与众不同的办法、主意。也作“独出心裁”。（侧重想法构思巧妙）</p></blockquote><p>【例】6 月 16 日上午，在国网庆城县供电公司组织的“安全月”宣传活动中，一份别出心裁的安全宣 传“资料”吸引了广大群众前来围观。</p><p>【例】为了更好地呈现人物，研习社在选角时别出心裁。史欣玉对“探春”的表演者印象深刻。这位表演者的五官英气中带着倔强，在才艺展示中声情并茂地朗诵一段探春的台词，获得选角小组一致好评。</p><p>【例】在新修建的 1 号码头漕运码头，记者看到，地面别出心裁地设置了几块铜版雕刻样式的地砖， 上面镌刻了记录不同历史时期潮白河变迁的文字。岸边是两座酷似船舱造型的长廊，连接着宽敞的浮桥向 水面延伸。</p><table><thead><tr><th>与众不同</th><th>独树一帜、自成一家、别开生面、不落窠臼</th></tr></thead></table><blockquote><p>独树一帜</p><p>单独树起一面旗帜。比喻创造独特风格，自成一家。同“别树一帜”。（侧重风格）</p></blockquote><p>【例】程砚秋在京剧旦角中独树一帜，以“程腔”闻名于世。</p><p>【例】在悠远的历史长河中，中华文明以独树一帜的创新创造、一脉相承的坚持坚守，树立起了一座 座文明高峰。</p><p>【例】淄博烧烤和天水麻辣烫有哪些“独树一帜”的特色，能够吸引游客纷至沓来？以美食圈粉的过 程，又是如何融入文化元素的？</p><blockquote><p>自成一家</p><p>意思是指在某一方面的学问或技术有独到的见解或独特的做法，能自成体系。</p></blockquote><p>【例】90 年代，石齐正式提出自己的三象合一艺术创作理念，自成一家。</p><p>【例】他的词不似温庭筠一般过于含蓄，也不似韦庄一般过于柔美，亦不似苏轼一般过于豪放，也不 像柳永一般细腻，可谓自成一家。</p><p>【例】沈括在《梦溪笔谈》里写道：近岁蔡君谟又以散笔作草书，谓之‘散草’，或曰‘飞草’，其 法皆生于飞白，亦自成一家。</p><blockquote><p>别开生面</p><p>比喻另外开创新的局面或新的形式。</p></blockquote><p>【例】日前，湖北体彩武汉分中心围绕“年度报告”和“责任彩票”在会议室召开一场别开生面的责 任彩票座谈会。</p><p>【例】伴随着《我的中国心》的悠扬旋律，一场别开生面的爱国主义音乐党课在桂林师范高等专科学 校报告厅拉开帷幕。</p><p>【例】代表们畅所欲言、局长们认真解答，一场别开生面的“代表·局长面对面”活动在灵台县首次 举办。这是灵台县人大常委会开辟的一条吸纳民意、汇集民智的新渠道。</p><blockquote><p>不落窠臼</p><p>意为不落俗套，有独创风格（多用于形容文章或艺术作品）。</p></blockquote><p>【例】可以说，周思辉对何其芳新月时期、京派时期思想和艺术的研究有着自己独特的分析，他的见 解总是不落窠臼，论述中时见新意，这是非常难得的。</p><p>【例】讲好中国故事要以“真”字当先，要用真人、真事、真理提升对外传播的可信度，以真情、真 心、真意提升对外传播的说服力；要“活”字当中，表达上运用新颖的话语方式，注重不落窠臼的谋篇布 局，巧妙提升阅读体验。</p><p>【例】电视剧《去有风的地方》不落窠臼地把田园治愈故事放到乡村振兴的时代背景下进行讲述，传 达出白族诗意栖居的生活态度。这些别开生面的案例，提示我们要善于化用时尚潮流，甚至主动创造潮流。</p><hr><h3 id="【第五组】-守旧不创新-5-个" tabindex="-1"><a class="header-anchor" href="#【第五组】-守旧不创新-5-个"><span>【第五组】 守旧不创新（5 个）</span></a></h3><table><thead><tr><th>形容守旧不创新</th><th>墨守成规、抱残守缺、因循守旧、固步自封、食古不化</th></tr></thead></table><ol><li>成语解释</li></ol><blockquote><p>墨守成规</p><p>形容因循守旧，不肯改变。</p></blockquote><p>【例】要破除墨守成规的思想，树立开拓创新的意识。</p><p>【例】不要墨守成规，专家教你如何健康“贴秋膘”。</p><p>【例】国货品牌没有墨守成规，而是紧跟时代潮流。</p><blockquote><p>抱残守缺</p><p>残、缺：不完整。①固守陈旧残缺的东西不放，形容保守，不知改进。②保存虽有残缺但仍有价值的古物。</p></blockquote><p>【例】在一切学术领域里，都需要参考、借鉴外国的成就，抱残守缺、固步自封是不行的。</p><p>【例】如果不能在保护与修复中保存文物的历史、科学、艺术等价值，还不如抱残守缺，让其静静展 现其历史的真实。</p><p>【例】坚定文化自信不是抱残守缺，而要推陈出新。</p><blockquote><p>因循守旧</p><p>因循：沿用旧办法。指一直沿用旧办法而不加改变。（侧重新与旧）</p></blockquote><p>【例】调整改革后，面对“小机关、大部队”的新体制，如何走出因循守旧的“老套路”，打破守旧、 守常、守成等习惯性思维的“旧束缚”，真正迎来部队建设的大发展、大突破？</p><p>【例】生活从不眷顾因循守旧、满足现状者，从不等待不思进取、坐享其成者，而是将更多机遇留给 善于和勇于开拓创新的人们。</p><p>【例】如果长期自我封闭、因循守旧，必将走向衰落。</p><blockquote><p>固步自封</p><p>封：限制在一定的范围内。比喻因循守旧，安于现状，不求创新进取。（侧重开放与封闭）</p></blockquote><p>【例】他们非常注意世界科学的新动向，紧紧跟上科学发展的步伐，决不固步自封。</p><p>【例】自信自强决不意味着盲目自信、妄自尊大、固步自封，而是要在弘扬中华优秀传统文化的同时， 积极学习借鉴人类文明的一切有益成果。</p><p>【例】文明因固步自封而丧失生机，因交流互鉴而充满活力。</p><blockquote><p>食古不化</p><p>意思是读书、作画一味学习古人，拘泥陈法，不善于灵活运用。指对所学的古代知识理解得不透，不 善于按现在的情况来运用。</p></blockquote><p>【例】坚持植根历史文化沃土发展马克思主义，不是食古不化，简单复兴传统文化，更不是以中华优 秀传统文化替代马克思主义，而是要以古人之规矩，开自己之生面，坚持古为今用、推陈出新。</p><p>【例】而从今年起，不少玩家发现日本今年发行的大量新作竟然都跳进了 VR的圈子。这对于“食古 不化”的日本游戏圈可是一件非常稀罕的事情。</p><p>【例】越来越多的人开始意识到，当代中国建筑既不能食古不化，简单套用中国传统建筑设计风格。</p><hr><h3 id="【第六组】-按规矩办事-6-个" tabindex="-1"><a class="header-anchor" href="#【第六组】-按规矩办事-6-个"><span>【第六组】 按规矩办事（6 个）</span></a></h3><table><thead><tr><th>形容按规矩办事</th><th>按部就班、循规蹈矩、循规拘礼、照本宣科</th></tr></thead><tbody><tr><td>有步骤有条理</td><td>循序渐进、有条不紊</td></tr></tbody></table><ol><li>成语解释</li></ol><table><thead><tr><th>形容按规矩办事</th><th>按部就班、循规蹈矩、循规拘礼、照本宣科</th></tr></thead></table><blockquote><p>按部就班</p><p>部：类别。班：次序。原指写文章按类别安排文义，按顺序组织文辞。后指按照一定的次序或部置进行。（中性词）</p></blockquote><p>【例】学习应当按部就班，循序渐进。</p><p>【例】老旧小区牵涉众多历史遗留，涉及问题复杂多样，无法按部就班，更无法照搬照用，往往是一 事一议。</p><p>【例】坚持不是轻轻松松地按部就班，更不是日复一日模样照旧。坚持离不开永不言弃的毅力，离不 开一往无前的奋斗。</p><blockquote><p>循规蹈矩</p><p>循：依照。规：圆规。蹈：踩。矩：曲尺。规、矩是定方圆的标准规则，借指行为的准则。原指遵守 规矩，不敢违反。后指拘守旧准则，不敢稍作变动。（中性词）</p></blockquote><p>【例】他很老实，一向循规蹈矩，没有乱说乱动的行迹。</p><p>【例】相比于循规蹈矩式的跟团旅游，年轻人更希望从旅行中收获轻松的氛围、社交的机遇以及舒心 的体验。</p><p>【例】历史可鉴，生活从来不眷顾那些循规蹈矩、易满足于现状者，青年要扎根在田间乡野，扎根在 生产车间，肩负起刻苦努力、敢于创新、勇于奋斗的历史重担。</p><blockquote><p>循规拘礼</p><p>遵循规则，拘泥礼节。</p></blockquote><p>【例】宋元君招试画师，应试者皆循规拘礼，唯有一后到者，任性自然地投身于画作。</p><blockquote><p>照本宣科</p><p>原指照着书本或讲稿逐字宣读。引申为机械执行既定规则或文本内容，完全依赖书面指示而缺乏实际 调整，批评其缺乏创造性。</p></blockquote><p>【例】浦毅华坦言，一开始照本宣科只讲宏观大背景，效果并不理想，之后融入方言土话、穿插生活案例，居民的接受度明显提升。“关键在于让抽象的理论转化为群众可感知、可共鸣的内容。”</p><p>【例】县人大代表李美亮表示：“政策宣讲不是照本宣科，而是要让群众感受到政策温度。在这里， 政策不是文件里的冷冰冰文字，民意不是表格上的抽象数据，而是板凳上交心的家长里短”。</p><p>【例】这样，不仅将马克思主义“本本主义化”，甚至将“大兴调查研究之风”变成了“照本宣科”。 美其名曰“下基层”，却是前呼后拥，离群众实际生活很远，将“调查研究”本身也“本本主义化”了。</p><table><thead><tr><th>有步骤有条理</th><th>循序渐进、有条不紊</th></tr></thead></table><blockquote><p>循序渐进</p><p>循：顺，按照。序：次序。指事物的发展或学习工作等按照一定的步骤逐渐深入或提高。</p></blockquote><p>【例】学习要循序渐进，不先掌握基础知识，就无法深造。</p><p>【例】坚持因地制宜，确保规模适度，循序渐进探索可复制可推广的经营模式，才能推动共享菜园实 现健康可持续发展。</p><p>【例】红树林生态系统的修复保护不可能一蹴而就，必须尊重规律、循序渐进。</p><blockquote><p>有条不紊</p><p>条：条理。紊：乱。形容办事很有条理。</p></blockquote><p>【例】中美经贸团队的磋商工作一直在有条不紊、按计划如期推进。 【例】粤港澳大湾区建设各项工作有条不紊推进，在建设国际一流湾区和世界级城市群上迈出坚实步</p><p>伐。</p><p>【例】当前，各项帮扶政策举措持续落地见效，各受灾地区的灾后恢复重建有条不紊进行。</p><hr><h3 id="【第七组】-处境困难-11-个" tabindex="-1"><a class="header-anchor" href="#【第七组】-处境困难-11-个"><span>【第七组】 处境困难（11 个）</span></a></h3><table><thead><tr><th>不敢前进</th><th>望而却步、望而生畏、畏葸不前</th></tr></thead><tbody><tr><td>不好抉择</td><td>骑虎难下、进退维谷、进退失据、瞻前顾后、踟蹰不前、 裹足不前</td></tr><tr><td>处境艰难</td><td>举步维艰、寸步难行</td></tr></tbody></table><ol><li>成语解释</li></ol><table><thead><tr><th>不敢前进</th><th>望而却步、望而生畏、畏葸不前</th></tr></thead></table><blockquote><p>望而却步</p><p>却步：不敢前进，往后退。形容遇到危险、困难或力所不及的事就往后退缩。（侧重行为上的退却， 多因为害怕、惊讶、震撼、犹豫、顾虑等）</p></blockquote><p>【例】当前，要改变“贪”的亚文化精髓，必须加快建立健全严格的法律制度，让腐败分子无利可图、 血本无归，让意欲腐败者望而生畏、望而却步。</p><p>【例】产妇在临盆前，母性的自豪感能叫死神望而却步。</p><p>【例】一些地方建设的零工市场门槛高、岗位少，要求高、程序复杂，让企业和零工望而却步。</p><blockquote><p>望而生畏</p><p>指看见了就害怕。（侧重情绪上的害怕）</p></blockquote><p>【例】他坐在椅子上，一言不发，那黑而严峻的面孔，令人望而生畏。</p><p>【例】如果没有绝对的实力，要想取得优异成绩非常困难。如何用我们的实力让对手望而生畏，是我们必须做的。</p><p>【例】作者生动记述了以毛泽东同志为主要代表的中国共产党人百折不挠、艰苦奋斗，经过四年多的 时间，使中国共产党的武装力量从星星点点形成燎原之势，形成一支令大小反动统治者、旧军队都望而生 畏、闻风丧胆的全新型军队。</p><blockquote><p>畏葸不前</p><p>畏惧退缩，不敢前进。</p></blockquote><p>【例】这个时候就要一鼓作气，瞻前顾后、畏葸不前不仅不能前进，而且可能前功尽弃。</p><p>【例】毛泽东同志曾形象地指出：“所谓政治，就是把拥护我们的人搞得多多的，把反对我们的人搞 得少少的！”把认识统一、贯彻有力的人搞得多多的，把左右摇摆、畏葸不前的人搞得少少的，就是我们 讲政治的应有之义。</p><p>【例】对于新时代青年官兵而言，理想信念就是精神之“钙”，补足了就能筋骨强壮，敢于逢山开路、 遇水架桥，反之就容易得“软骨病”，在困难挑战面前畏葸不前，在欲望诱惑面前迷失灵魂。</p><table><thead><tr><th>不好抉择</th><th style="text-align:left;">骑虎难下、进退维谷、进退失据、瞻前顾后、踟蹰不前、 裹足不前</th></tr></thead></table><blockquote><p>骑虎难下</p><p>骑在虎背上难以下来。比喻事情遇到困难，迫于形势不能中止，陷入进退两难的境地。（侧重同一件 事已经开始了就难以停止）</p></blockquote><p>【例】因为当初没人，我勉强承担这个活，到现在已是骑虎难下了。</p><p>【例】还有少数“无良”中介会在办理过程中，以各种名义加收额外费用，比如担保费、公证费等， 转贷者骑虎难下只能应允，最后发现，转贷的成本比自己预想的高了很多。</p><p>【例】如果再不扩大企业生产规模，必然导致收账难、生产难、订单交付难等恶性循环，企业已经到 了骑虎难下的境地。龚文虎向记者讲述着一度遭遇的难题。</p><blockquote><p>进退维谷</p><p>谷：困窘。指前进或后退都处于困难境地。形容进退两难。同“进退两难”“进退失据”。（侧重很难抉择）</p></blockquote><p>【例】她进退维谷，不知该如何是好。</p><p>【例】当敌发觉陷入我埋伏时，已进退维谷，陷入绝境，成瓮中之鳖。</p><p>【例】从党的十一届三中全会拨乱反正、重新确立实事求是的思想路线到党的十八大后全面从严治党 取得新成效，我们党正是通过一次次自我革命，汇聚起在蒿目时艰中乘风破浪、在进退维谷时勃然奋励、 在失策失误后纳新吐故、在形格势禁里拨云破雾的强大力量。</p><blockquote><p>进退失据</p><p>同“进退失据”“进退两难”</p></blockquote><p>【例】实践证明，抓落实不能仅凭一腔热情，若没有行之有效的方法，就会陷入进退失据、劳而无功 的境地。因而，要想增强抓落实的质效，必须讲究工作方法，着力解决好“抓什么、怎么抓”的问题。</p><p>【例】历史和实践反复证明，抓全局有高度、有方向，抓局部就会有目标、有秩序，各项工作就能统 成一盘棋、拧成一股绳，反之则往往顾此失彼、进退失据，陷入捡了芝麻丢西瓜、补了东墙短西墙的困境 之中。</p><p>【例】实现“两个一百年”奋斗目标、实现中国梦,也面临前所未有的风险和挑战,各种发展陷阱、疑 难杂症、两难命题摆在我们面前。复兴路上,没有坚强有力的领导核心,就会进退失据、贻误战机,甚至出现颠覆性失误。</p><blockquote><p>瞻前顾后</p><p>多用于形容顾虑太多，犹豫不决。</p></blockquote><p>【例】“管得太严，干事放不开”“干多错多，不如不干”……实际工作中，党员干部总的是积极主 动的，但也有极少数人或为不担当作为找借口，或担心动辄得咎而瞻前顾后，不敢放手大胆干事。</p><p>【例】如果只有科学精神而缺少干事热情，就可能在机遇面前瞻前顾后，该担当时不担当、该作为时 不作为。</p><p>【例】长期分管健康工作的河南新密市市直第二幼儿园副园长张慧巧告诉记者，在没有这个制度之前， 张慧巧开展工作时总担心做不对，也找不到专业的人去咨询，工作开展起来有些瞻前顾后。</p><blockquote><p>踟蹰不前</p><p>意思为迟疑不决，不敢前进。</p></blockquote><p>【例】数字化转型能为中小企业带来提质升级、降本增效、业务创新等发展红利，但由于前期投入高、 不确定性大、回报周期长，不少企业往往踟蹰不前。</p><p>【例】尤其是社会预期偏弱的影响需高度重视，当前民间投资预期较弱，人们在能消费敢消费愿消费 方面踟蹰不前，都是预期直接影响投资和消费意愿的具体体现。</p><p>【例】而世界上一些发展中国家，之所以在现代化道路上踟蹰不前甚至陷入倒退的陷阱，一个重要原 因就是缺乏坚强有力政党的领导，整个国家处于一盘散沙的局面。当前，全面建设社会主义现代化国家的 新征程已经开启。</p><blockquote><p>裹足不前</p><p>比喻因心存顾虑而停止行动或不敢向前，与踟蹰不前相比停滞程度更彻底，暗示难以突破。</p></blockquote><p>【例】某型装备操作手于继旺告诉记者，转岗之初，他曾因信心不足，很长一段时间裹足不前。中队 干部鼓励他结合自己的特长大胆尝试、勇敢探索，扎扎实实打基础、长本领。一次大项任务中，他凭借优 异表现，赢得上下好评。</p><p>【例】真正的改革者既不能对“阵痛”无感，更不能在“阵痛”面前裹足不前、错失良机。发展中碰 到的问题，要靠发展来解决；改革的“阵痛”，要靠深化改革去平复。</p><p>【例】当年，山岳景区是否建设索道，也引发了不少争议，但索道建设并没有因争议而裹足不前。除 了少数由于环境保护等硬性要求被叫停或拆除外，索道的数量和规模持续增加，目前已成为不少山岳型景 区的标配。</p><table><thead><tr><th>处境艰难</th><th>举步维艰、寸步难行</th></tr></thead></table><blockquote><p>举步维艰</p><p>抬脚行走十分困难。形容已陷入十分艰难的处境。</p></blockquote><p>【例】同时，我们也要看到，世界经济下行风险和不确定性在同步上升，多边贸易谈判举步维艰，《巴 黎协定》落实遭遇阻力。</p><p>【例】“唯意志论”者固然举步维艰，只有利益的人生同样苍白无力，“喻于利”不是开启幸福的万 能之钥，“喻于义”才能推开梦想的必然之门。</p><p>【例】无论美国经济是否已经陷入衰退，经济增长放缓、企业举步维艰、千千万万家庭被物价击垮已 是不争的事实。</p><blockquote><p>寸步难行</p><p>寸步：很短的距离。一步也难以行走。形容行走十分困难。也形容陷入困难境地，无法进行活动。（程度较重）</p></blockquote><p>【例】停电停水，原料缺乏，市场困难，真是寸步难行。</p><p>【例】林西县将深入挖掘诚信文化底蕴，讲好诚信故事，传承诚信美德，叫响诚信品牌，真正让守信 者“一路绿灯”、失信者“寸步难行”，打造诚信之县。</p><p>【例】这是今年以来工信部第三次就 APP 侵害用户权益问题开展“回头看”，表明整治违规 APP 已成 监管部门“常规动作”，“越界”索权的 APP 必将寸步难行。</p><hr><h3 id="【第八组】-形势危险-5-个" tabindex="-1"><a class="header-anchor" href="#【第八组】-形势危险-5-个"><span>【第八组】 形势危险（5 个）</span></a></h3><table><thead><tr><th>形势危险</th><th>岌岌可危、危如累卵、命悬一线、风雨飘摇</th></tr></thead><tbody><tr><td>侧重时间</td><td>危在旦夕</td></tr></tbody></table><ol><li>成语解释</li></ol><table><thead><tr><th>形势危险</th><th>岌岌可危、危如累卵、命悬一线、风雨飘摇</th></tr></thead></table><blockquote><p>岌岌可危</p><p>岌岌：山高陡峭，就要倒下的样子。形容非常危险，快要倾覆或灭亡。</p></blockquote><p>【例】古特雷斯当天在位于纽约的美国自然历史博物馆发表讲话说，2024 年 5 月是有记录以来最热的 5 月，全球单月平均气温已连续 12 个月刷新同期最高值，将长期气候变暖限制在 1.5 摄氏度以内的目标已 经岌岌可危。</p><p>【例】来往船只频频撞击桥墩，桥体不均匀沉降，桥墩条石撞裂，古桥岌岌可危。</p><p>【例】相比之下，美国继续花掉越来越多的钱支持其在海外的行动，却不愿想办法解决国内摇摇欲坠 的社会结构和岌岌可危的民族品牌。</p><blockquote><p>危如累卵</p><p>比喻形势非常危险，如同堆起来的蛋，随时都有塌下打碎的可能。</p></blockquote><p>【例】10 月 4 日，降雨如注，洪水滔滔，位于黄土山沟中的大宁县向阳社区永和圪洞居民区危如累卵，随时有房屋坍塌和发生泥石流的风险。</p><p>【例】有关内战对叙利亚经济的影响数据还不清楚，可是许多迹象显示叙利亚经济已经危如累卵。</p><p>【例】随着战局越来越紧张，黑山防线危如累卵。第 10 纵队司令员梁兴初直接来到第 28 师前线。晏福生对梁兴初说：“我们立军令状，一定把阵地夺回来！”</p><blockquote><p>命悬一线</p><p>处境危险，随时可能丧失生命。</p></blockquote><p>【例】5 月 1 日，突尼斯坚杜拜省的玛卜鲁卡突发心跳呼吸骤停，命悬一线之时，是中国援突医疗队的医护工作者让她转危为安。</p><p>【例】持续 7 个多月的加沙冲突已造成前所未有的平民伤亡和人道主义灾难，当前在拉法聚集避难的 上百万民众更是命悬一线。</p><p>【例】去年夏天，消防站经历了建站以来最大的考验，在营救转移受灾群众的过程中，不少队员命悬 一线，离死亡只有一步之遥，32 岁的消防站指导员李战坤就是其中之一。</p><blockquote><p>风雨飘摇</p><p>飘摇：飘荡。在风雨中飘荡不定。比喻局势动荡不安，很不稳定。</p></blockquote><p>【例】3 亩茶叶地，一个瘦小的身体，谢婆婆就这样苦苦支撑着这个风雨飘摇的家庭。“真的太苦太 难了。”事实上，除了长期的照顾和干农活，病情更重的孙儿伟伟时常发病，无法控制的行为更让谢婆婆 心力交瘁。</p><p>【例】大革命失败后，面对风雨飘摇的革命形势，谢维俊始终忠诚革命、对共产主义事业充满信心， 一直在群众中秘密宣传革命终究要胜利的道理，在艰难局势下重建了肥田党支部，团结了一批党员和群众， 为迎接新的斗争积蓄了革命力量</p><p>【例】6 年来，郭二培照顾瘫痪丈夫、孝敬公婆、养育女儿，带着这个风雨飘摇的小家勇敢前行的事迹深深感动着中原大地，凭着一腔热忱和无私大爱，郭二培荣登 2023 年度河南省“乡村光荣榜”好媳妇 榜单。</p><table><thead><tr><th>侧重时间</th><th>危在旦夕</th></tr></thead></table><blockquote><p>危在旦夕</p><p>旦夕：早晨和晚上，形容时间短。形容危险就在眼前。</p></blockquote><p>【例】李先生（化名）在单位突发心梗晕倒，被发现后就近送往萧县人民医院抢救，当时患者已错过 最佳抢救时间，心脏出现多次停跳，生命危在旦夕。</p><p>【例】当时河水猛涨，山洪暴发，宝湾村、龙头村情况紧急，200 余名群众被洪水围困，生命危在旦夕。滔天的洪水中，一个身材微胖、戴着眼镜的消防战士义无反顾地迎着洪峰逆行。</p><p>【例】落水两人是母女，此时 3 岁的女童已经没有了呼吸，生命危在旦夕，四周没有人懂得急救，师 维楠尝试用在部队服役时学过的心肺复苏法施救。约两分钟后，女童终于有了呼吸。</p><hr><h3 id="【第九组】事物出现、发展、衰落-22个" tabindex="-1"><a class="header-anchor" href="#【第九组】事物出现、发展、衰落-22个"><span>【第九组】事物出现、发展、衰落（22个）</span></a></h3><table><thead><tr><th>出现、萌生</th><th style="text-align:left;">层出不穷、风起云涌、方兴未艾、雨后春笋、横空出世、 异军突起、应运而生</th></tr></thead><tbody><tr><td>发展势头好</td><td style="text-align:left;">高歌猛进、如火如荼、如日中天、势如破竹、突飞猛进</td></tr><tr><td>形容发展迅速</td><td style="text-align:left;">一日千里、日新月异</td></tr><tr><td>逐渐衰落</td><td style="text-align:left;">日渐式微、每况愈下、江河日下</td></tr><tr><td>其他</td><td style="text-align:left;">水涨船高、此消彼长、波澜壮阔、柳暗花明、拨云见日</td></tr></tbody></table><ol><li>成语解释</li></ol><table><thead><tr><th>出现、萌生</th><th style="text-align:left;">层出不穷、风起云涌、方兴未艾、雨后春笋、横空出世、 异军突起、应运而生</th></tr></thead></table><blockquote><p>层出不穷</p><p>层出：接连出现。穷：尽。接连出现，没有穷尽。（主语是事物）</p></blockquote><p>【例】市场上时装的款式层出不穷。</p><p>【例】节假日热门景区再现人潮涌动，各类演出轮番上阵，演唱会音乐节一票难求，“淄博赶烤”“村 超”“村 BA”等现象级消费新热点层出不穷。</p><p>【例】从办案情况来看，当前毒品犯罪案件呈现以下特点：新型毒品层出不穷。</p><blockquote><p>风起云涌</p><p>形容雄浑磅礴之势；也比喻事物迅速发展，声势浩大。</p></blockquote><p>【例】时代风起云涌，目睹了百年交汇，注视着国际盛会，更见证了青年在历史洪流中惊起波涛。</p><p>【例】他说：“量子计算正风起云涌”，要避免产业泡沫可能带来的负面影响，抓住可以带来真正算 力优势的量子计算发展机遇。</p><p>【例】一场风起云涌的创投盛会，激起强劲的科创动能。</p><blockquote><p>方兴未艾</p><p>意思是事物正在发展，尚未达到止境。</p></blockquote><p>【例】随着大数据、人工智能等数字技术在农业领域的广泛应用，智慧农业在许多国家方兴未艾。</p><p>【例】两国双向投资规模持续增长，能源、航空航天、互联互通等领域大项目合作稳步推进，科技创 新、跨境电商等新兴领域合作势头强劲，地方合作方兴未艾，为两国民众带来实实在在的好处，也为双方 各自发展振兴提供了源源不断的动力。</p><p>【例】随着上海“一江一河”的开放贯通，打造世界级滨水区，水岸经济、文旅活动方兴未艾，而沿 岸的滨水型商业也迎来了新的发展机遇。</p><blockquote><p>雨后春笋</p><p>春天下雨后竹笋长得又多又快，比喻新生事物大量涌现和蓬勃发展。（侧重数量多）</p></blockquote><p>【例】十余年来，各式各样的创意产业园在广州如雨后春笋，蓬勃生长。</p><p>【例】近年来，长沙市不断新建幼儿园、中小学，漂亮的学校如雨后春笋，让更多的学生就近入学； 大力开展医疗改革，互联网医院、智慧医疗为老百姓就医打造新模式，“看病难”变成了“看病易”。</p><p>【例】眼下，在湘西州凤凰县筸子坪镇板参晒金塘村的食用菌种植基地，一个个黑皮鸡枞菌如雨后春笋般破土而出，工人忙着采摘、分拣、储运。</p><blockquote><p>横空出世</p><p>形容人或物高大，横在空中，浮出人世，或比喻卓尔不群。（侧重突出、明显）</p></blockquote><p>【例】四十载波澜壮阔，一批批拓荒者、先行者、攻关者，闯关夺隘、攻坚克难，把一个个“不可能” 变成“可能”，让一串串惊世奇迹横空出世。</p><p>【例】“矩阵游戏”横空出世，AI 首次创建高效准确数学算法</p><p>【例】本届展会是科技企业与艺术机构同台碰撞与融合的会展新物种，既有全球最新智能设备和创意 软件的亮相，有顶级艺术家和科技大咖的加盟，也有校园新势力横空出世，看点十足。</p><blockquote><p>异军突起</p><p>异军：另一支军队。比喻一支新生力量突然崛起。（侧重新生事物）</p></blockquote><p>【例】今年夏天，选秀节目被互联网再次引爆。《中国有嘻哈》异军突起，让“嘻哈”这一小众音乐 备受关注。</p><p>【例】面对近年来外部形势的新变化，延边州抓住机遇，寻找新的发展空间，探索新的发展出路，不 断推动纺织服装加工产业成为“十三五”期间异军突起的新动能产业。</p><p>【例】从无到有、从低到高，着手打造性能可靠、功能互补、性价比较高的“无人机军团”，不少外 媒以“异军突起”来形容伊朗无人机的发展，并对该国生产的无人机冠以“空中波斯弯刀”的名号。</p><blockquote><p>应运而生</p><p>应运：应天命。指顺应天命而降生。后泛指顺应时机而产生。</p></blockquote><p>【例】现代经济学已经失灵，适应信息时代的新型经济学将应运而生。</p><p>【例】帮助更多人科学养宠，新兴职业宠物训练师应运而生。</p><p>【例】新时代新需求：智慧养老服务模式应运而生。</p><table><thead><tr><th>发展势头好</th><th>高歌猛进、如火如荼、如日中天、势如破竹、突飞猛进</th></tr></thead></table><blockquote><p>高歌猛进</p></blockquote><p>意思是高声歌唱，勇猛前进；形容在前进的道路上，充满乐观精神。</p><p>【例】自去年开工以来，项目建设一路高歌猛进，预计年底验收手续办理后即可试生产。</p><p>【例】这 30 多年，是改革开放波澜壮阔的 30 多年，是上海高歌猛进的 30 多年。</p><p>【例】3 月 15 日晚，CBA联赛第 35 轮，回到绍兴柯桥主场的浙江稠州金租男篮迎来了江苏男篮的挑 战，凭借全队在外线的滚烫手感，最终 121 比 80 轻取对手，继续在连胜道路上高歌猛进。</p><blockquote><p>如火如荼</p><p>原比喻军容之盛，现在形容旺盛、热烈或激烈。</p></blockquote><p>【例】放眼北疆各大项目现场，处处呈现出如火如荼的建设场面。</p><p>【例】春节假期，浙江省宁波舟山港船舶往来频繁，生产如火如荼，工人们坚守岗位，忙碌装卸货物， 确保供应链、物流链的高效顺畅，力争龙年“开门红”。</p><p>【例】时下，走进三都县各个早春蔬菜种植基地，到处可见群众翻犁田土、开厢起垄、施肥、覆膜的 忙碌身影，各地春耕备耕正如火如荼地进行着。</p><blockquote><p>如日中天</p><p>像太阳正运行到正午。比喻正发展到最兴盛的阶段。</p></blockquote><p>【例】2013 年热播的《徐悲鸿》更是让他的演艺事业如日中天。</p><p>【例】二人长期在外做虫草生意、搞建筑，事业如日中天。</p><p>【例】这座令聂鲁达久久驻足的古城，名叫马丘比丘，在 15 世纪印加帝国如日中天的年代里，工匠 以极高的建筑技艺建成了它，作为贵族的休养地与祭祀地。</p><blockquote><p>势如破竹</p><p>势：形势。破竹：劈开竹子。形势如同劈竹子一样，劈开头上几节，下面各节就顺着刀口裂开了。后 用来形容节节胜利，毫无阻碍。</p></blockquote><p>【例】尽管《私人订制》在上映后遭遇恶评，但无论是在排片上还是票房上都势如破竹。</p><p>【例】一切围着项目转，一切为了项目干，全市招商引资势如破竹。中建材凯盛集团、海螺集团等大 企业大集团纷纷投资通辽。</p><p>【例】末节中国队势如破竹，连入四球大胜。</p><blockquote><p>突飞猛进</p><p>形容发展十分迅速。</p></blockquote><p>【例】我可以肯定地认为，中国会继续实现突飞猛进的发展。</p><p>【例】从去年 10 月开始，随着张之臻、吴易昺、商竣程等新生代球员集体崛起，中国男网的成绩突飞猛进。</p><p>【例】当下，生命科学、生物技术发展突飞猛进。在医疗领域深耕多年的华大集团首席执行官、执行 董事尹烨对此深有体会。</p><table><thead><tr><th>形容发展迅速</th><th>一日千里、日新月异</th></tr></thead></table><blockquote><p>一日千里</p><p>一日千里一天跑一千里。也比喻进步、发展很快。</p></blockquote><p>【例】从时代的大趋势看，无论世界还是中国的发展都好像有一种加速度，真是日新月异、一日千里， 新情况新问题格外多。</p><p>【例】昌都交通事业发展综述：人背畜驮到“一日千里”。</p><p>【例】连日来，前来参观的香港市民络绎不绝，纷纷感叹国家建设成就一日千里，对香港背靠祖国发 展更有信心。</p><blockquote><p>日新月异</p><p>形容发展变化很快，新事物、新气象不断出现。</p></blockquote><p>【例】我工作的地方，如今建起了高楼大厦，马路变宽了，环境变美了，城市面貌日新月异的变化令人振奋。</p><p>【例】跟随“大地飞歌·山水人文秀”之“幸福歌”，感受邕城日新月异。</p><p>【例】初春时节，在 5270 平方公里的瓷都大地上，到处都是活跃的创造，到处都是日新月异的进步。</p><table><thead><tr><th>逐渐衰落</th><th>日渐式微、每况愈下、江河日下</th></tr></thead></table><blockquote><p>日渐式微</p><p>事物逐渐地由兴盛而衰落。</p></blockquote><p>【例】牛墟鼎盛时日交易量上千头，牛客来自全国各地，如今随着时代变迁，日渐式微。</p><p>【例】整体实力相对衰落、内部矛盾不断，G7 在全球的影响力日渐式微。</p><p>【例】越来越多的拉美国家对美国大声说“不”，凸显出在拉美乃至全球范围内，美国的影响力日渐式微，霸权主义不得人心。</p><blockquote><p>每况愈下</p><p>指情况越来越坏，越来越糟糕。（也可形容身体或精神越来越糟糕）</p></blockquote><p>【例】7 年多来，饱受动荡之苦的突尼斯人民生活水平每况愈下。</p><p>【例】在这个时代里，人人都讲究社交，白天要勤奋工作，晚上还得与同事应酬、放松，殊不知，彻 夜的狂欢非但不能把压力派遣出去，反而会令身体每况愈下。</p><p>【例】美国一边为本国每况愈下的人权状况寻找借口，一边傲慢地以“人权教师爷”的姿态对别国指 手画脚，四处挥舞人权大棒</p><blockquote><p>江河日下</p><p>长江大河的水，日夜流向下游而不止。比喻事物日趋衰落，情况一天不如一天。（多指国势等事物发展趋势）</p></blockquote><p>【例】历史上，诸葛亮长于谋事短于谋人，最后造成蜀国江河日下，走向衰落。 【例】自从那笔生意亏了本后，他的事业便江河日下了。</p><p>【例】随着互联网的发展，传统出版社已经处于江河日下的境地。</p><table><thead><tr><th>其他</th><th>水涨船高、此消彼长、波澜壮阔、柳暗花明、拨云见日</th></tr></thead></table><blockquote><p>水涨船高</p><p>水位上涨，船体也随着上升。比喻事物随着它所凭借的基础而增长。</p></blockquote><p>【例】人民网发表评论文章《明星片酬为啥屡“限”屡高？》，文章指出，市场认明星，有人认有人 买单，明星片酬自然就水涨船高。</p><p>【例】在浙江宁波，不少市民表示“五一”假期的出行意愿较高，从宁波出发的国内机票价格也“水涨船高”。</p><p>【例】新的组训模式推行后，不仅本场训练时间不足的问题得到有效解决，还倒逼任务飞行员更加注 重理论学习和模拟训练，任务完成质量水涨船高。</p><blockquote><p>此消彼长</p><p>指这个下降，那个上升。</p></blockquote><p>【例】传统媒体和新兴媒体融合发展，不是一个此消彼长的概念，而是此长彼长的过程。</p><p>【例】改革开放以来，随着社会的巨变，各种各样的思潮也此消彼长。</p><p>【例】传统产业和现代化产业不是此消彼长，而是相辅相成。</p><blockquote><p>波澜壮阔</p><p>原形容水面辽阔，现比喻声势雄壮或规模宏大。</p></blockquote><p>【例】此次演出不仅为观众带来一幅波澜壮阔的运河音画，也通过“民乐联合”的形式彰显出大运河 的文化活力。</p><p>【例】百年征程波澜壮阔，百年初心历久弥坚。</p><p>【例】庆祝中国共产党成立 100 周年文艺演出《伟大征程》，以大型情景史诗形式呈现，分为“浴火 前行”“风雨无阻”“激流勇进”“锦绣前程”4 个篇章，综合运用多种艺术手段，生动展现中国共产党 波澜壮阔的百年历史。</p><blockquote><p>柳暗花明</p><p>垂柳浓密，鲜花夺目。形容柳树成荫，繁花似锦的春天景象。也比喻在困难中遇到转机。</p></blockquote><p>【例】成绩虽然不尽如人意，但辽篮如果调整得当，第三阶段和季后赛有望迎来柳暗花明的新局面。</p><p>【例】海峡论坛的召开，无论是伴着热情红火的夏季凤凰花，还是明媚动人的冬季异木棉，都看得到 “柳暗花明”的光明美景，都绽放着万树繁花。</p><p>【例】从山重水复到柳暗花明，路是从荆棘丛生处开辟出来的。只要方向对了，就不怕山高路远。</p><blockquote><p>拨云见日</p><p>拨开乌云见到太阳。比喻冲破黑暗见到光明。也比喻疑团消除，心里顿时明白。</p></blockquote><p>【例】“拨云见日终有时，一碧万顷醉晴空。”不容辩驳的数据事实击碎造谣与谎言编织的黑幕，东 方明珠光芒更加耀眼夺目。心中有“数”，脚下有路。</p><p>【例】17 年的风风雨雨一路走来，赵昭有过白手起家的迷茫困惑，也有过小有成就的激动喜悦，遭受 过突遇险阻的步履维艰，也感受过拨云见日的豁然开朗。</p><p>【例】全国各大美院的资深教授亲临现场，以权威之姿进行深度分享，精准剖析新时代美术教育的发 展趋势，为行业发展拨云见日、指明方向。</p><hr><h3 id="【第十组】-竞争比较-12-个" tabindex="-1"><a class="header-anchor" href="#【第十组】-竞争比较-12-个"><span>【第十组】 竞争比较（12 个）</span></a></h3><table><thead><tr><th>和别人相比较</th><th>相形见绌、出类拔萃、无与伦比</th></tr></thead><tbody><tr><td>表现突出</td><td>脱颖而出、崭露头角、大显身手</td></tr><tr><td>赶得上/赶不上</td><td>望其项背、望尘莫及</td></tr><tr><td>同等地位</td><td>相提并论、同日而语、等量齐观、一概而论</td></tr></tbody></table><p>10.1 成语解释</p><table><thead><tr><th>和别人相比较</th><th>相形见绌、出类拔萃、无与伦比</th></tr></thead></table><blockquote><p>相形见绌</p><p>形：对照，比较。绌：不够，不足。跟同类的人或同类事物相比较，显出很不足。</p></blockquote><p>【例】与中国的西进战略比起来，西澳大利亚州吸引东部诸州人口的努力相形见绌。</p><p>【例】此外，最新量子计算机用激光束制成的“光镊”使量子比特相互靠近，从而相互作用并交换信 息，而 IBM和谷歌制造的基于芯片的量子计算机，必须使用多条导线来控制每个量子比特，控制能力相形见绌。</p><p>【例】建好建强领导干部队伍，至关重要的是要发扬彻底的自我革命精神，既要持之以恒正风肃纪反 腐，坚决清除各种腐败变质分子，又要持续推进能上能下、能进能出，把忠诚干净担当的干部选拔出来， 把相形见绌的干部调整下去，实现干部队伍新陈代谢</p><blockquote><p>出类拔萃</p><p>出：超出。类：同类。拔：高出。萃：指聚在一起的人或物。指人的品德才能出众，高出同类之上。 （侧重能力或品德）（高于同类）</p></blockquote><p>【例】这感人的姿态，正是出类拔萃的外交家周恩来的特色。</p><p>【例】这些创新工作室由‘点’成‘面’、聚‘线’成‘网’，实现信息共享、资源共享、项目共享、 人才共享，在‘链’上汇聚产业发展动能，深入挖掘改进产业链技能人才协同创新机制的办法，让出类拔萃的创新团队、创新人才成为培育发展新质生产力的新动能。</p><p>【例】正如中国近代教育家、早期话剧活动家张彭春在为梅兰芳访苏撰写的《中国舞台艺术纵横谈》 中谈到的，一个卓越的演员只有在对一般剧目做出了出类拔萃的成绩以后，才能创造新格局。</p><blockquote><p>无与伦比</p><p>意思是指事物非常完美，没有能够与它相比的同类的东西。</p></blockquote><p>【例】弋江区既是高新技术产业集聚区，也是高科技人才汇集地，境内有安徽师范大学等七所高等院 校，在校学生 10 万多人，这是弋江区无与伦比的独特优势和宝贵财富。</p><p>【例】中华文明具有无与伦比的包容性。</p><p>【例】要全面贯彻落实党的二十大精神和习近平总书记视察江西重要讲话精神，认真落实李书磊同志 有关工作要求，按照省委和省政府部署安排，积极探索实践，合力攻坚克难，提升建设质效，让千年瓷都 重振辉煌，让无与伦比的景德镇陶瓷文化永远享誉世界。</p><table><thead><tr><th>表现突出</th><th>脱颖而出、崭露头角、大显身手</th></tr></thead></table><blockquote><p>脱颖而出</p><p>颖：细长物体的尖端。比喻才能完全显露出来。（侧重能力）（明显地）</p></blockquote><p>【例】近来，张杨执导的《冈仁波齐》脱颖而出，创造了近期国产文艺片的票房新纪录。</p><p>【例】但仇安和车组成员包扎伤口后又咬牙坚持训练，最终凭借顽强战斗精神在比赛中脱颖而出，荣 获陆军“优胜战斗车组”。“‘杨根思连’的荣光是打出来的，荣誉是拼出来的！</p><p>【例】经过层层筛选，来自成都新都公安各科所队的 20 余名民警脱颖而出，在射击场上尽锐出战，在真枪实弹中展开巅峰对决。裁判员下达比赛口令后，比武正式拉开帷幕。</p><blockquote><p>崭露头角</p><p>崭：高出。比喻突出地显露出才能。（侧重能力）</p></blockquote><p>【例】近日，记者发现上海一批高学历、广视野、敢创新，且经过多年基层磨炼的年轻干部，正在崭露头角。</p><p>【例】上等兵胡海鹏大学期间学的是光电技术应用专业，入伍后凭借专业优势很快崭露头角。前段时 间，他参加上级组织的专业考核表现出色，被旅队遴选担任专业教练员。</p><p>【例】第一打击中心共有民警 11 人，他们都是在港区基层派出所摸爬滚打后“穗选”而出的热血民 警，他们怀着对群众的深厚情感，敢拼善学、精益求精，在各起案件中崭露头角，他们是具有巨大潜能的 后浪。</p><blockquote><p>大显身手</p><p>显：表露，表现；身手：指本领。充分显示出本领和才能。</p></blockquote><p>【例】新时代新征程民营经济发展前景广阔、大有可为，广大民营企业和民营企业家大显身手正当其</p><p>时。</p><p>【例】新年伊始，成都民营企业正开足马力忙生产，力争在广阔的舞台上大显身手。 【例】未来，期待更多青年踊跃投身乡村全面振兴主战场，施展才华、大显身手。</p><table><thead><tr><th>赶得上/赶不上</th><th>望其项背、望尘莫及</th></tr></thead></table><blockquote><p>望其项背</p><p>项：脖子的后部。能够看到别人的颈项和脊背。比喻有能力赶得上。（常用于否定句：难以～、无法～、不能～）</p></blockquote><p>【例】AI 音乐的创作效率，让传统音乐制作难以望其项背。</p><p>【例】此后，两国间经济差距逐渐扩大，到 2000 年，日本的经济规模已跃升为德国的 2.5 倍，使得 德国难以望其项背。时过境迁，到了 2024 年，日本的“神话”终于被德国赶超。</p><blockquote><p>望尘莫及</p><p>莫：不能。及：到，赶上。指望见前面人马扬起的尘土而追赶不上。比喻远远落在后面，相差甚远。 也用以表示自谦。</p></blockquote><p>【例】中国移动一骑绝尘，联通和电信望尘莫及，这或许是对三家运营商 4G用户发展最形象的描述。</p><p>【例】最终，这两次半决赛的结果都是辽宁队翻盘成功，他们在绝境中所展现出的比赛韧性和反弹力， 以及关键时刻把握机会的能力，都令对手望尘莫及。这是久经历练后的队伍才会呈现出的状态，也是辽宁 队此前多年积淀的结果。</p><p>【例】一部 300 多万字的《资治通鉴》，毛主席竟看了 17 遍之多，令人望尘莫及。毛泽东诗词植根于中华优秀文化的深厚土壤，蕴涵着丰富多彩的中国文化元素。</p><table><thead><tr><th>同等地位</th><th>相提并论、同日而语、等量齐观、一概而论</th></tr></thead></table><blockquote><p>相提并论</p><p>把不同的人或不同的事情放在一起谈论或看待。（侧重于评价几个人或事物之间有没有差距/能否比 得上。多用于否定句中）</p></blockquote><p>【例】他们两人差别太大，怎么可以相提并论。</p><p>【例】“无论逮捕令可能意味着什么，绝不能把以色列与哈马斯相提并论。”</p><p>【例】幸运的是，目前在各行各业中 AI 替代的大多是重复性、程序性的工作，在创造性和情感的表 达上，AI 制造暂时还无法与人类相提并论。但是，未来呢？</p><blockquote><p>同日而语</p><p>指相提并论。（侧重于强调时间上的对比，多形容与自身的比较。现有时也有例外）（多用于否定句中）</p></blockquote><p>【例】俄罗斯目前的经济状况同 2008 年的危机不能同日而语。</p><p>【例】相比之下，缺少了亲情为纽带的智能家居，单纯依靠智能化控制电器的行业局限，早已在行业 升级的梯度上落下了明显差距，远不能与智慧家庭同日而语。</p><p>【例】唐仁健指出，当前，我国农业的装备条件、抗灾能力以及生产经营的组织方式，不要说跟几十 年前，跟 10 年前、跟几年前比恐怕都不可同日而语。水利条件大大改善，现在已经有 10 亿亩的高标准农 田，旱能浇、涝能排。</p><blockquote><p>等量齐观</p><p>等：同等。齐：一样。把不同的事物一律同等看待。</p></blockquote><p>【例】他们二人的工作成绩不可等量齐观。</p><p>【例】在传统文明中，确实存在着许多与现代文明相近的话语、箴言、观念等，但在其具体内容和精 神实质上又不能等量齐观。</p><p>【例】书籍固然数量浩如烟海，但其轻重分量不同，不可等量齐观。大量的书实际上可有可无，不读 也没有明显的损失。只有极少数才真正具有原创意义，是那种被称为经典的书籍，是书中之书。</p><blockquote><p>一概而论</p><p>用同一标准来对待或处理（多用于否定）：不能～。</p></blockquote><p>【例】其实，服中药时能否喝绿豆汤，不可一概而论，应根据病情的寒热虚实以及所服中药的性味而定。</p><p>【例】用工自主权与平等就业权的边界应当结合具体岗位的内在要求具体分析，不能一概而论，但应 该明确的是，用人单位不得以与工作岗位内在要求无关的身体条件而对劳动者予以差别对待，限制劳动者 平等就业和自主择业的权利。</p><p>【例】褒扬西安交大等高校院士给本科生上课的传统，并不是苛求所有院士都去给本科生上课，毕竟 每个院士研究的领域不同、擅长的工作不同、承担的任务也不同，不能一概而论。</p><hr><h3 id="【第十一组】-能力不足-10-个" tabindex="-1"><a class="header-anchor" href="#【第十一组】-能力不足-10-个"><span>【第十一组】 能力不足（10 个）</span></a></h3><table><thead><tr><th>能力有限</th><th>力有未逮、爱莫能助、鞭长莫及、望洋兴叹、回天乏术</th></tr></thead><tbody><tr><td>应付不了</td><td>捉襟见肘、顾此失彼、左支右绌、独木难支、应接不暇</td></tr></tbody></table><ol><li>成语解释</li></ol><table><thead><tr><th>能力有限</th><th>力有未逮、爱莫能助、鞭长莫及、望洋兴叹、回天乏术</th></tr></thead></table><blockquote><p>力有未逮</p><p>指有意愿却做不到。</p></blockquote><p>【例】虽然我们强调政府应尽可能为民服务，但单纯靠政府来解决民生问题力有未逮。</p><p>【例】关口辖区面积这么大，要向道路扬尘和垃圾“宣战”，仅靠环卫工人清扫，显然力有未逮。近 日来，在关口辖区各村主干道和浏东公路沿线，时刻可见党员、志愿者和学生清扫道路，清洗指示牌和护栏。</p><p>【例】部分路段逢雨“看海”，排水管网力有未逮，我市城市路面防涝又临“大考”。</p><blockquote><p>爱莫能助</p><p>爱：爱惜；莫：不。虽然心中关切同情，却没有力量帮助。</p></blockquote><p>【例】家里经济条件有限，这也并不是危及到生命的残疾，父母也爱莫能助。</p><p>【例】“虽然事不大，但双方的矛盾却很尖锐，几十户业主围满了物业公司办公室，物业公司表示爱莫能助，武某当场拍着桌子说，就算法院判了，他也不交！”回想起当时的调解场景，李青仍心有余悸。</p><p>【例】但他们一个是肢体二级残疾、卧床不起，另一个患严重心脏病，再加上同样患有心脏病、无法 工作的儿子，每个月的医药费开销很大，按照现行政策，他们家有机动车无法申请低保边缘家庭，我们想 ‘雪中送炭’，却又受政策限制爱莫能助。</p><blockquote><p>鞭长莫及</p><p>及：到。原意是鞭子虽长，也不能打马肚子。比喻相隔太远，力量达不到。（有时会用于形容“虽有 能力，但能力有限”）</p></blockquote><p>【例】为有效缓解火灾扑救鞭长莫及的不足，提高抗御火灾和抢险救援能力，遏制农村重特大火灾事 故的发生，芜湖市繁昌区平铺镇将消防站建设纳入重要议事日程，在上级主管部门支持下，不断加强消防 站建设力度。</p><p>【例】该部领导看在眼里、急在心上，“其实，我们也想了不少办法……无奈两地相隔太远，鞭长莫 及”。杨斌的困境，并非个例。在前期调研中，空军领导发现，像杨斌这样需要异地解难的诉求不在少数。</p><p>【例】在强烈的不稳定大风作用下，沙土被卷入空中，并伴随蒙古气旋和冷锋，通过平流过程输送南 下，其输送高度可达 4000 米以上，在这样的高度下，防护林也鞭长莫及了。</p><blockquote><p>望洋兴叹</p><p>望洋：仰视的样子。仰望海神而兴叹。原指在伟大事物面前感叹自己的渺小。现多比喻做事时因力不胜任或没有条件而感到无可奈何。</p></blockquote><p>【例】面对如此浩瀚的古籍，即便是焚膏继晷、皓首博览的文人学士，也只能侧重于某些具体范围和 方面，而对于普通读者来说，更难免望洋兴叹之感。</p><p>【例】10 年前，作为“三不沿”的内陆城市，遵义要“绕一大圈”才能走出国门、联通世界。遵义的 产品囿于交通的不顺畅，“豆腐运出了肉价钱。”令不少投资者望洋兴叹。</p><p>【例】数字变革正在深刻地改变人们的生活习惯，但对大多数老年人来说，信息化服务的操作门槛让 一些老年人望洋兴叹，不少老年人不会上网、不会使用智能手机，在出行、就医、消费等日常生活中遇到 不便，无法充分享受智能化服务带来的便利。</p><blockquote><p>回天乏术</p><p>回天：比喻力量大，能移转极难挽回的时势；乏术：缺少方法。比喻局势或病情严重，已无法挽救。</p></blockquote><p>【例】对于一些晚期肿瘤和罕见病，再高明的医生也回天乏术，再先进的医学也无能为力。但是，如 果人们具有一定的医学常识，在疾病初期就发现苗头，尽早诊治，结果就可能大不一样。</p><p>【例】由于蜀汉政权诸多先天不足，到蒋琬执政时北伐已显乏力，“光复汉室”的愿望在现实面前日 渐渺茫，因而蒋琬的经历中也多少透着回天乏术的无奈。</p><p>【例】落后的情况下奋力追赶比分，将对手拖入抢七局，在 6：3 领先的情况下未能抓住 3 个盘点， 好在最终仍拿下抢七局，以 7：6 险胜；第三盘王欣瑜率先破发，一度 3：1 领先，然而又被对手反超，虽 然在盘末挽救 4 个赛点仍回天乏术。</p><table><thead><tr><th>应付不了</th><th>捉襟见肘、顾此失彼、左支右绌、独木难支、应接不暇</th></tr></thead></table><blockquote><p>捉襟见肘</p></blockquote><p>捉襟：整理衣襟。肘：胳膊肘儿。形容衣裳破旧。也比喻困难很多，应付不过来。（侧重人力、物力 等资源有限）</p><p>【例】然而作为新旅游区，这里在配套设施方面一时有些捉襟见肘。特别是到了凤凰花开时节或者节 假日，游客大量涌入，停车成了难题。</p><p>【例】梅花只会务农，一双儿女都在上学，家庭支出大，仅靠低保和务农，捉襟见肘。以往，监测户认定主要以家庭收入为依据，梅花虽享受低保补贴，可面对巨大生活压力，没有政策引导寻求致富门路， 实在难以为继。</p><p>【例】曾几何时，提起思想政治教育，官兵有不少“槽点”——“出差、休假回来就有一堆笔记要补” “笔记记了好几本，真正记得住的课却没几堂”……以往上级‘刚性’教育内容多，时间和精力上捉襟见肘。</p><blockquote><p>顾此失彼</p><p>顾了这个，丢了那个。形容忙乱或慌张的情景。（侧重考虑不周全）</p></blockquote><p>【例】保障粮食安全、促进农民增收、推进农业可持续发展等，都是我国农业支持政策要实现的重要 目标，不能顾此失彼甚至对立起来。</p><p>【例】头痛医头、脚痛医脚，“九龙治水”、各吹各调，很容易顾此失彼、事倍功半，甚至造成生态 破坏。</p><p>【例】“与普通高校相比，职业院校教师教学工作量较为繁重，教学和科研往往顾此失彼，学校采取 的这种转化方式解决了老师们开展科研的后顾之忧。”扬州市职业大学生物与化工工程学院孙长花老师激动地说。</p><blockquote><p>左支右绌</p><p>支：支持；绌：屈曲，引申为不足。原指弯弓射箭的姿势，左手支持，右手屈曲。指力量不足，应付 了这方面，那方面又出了问题。（侧重能力有限，应付不过来）</p></blockquote><p>【例】有一名美国国务院官员因不满美国在巴以冲突中持续向以色列供应武器愤然辞职。外有以色列 “不听劝”，内有自家人“唱反调”，巴以冲突爆发迄今，拜登政府左支右绌，大选年里“很犯愁”。</p><p>【例】美国想同时压垮中俄两个大国，只能是左支右绌，反而让中俄背靠背协作关系越来越紧密。而 中俄越是走得近、站得稳、发展得好，美国就越担心害怕。</p><p>【例】近年来，英国“脱欧”，美国撕裂，西方民粹主义蔓延，种族矛盾、仇恨犯罪愈演愈烈，很多 西方国家社会思潮紊乱失序，“后真相”“后秩序”等成为西方自我反思的高频词，一连串国际地缘政治 冲击令西方左支右绌，力不从心。</p><blockquote><p>独木难支</p><p>一根木头支不住高大的房子。比喻一个人的力量单薄，维持不住全局。</p></blockquote><p>【例】祛除未成年人的网瘾光靠王波一人独木难支，近期三建乡将组织文服中心、司法所、平安办、 派出所共同开展网络宣传相关活动，一方面走进校园开展防沉溺网络专题活动，另一方面再组织专业老师 上门家访。</p><p>【例】末节决战，尽管眼看分差只有个位数，但浙江东阳光队始终差了一口气，孙铭徽独木难支，最 终球队没能实现逆转。</p><p>【例】钢价未出现明显下跌，可能仅仅是原料矿石价格坚挺支撑所致，但独木难支，下游需求不济终 将让钢材价格支撑不住。</p><blockquote><p>应接不暇</p><p>暇：空闲。原指景物很多，顾不上观赏。后形容太繁忙，应付不过来。（主语是人）（侧重人或事物 太多忙不过来）</p></blockquote><p>【例】火车站的问事处挤满了人，工作人员应接不暇。</p><p>【例】上下左右键控制出脚，层出不穷的指令，让你应接不暇。</p><p>【例】火车外的风景让人应接不暇。</p><hr><h3 id="【第十二组】印象和效果-9个" tabindex="-1"><a class="header-anchor" href="#【第十二组】印象和效果-9个"><span>【第十二组】印象和效果（9个）</span></a></h3><table><thead><tr><th>形容学习粗浅</th><th style="text-align:left;">浮光掠影、走马观花、蜻蜓点水、浅尝辄止、囫囵吞枣、 不求甚解</th></tr></thead><tbody><tr><td>形容学到一点就能推知 更多</td><td style="text-align:left;">举一反三、融会贯通、触类旁通</td></tr></tbody></table><ol><li>成语解释</li></ol><table><thead><tr><th>形容学习粗浅</th><th style="text-align:left;">浮光掠影、走马观花、蜻蜓点水、浅尝辄止、囫囵吞枣、 不求甚解</th></tr></thead></table><blockquote><p>浮光掠影</p><p>浮光：水面上的反光。掠影：一闪而过的影子。比喻因事物很快消逝而使人对它的印象不深。（侧重 抽象语境）</p></blockquote><p>【例】在评价问题上，不能“浮光掠影”“点到为止”，应进行更加细致地研究。</p><p>【例】当下，对“现实题材”舞剧来说，“现实”应当是对时代内核的深刻反映，是对人性与社会内 涵的细致剖析，而非仅停留于表面的浮光掠影。</p><p>【例】搞调查研究必须深入一线，不能只坐在办公室中看汇报材料，更不能浮光掠影，将调查研究变成“旅游”。</p><blockquote><p>走马观花</p><p>走马：骑马快跑。比喻观察事物或者了解情况不深入细致。（侧重于用眼看具体事物的过程）（一般 不形容读书）</p></blockquote><p>【例】有的上级督查检查考核兴师动众但又各行其是，基层没有那么多人力、物力，往往只能应付， 而检查组也是走马观花。</p><p>【例】随着经济社会的发展和消费需求的升级，越来越多的游客愿意选择休闲游，从走马观花转向深</p><p>度体验。</p><p>【例】景区在配套设施完善上下功夫，增设服务站点、提供优质餐饮、举办节庆活动，把过去单纯的 “走马观花”式消费变成可互动可参与的深度体验消费。</p><blockquote><p>蜻蜓点水</p><p>比喻进行某些动作时只轻轻接触表面。也比喻肤浅不深入。</p></blockquote><p>【例】治标不治本、蜻蜓点水式的清理，最终只能是“换汤不换药”。</p><p>【例】在安全生产工作中，我们要严防“蜻蜓点水”式隐患排查等形式主义做法，做到隐患早发现、 问题早处置，切实维护人民群众生命财产安全和社会大局稳定。</p><p>【例】坚决杜绝粉饰太平、蜻蜓点水现象，对待前进过程中党内出现的各种问题决不能反应迟钝，慢 腾腾、软绵绵。</p><blockquote><p>浅尝辄止</p><p>辄：立即，就。只略微尝试一下就停止。比喻做事不求深入。（侧重停止）</p></blockquote><p>【例】可惜的是，我尽管出于爱好选修了足够多的人文类课程，但终是浅尝辄止、不成体系。</p><p>【例】要注重实际效果，坚决防止学习教育浅尝辄止，坚决杜绝不必要的形式创新。</p><p>【例】读书是一个长期地需要付出辛劳的过程，不能心浮气躁、浅尝辄止。</p><blockquote><p>囫囵吞枣</p><p>囫囵：整个的。把枣子整个吞下。比喻笼统地接受，不加分析、甄别，不求甚解。</p></blockquote><p>【例】事实上世界上还是有那么一些人做到，区别只在于，是认真阅读还是囫囵吞枣。</p><p>【例】特别是要注意警惕和克服对上级精神和实际情况吃不透、盲目照搬照抄上级文件、囫囵吞枣地 传给下级去落实的现象和问题。</p><p>【例】面上轰轰烈烈的传达，口号式、机械式的传达，不加消化、囫囵吞枣的传达，上下一般粗的传 达，比比皆是。</p><blockquote><p>不求甚解</p><p>甚：很，极。原指读书只领会要旨，不必在一字一句上下功夫。后指只求懂个大概，不去深入理会。</p></blockquote><p>【例】碎片化的阅读或观看，是快节奏生活下的产物，却也带来不求甚解。</p><p>【例】我们虽然不必提倡不求甚解的态度，但是，盲目地反对不求甚解的态度同样没有充分的理由。</p><p>【例】一方面，也不能把读书看得太容易，不求甚解，囫囵吞枣，抓不住实质，把握不住精髓。</p><table><thead><tr><th style="text-align:center;">形容学到一点就能推知 更多</th><th>举一反三、融会贯通、触类旁通</th></tr></thead></table><blockquote><p>举一反三</p><p>反：类推，推论。指举出一件事，就可以触类旁通，类推出许多同类事物来。（侧重于具象的事物层 面。）（侧重类推）</p></blockquote><p>【例】数字的背后，是海南对环保督察中所反映出问题的重视：不仅要立行立改，而且要真正做到举一反三。</p><p>【例】不少地方、部门和单位不光认真分析和解决一个个具体问题，还举一反三，推动解决更多同类问题。</p><p>【例】小样本的“小”并不是不需要数据，而是希望通过提高数据利用率，实现举一反三。</p><blockquote><p>融会贯通</p><p>融会：融合。贯通：全面透彻地了解。指把各方面的知识、道理融合贯穿，从而获得对事物全面、透 彻地理解和领悟。（侧重融合）</p></blockquote><p>【例】如何做到在讲好中国故事的同时与世界对话，把我们的好东西介绍到国外，把国外好的东西引 进来，洋为中用，做到中西合璧、融会贯通，是文学工作者必须破解的难题。</p><p>【例】坚持把学和做结合起来、把查和改贯通起来，做到融会贯通。</p><p>【例】开展党纪学习教育，不能孤立割裂地学，而要全面系统学、融会贯通学。</p><blockquote><p>触类旁通</p><p>触：接触。通：通晓。指掌握了某一事物的规律或知识，就能够以此类推，了解同类的其他事物。（侧 重于抽象的思维层面。）（侧重类推）</p></blockquote><p>【例】大多数人其实是被“狼来了”骗过之后才醒悟，并没有触类旁通，学会辨别这类假新闻，因而不时被低劣的造假手段“套路”。</p><p>【例】突出从细处入手、从实处着力，以问题整改为抓手，举一反三、触类旁通，做到整改一个问题、 避免一类问题，规范一个领域。</p><p>【例】研究解决高频共性难题的方法，汇集各方面力量开展协同攻关，于典型问题中触类旁通，推动 一类问题迎刃而解。</p><hr><h3 id="【第十三组】-搭档配合-8-个" tabindex="-1"><a class="header-anchor" href="#【第十三组】-搭档配合-8-个"><span>【第十三组】 搭档配合（8 个）</span></a></h3><table><thead><tr><th>合作配合</th><th>相辅相成、相得益彰、锦上添花、珠联璧合、互为表里</th></tr></thead><tbody><tr><td>二者都好</td><td>交相辉映、相映成趣、双峰并峙</td></tr></tbody></table><p>13.1 成语解释</p><table><thead><tr><th>合作配合</th><th>相辅相成、相得益彰、锦上添花、珠联璧合、互为表里</th></tr></thead></table><blockquote><p>相辅相成</p><p>辅：辅助，帮助。指两种事物互相辅助，互相促成，缺一不可。</p></blockquote><p>【例】丰富的知识和健康的身体是事业成功的基础，二者相辅相成，缺一不可。</p><p>【例】巩固文化主体性和文明交流互鉴并不矛盾，而是相辅相成、相互促进的。</p><p>【例】作为交叉学科门类下的一级学科，区域国别学应与交叉学科的整体发展相辅相成、相互促进， 推动更多学科和人才投入区域国别研究中。</p><blockquote><p>相得益彰</p><p>相得：互相投合。益：更加。彰：明显。两个人或两件事相互配合，双方的能力和作用更能显示出来。</p></blockquote><p>【例】小王的男中音和老张的手风琴伴奏相得益彰，更富有艺术感染力。</p><p>【例】评弹声与吉他声遥相呼应，烟火气与文艺范相得益彰，古老与现代的融合共生，铺就了一座城 市温暖的底色。</p><p>【例】当然，“减量化”“轻量化”不意味着“简陋化”，在简化的基础上优化设计，使包装与茶品 相得益彰，才能助推茶产业和茶文化齐头并进。</p><blockquote><p>锦上添花</p><p>指在有彩色花纹的丝织品上再绣上花朵。比喻美上加美，喜上加喜。</p></blockquote><p>【例】也许，对中国的银行来说，已经习惯了做锦上添花，而不是雪中送炭的事了。这些年来，中国 的企业，所以寿命都十分短暂。</p><p>【例】本身愉快的交易关系，因为赠品和“保价”这样本该是锦上添花的事变了味，哪头是便宜哪头 是吃亏，值得商家仔细掂量！</p><p>【例】促进区域协调发展，需要处理好政府与市场的关系，既做好锦上添花、培育增长极的事情，更 做好雪中送炭、增强欠发达地区发展能力的事情。</p><blockquote><p>珠联璧合</p><p>珠联：像珍珠一样串在一起。璧合：像碧玉一样合在一起。原指一种日月重合、五星相联的天相。后 多比喻美好的人及事物凑在一起。同“珠连璧合”。（侧重结合）</p></blockquote><p>【例】天文专家介绍说，日落后，天宇将上演“金星合木星”的奇特天象，届时这两颗最明亮的行星 将近距离“珠联璧合”，上演一幕虽短暂却精彩的“二人转”。</p><p>【例】共享与旅行的结合可谓珠联璧合，旅行的“吃、住、行、游、购、娱”几个环节都涉及到资源 信息的共享和充分利用。</p><p>【例】有为政府与有效市场的珠联璧合，为外贸稳增长打下坚实基础。</p><blockquote><p>互为表里</p><p>甲为乙的外表，乙为甲的内里；比喻互相依存，互相接受。</p></blockquote><p>【例】官僚主义与形式主义互为表里。两者往往是你中有我、我中有你，或你就是我、我就是你。</p><p>【例】不正之风与腐败现象相互交织、互为表里，严重影响党的先进性和纯洁性，侵蚀党的健康肌体， 损害党群干群关系。</p><p>【例】“四风”和腐败互为表里、同根同源，蜕化变质往往始于吃喝玩乐。</p><table><thead><tr><th>二者都好</th><th>交相辉映、相映成趣、双峰并峙</th></tr></thead></table><blockquote><p>交相辉映</p><p>各种光亮、色彩相互照耀、映射。常用语形容美好的景象。 【例】让我们为青春之中国放歌，让青春梦想与中国梦交相辉映！</p></blockquote><p>【例】风电是工业发展的产物，作为工业景观，与环境交相辉映，实现生态、人文、工业、产业深度 融合，协同发展，让人民从中体验到幸福感和获得感。</p><p>【例】走进 2024 成都世园会主会场，具有典型中国元素、东方美学的国内城市展园与充满异国风情、 多元特色的国际园艺展区交相辉映，相映成趣。</p><blockquote><p>相映成趣</p><p>映：映衬。趣：意趣。相互衬托着，显得很有情趣，很有意思。</p></blockquote><p>【例】法国游客“玩”的“糟评”，与法国作家莫迪亚诺“写”的“诺奖”相映成趣，让人不乏有些联想。</p><p>【例】清风穿竹林，水溪绕山川，与白墙黛瓦的中式建筑相映成趣，氤氲出水墨画般的盎然诗意。初 夏时节，不少游客相聚于此，享露营、住民宿，揽碧水青山入怀，拥清风暖阳入袂，体验桃花源般归隐避 世慢生活</p><p>【例】5 月 23 日，黔江区麒麟盖光伏发电站的光伏板、麒麟盖风电场的风电塔与山峦、民居、农田、 公路相映成趣，勾勒出山村独特的夏日风景。</p><blockquote><p>双峰并峙</p><p>意思通常指的是两座高大的山峰彼此对立地耸立着。这个成语不仅用来形容自然界的景观，也常用于 比喻在某一领域或文化上同时达到极高成就的两个人物或事物。</p></blockquote><p>【例】在《红楼梦》里，薛宝钗和林黛玉共用一首判词，排名第一，双峰并峙，各具风采。</p><p>【例】战国时期，屈原行吟于江上，赋予长江浪漫瑰丽的诗意想象，形成以楚辞为代表的诗歌艺术形 式，奠定了中国文学史上南《骚》北《诗》双峰并峙的格局。</p><p>【例】鲁迅研究专家、绍兴文理学院阳明文化研究中心主任卓光平认为，王阳明与鲁迅是双峰并峙的 越地思想家，也是前后相继的精神独异者，还是引领时代的思想源泉者。</p><hr><h3 id="【第十四组】工作开展顺序-4个" tabindex="-1"><a class="header-anchor" href="#【第十四组】工作开展顺序-4个"><span>【第十四组】工作开展顺序（4个）</span></a></h3><table><thead><tr><th>工作开展顺序</th><th>并行不悖、齐头并进、双管齐下、并驾齐驱</th></tr></thead></table><ol><li>成语解释</li></ol><blockquote><p>并行不悖</p><p>悖：违反，违背。指同时进行而互相不违背。</p></blockquote><p>【例】科技与法律，承载着人类社会的两种价值指向：前者不断突破人类智慧的前沿，后者则兜住公 序良俗的底线。两种价值取向其实并行不悖，都服务于人类的生存状态的改善。</p><p>【例】抢位发展强调积极进取、抢抓机遇，错位发展强调立足实际、因地制宜，二者并行不悖，都是 构建现代化产业体系的重要路径。</p><p>【例】徜徉在钟楼街，中西合璧、明净沉稳的建筑风格协调统一，美陈装置、绿化景观精致多样，传 统风格与时尚元素并行不悖。</p><blockquote><p>齐头并进</p><p>指几方面同时前进。</p></blockquote><p>【例】我们的教育、教学、科研，要齐头并进。</p><p>【例】提高粮食生产效益、增加农民种粮收入，实现农民生产粮食和增加收入齐头并进，应当成为政 策制定的关切所在。种粮收益一端是价格，一端是成本。</p><p>【例】“双 11”多平台齐头并进、商家努力争先的场景，体现了我国企业昂扬奋进的意志。</p><blockquote><p>双管齐下</p><p>管：笔管。比喻一件事同时采用两种办法或两件事同时进行。</p></blockquote><p>【例】治理社会公德失范，需要双管齐下，既要建立社会公共规则，又要重视社会公德教育。</p><p>【例】自进入新一轮森林草原防火期以来，文山市通过“技防”和“人防”双管齐下，不断增强森林 防灭火能力。</p><p>【例】乌审旗牢固树立“人才是第一资源”的理念，大力实施“人才乌审”战略，坚持“走出去”+ “引进来”的方式，双管齐下进行招才引智，不断拓宽人才引进渠道，为全旗经济社会高质量发展注入新 活力、增添新动能。</p><blockquote><p>并驾齐驱</p><p>并驾：几匹马并排在一起拉车。齐驱：共同快跑。比喻相互之间不相上下。</p></blockquote><p>【例】必须坚持民生优先，科学重建，让房屋重建与产业发展并驾齐驱。</p><p>【例】十余年间，中国品牌发展迅速，背后的产业基础逐步壮大，由奋力追赶、并驾齐驱、弯道超车，到今天的“全球车企看中国，发展机遇在中国”。</p><p>【例】技能与理财培训并驾齐驱才能让农民工的“钱袋子”真正鼓起来。</p><hr><h3 id="【第十五组】错误抉择和方向-9个" tabindex="-1"><a class="header-anchor" href="#【第十五组】错误抉择和方向-9个"><span>【第十五组】错误抉择和方向（9个）</span></a></h3><table><thead><tr><th>错误抉择</th><th>本末倒置、舍本逐末、喧宾夺主、因小失大、因噎废食</th></tr></thead><tbody><tr><td>相悖、相反</td><td>背道而驰、南辕北辙、分道扬镳、缘木求鱼</td></tr></tbody></table><p>15.1 成语解释</p><table><thead><tr><th>错误抉择</th><th>本末倒置、舍本逐末、喧宾夺主、因小失大、因噎废食</th></tr></thead></table><blockquote><p>本末倒置</p><p>本：树根。末：树梢。比喻把事情的轻重主次颠倒了过来。</p></blockquote><p>【例】搞政治理论和业务学习，却以“停止办公”之举直接违背这个最基本的“政治理论”，实在是 本末倒置。</p><p>【例】在这样的现代法治体制面前，大数据、云计算、信息技术、人工智能都只是实现合法正义的辅 助手段，切不可本末倒置，这是我们始终应该铭记的一条基本原则。</p><p>【例】因此，将媒体传播作为国际传播的主战场，纵然是本末倒置、不得要领，却也是在文化输出上 没有知识积累、缺少人脉网络、基础条件薄弱的一种尴尬选择。</p><blockquote><p>舍本逐末</p><p>舍：放弃。本：根本。逐：追求。末：枝节。比喻放弃根本，追求末节，轻重主次倒置。</p></blockquote><p>【例】这样的分析和议论，选择性忽视了两个赞助对于体育事业发展的裨益，而纠结于所谓的营销效 果，属于舍本逐末的“跑偏”。</p><p>【例】追求厚重感绝不能以牺牲价值观为代价，否则便成了舍本逐末。</p><p>【例】做好食物、营造良好的就餐环境才是餐厅应当走上的正途，内卷高分无异于舍本逐末。</p><blockquote><p>喧宾夺主</p><p>喧：吵吵嚷嚷。客人的声音压倒了主人的声音。比喻外来的或次要的事物占据了原有的或主要的事物的位置。</p></blockquote><p>【例】麻辣语录走红，折射出社会各界对学校加快教学法改革的期待，但喧宾夺主分散学生学习知识 的注意力，就不值得提倡了。</p><p>【例】茶点滋味略带咸，结合乌龙茶的香醇，不会喧宾夺主的同时，使得茶汤滋味更上一层楼。</p><p>【例】强调兼容用途对主用途的辅助支撑作用，兼容用途不能反超主用途，喧宾夺主。</p><blockquote><p>因小失大</p><p>因：为了。指为了小的利益造成大的损失。</p></blockquote><p>【例】有的人就看着鼻子底下这点实利，算盘珠子拨得很精，看起来很聪明，实际上常常因小失大。</p><p>【例】要处理好抓大与放小，抓住关系全局的大事，防住影响大局的小事，防止因小失大。</p><p>【例】警惕各类打着“内幕消息”“高额回报”旗号的私下换汇、投资理财、优惠购物、刷单赚钱信 息，切勿因一时贪图便宜而因小失大。</p><blockquote><p>因噎废食</p><p>废：停止。比喻因为碰到挫折，连该做的事情也不做了。</p></blockquote><p>【例】在这样的背景下，这两个项目进展不理想只能作为个案处理，我们不能因噎废食。</p><p>【例】慎独慎微不是谈友色变，守住交往关也不是要因噎废食，在不违反纪律和规矩的前提下，需遵 循一定的“尺度”。</p><p>【例】盲目的禁止犹如因噎废食，与公共交通的便民性和利民性背道而驰。</p><table><thead><tr><th>相悖、相反</th><th>背道而驰、南辕北辙、分道扬镳、缘木求鱼</th></tr></thead></table><blockquote><p>背道而驰</p><p>背：逆着。道：道路。向着相反的道路奔驰。比喻彼此方向目标完全相反。也比喻背离正确的目标， 朝相反的方向走。</p></blockquote><p>【例】以零售和餐饮经营的服务业都应该向日本学，正如日本餐饮业的赚钱大王西腾公司，其经营技 术与一般餐饮企业背道而驰，但却成为日本餐饮界当之无愧的赚钱大王。</p><p>【例】社会舆论应该理性地跳出何涛“道德模范”的特殊身份，严格按照法律来进行公允的评判，才 能真正救赎这个和法律“背道而驰”的“道德标杆”。</p><p>【例】倘若搞“拍脑袋”“一刀切”，甚至为了所谓政绩好看搞摊派，就会与初衷背道而驰。</p><blockquote><p>南辕北辙</p><p>辕：车辕，车前驾牲口的直木。辙：车轮经过留下的痕迹。本要往南边去却驾车向北。比喻行动和目 的截然相反。（多形容同一个主语、侧重目标与做法相反）</p></blockquote><p>【例】如果一方面盼望有功于“世道人心”的文艺，而同时又不许文艺作品带着强心和清泻的药品， 这何异南辕北辙？</p><p>【例】司机以为我是人生地不熟的外来客，南辕北辙地大兜圈子。</p><p>【例】如果政绩观不端正，抓落实就会失去方向、脱离轨道，看似做了很多工作，却可能是无用功， 甚至南辕北辙。</p><blockquote><p>分道扬镳</p><p>道：道路。镳：马嚼子。分开道路，驱马前进。指分道而行。也比喻思想、志趣不同而各人干各人的 事。也作“扬镳分路”。</p></blockquote><p>【例】经历了两年半的短暂“联姻”，青岛啤酒与三得利最终分道扬镳。</p><p>【例】欧洲政客与科学家正在抗击新冠的战斗中分道扬镳。</p><p>【例】研究发现，在晚白垩纪时期，这两大鸟类谱系便已经分道扬镳，远早于著名的恐龙灭绝事件。</p><blockquote><p>缘木求鱼</p><p>缘木：爬树。比喻方向、方法不对，就不可能达到目的。（侧重方法错误）</p></blockquote><p>【例】脱离经济发展抓环境保护是“缘木求鱼”，离开环境保护搞经济发展是“竭泽而渔”，正确的 经济政策就是正确的环境政策。</p><p>【例】诚信有着坚实的精神内核，诚在于仁，信近乎义，离开仁义谈诚信如同舍本逐末，缘木求鱼。</p><p>【例】牢固树立和践行绿水青山就是金山银山的理念，既不能在发展经济的过程中对生态环境竭泽而 渔，也不能在保护生态环境的过程中舍弃经济发展而缘木求鱼，而是坚持走绿水青山和金山银山双赢的可 持续发展道路。</p><hr><h3 id="【第十六组】错误做法-寓言故事-10个" tabindex="-1"><a class="header-anchor" href="#【第十六组】错误做法-寓言故事-10个"><span>【第十六组】错误做法（寓言故事）（10个）</span></a></h3><table><thead><tr><th>错误做法</th><th style="text-align:left;">拔苗助长、越俎代庖、削足适履、闭门造车、刻舟求剑、 守株待兔、掩耳盗铃、邯郸学步、东施效颦、与虎谋皮</th></tr></thead></table><p>16.1 成语解释</p><blockquote><p>拔苗助长</p><p>用来比喻违反事物的发展规律，急于求成，反而坏事。也作“揠苗助长”。</p></blockquote><p>【例】超前教育无异于拔苗助长，看似成绩领先，特长傍身，实则根基不稳，不能行稳致远。</p><p>【例】中国电影这些年高速增长有目共睹，但在各路人马疯狂携资进入影视领域的当下，中国电影也 有泡沫之虞，需重塑“好心态”，莫急功近利，更不能拔苗助长。</p><p>【例】但仍有部分家长安排孩子“超前学习”，这到底是在帮孩子赢在起跑线，还是在拔苗助长毁其 未来？</p><blockquote><p>越俎代庖</p><p>越：超过。俎：古代盛牛羊等祭品的器具。庖：厨师。比喻处理超过自己职权范围的事情。</p></blockquote><p>【例】孩子能做的事，父母不要越俎代庖。</p><p>【例】野生动物往往有其独特习性，保护不能越俎代庖、事事包办，而要“有所为有所不为”。</p><p>【例】要加强规范，专业问题专业办，切忌妄图省事越俎代庖。</p><blockquote><p>削足适履</p><p>履：鞋。鞋小脚大，把脚削去一块来凑合鞋的大小。比喻不合理地迁就凑合或不顾具体条件，生搬硬套。</p></blockquote><p>【例】倘若作者如此牺牲了书写的自由，即使极小部分，也无异于削足适履。</p><p>【例】我国幅员辽阔、人口众多，各地的资源禀赋、产业基础、人才结构和发展状况不尽相同，不能 简单套用单一发展模式，削足适履地盲目追“新”。</p><p>【例】习近平总书记指出：“现代化道路并没有固定模式，适合自己的才是最好的，不能削足适履。</p><blockquote><p>闭门造车</p><p>关起门来造车子。比喻不管客观情况如何，只凭主观愿望办事。（多形容不开放、自我封闭）</p></blockquote><p>【例】广义的开放没有问题，不能闭门造车，不能单打独斗，不能不学习人家的先进技术。</p><p>【例】要践行“身入心至”的求实作风，从“闭门造车”中摆脱出来，从“纸上谈兵”中解放出来。</p><p>【例】流芳百世的作品都是需要实践检验的，都是需要大众认可的，都是饱含生活之中的真情实感， 不是坐在屋子里闭门造车写出来的，不是钱财名利所能左右的。</p><blockquote><p>刻舟求剑</p><p>求：寻找。比喻拘泥、不变通，不懂得根据实际情况处理问题。也比喻徒劳无功，达不到目的。</p></blockquote><p>【例】人的一切身家性命信息都是在变动的，如果非要像填表时代那样记录静态的数据，那就像刻舟 求剑，永远难以还原一个人的全息图景。</p><p>【例】在此背景下，如果继续用传统的观念来看待和分析问题，继续用高速增长阶段的逻辑来解读经 济指标和经济数据，无异于“刻舟求剑”。</p><p>【例】习近平总书记在陕西榆林考察时强调：“要坚持马克思主义基本原理，同时坚持实事求是，不 断推进马克思主义中国化、时代化，学马克思主义不能刻舟求剑。”</p><blockquote><p>守株待兔</p><p>株：树桩子。比喻心存侥幸，不劳而获。也比喻死守狭隘经验，不知变通。</p></blockquote><p>【例】他说自己的投资理念其实很简单，就是找估值最低的个股，重仓布局，守株待兔。</p><p>【例】习近平总书记强调，“推进中国式现代化是一个探索性事业，还有许多未知领域，需要我们在 实践中去大胆探索，通过改革创新来推动事业发展，决不能刻舟求剑、守株待兔”。</p><p>【例】立足当下，展望未来，在进一步推进中国式现代化的探索中，我们依然要依靠改革开放这一法 宝来推动事业发展，决不能刻舟求剑、守株待兔。</p><blockquote><p>掩耳盗铃</p><p>掩：掩盖，遮盖。比喻自欺欺人。同“掩耳偷铃”。</p></blockquote><p>【例】个人主义严重的人，常常会干出掩耳盗铃的蠢事，这又有什么奇怪呢！</p><p>【例】‘包装式’落实，表现形式多为‘雷厉风行’装点敷衍塞责，以‘假把式’装饰门面欺上瞒下， 这种掩耳盗铃的做法，最终必将是搬起石头砸自己的脚。</p><p>【例】安全生产是实打实、硬碰硬的工作，说了不等于做了，做了不等于做成了，做完了也不等于做 好了，既不能流于形式，更不能搞掩耳盗铃、弄虚作假那一套。</p><blockquote><p>邯郸学步</p><p>比喻一味地模仿别人，不仅没学到本事，反而把原来的本事也丢了。</p></blockquote><p>【例】各地文旅在“抄作业”时，也要谨防“邯郸学步”，阻碍了当地旅游产业的发展壮大。</p><p>【例】创新之思：邯郸学步，走不出强军之路。 【例】论文要得文中天，邯郸学步终不然。</p><blockquote><p>东施效颦</p><p>比喻盲目地胡乱模仿，效果适得其反。</p></blockquote><p>【例】虽然“西方甄选”及时被封，难挡南方臻选、北方臻选、中方甄选们继续东施效颦。</p><p>【例】如果城市营销一味追求流量而“东施效颦”，丢掉了城市的文化根脉和自身特色，将被流量所抛弃。</p><p>【例】“东施效颦”不成，通过制造舆论挑唆“一带一路”合作，企图“花小钱办大事”，似乎也成 为了美国的“权宜之计”。</p><blockquote><p>与虎谋皮</p><p>跟老虎商量取下它的皮来，比喻跟坏人商量要其牺牲自己的利益，是绝对办不到的。</p></blockquote><p>【例】美国民众要指望着这些政客来做出回应性的改革和调整，完全是与虎谋皮、毫无可能。</p><p>【例】国际联盟，是帝国主义压迫弱小民族的工具，希望国际联盟来帮助中国，无异与虎谋皮。</p><p>【例】在这样的情况下，国际社会若要与美国“团结一致”，岂不是与虎谋皮？</p><hr><h3 id="【第十七组】教育影响批评-13个" tabindex="-1"><a class="header-anchor" href="#【第十七组】教育影响批评-13个"><span>【第十七组】教育影响批评（13个）</span></a></h3><table><thead><tr><th>影响感化</th><th>潜移默化、耳濡目染、如沐春风、润物无声、成风化人</th></tr></thead><tbody><tr><td>教育和批评</td><td>耳提面命、苦口婆心、循循善诱、诲人不倦、春风化雨、 和风细雨</td></tr><tr><td>醒悟、唤醒</td><td>醍醐灌顶、振聋发聩</td></tr></tbody></table><ol><li>成语解释</li></ol><table><thead><tr><th>影响感化</th><th>潜移默化、耳濡目染、如沐春风、润物无声、成风化人</th></tr></thead></table><blockquote><p>潜移默化</p><p>潜：暗中。默：毫无声息。指受到外来影响而在不知不觉中发生变化（侧重长时间）。</p></blockquote><p>【例】精神文明建设关键要认真抓、切实干，做到潜移默化、春风化雨，引导群众树立文明观念、争当文明公民。</p><p>【例】我想，军歌的魅力，就是能够在潜移默化中发挥教育引导作用，产生直抵人心的力量。</p><p>【例】我们党高度重视发展积极健康的政治文化，以良好政治文化涵养政治生态，潜移默化地影响着 政治环境建设，不仅使党内正气充盈，而且推动经济社会发展环境日益优化。</p><blockquote><p>耳濡目染</p><p>濡：沾染。因经常听到看到而不知不觉受到影响。同“耳染目濡”“目染耳濡”“目濡耳染”。（侧 重身处某种外在环境中）</p></blockquote><p>【例】环保行为的养成仅靠大道理还不够，还需要家庭的耳濡目染和社会的良好氛围。</p><p>【例】洪海涛生动讲述了身为“航二代”，从小耳濡目染受到父母熏陶，在航天精神引领下，多年坚 守航天生产一线的故事。</p><p>【例】让师生在耳濡目染之中深刻感悟英雄的不朽，深切感受方志敏烈士对共产主义理想的坚定信仰 和对祖国人民的无限热爱。</p><blockquote><p>如沐春风</p><p>通常用来形容人感到非常舒适和愉悦。它的意思是像沐浴在春风里一样，感受到舒适的感觉。</p></blockquote><p>【例】“芜湖正在建设省域副中心城市，因为坐拥长三角，覆盖了我们 80%以上的客户，最关键的是 当地让企业如沐春风的营商环境，没有理由不选择这里。”</p><p>【例】良好的营商环境已成为酒泉市一道新的亮丽风景线，不仅让企业如沐春风，而且使得磁场效应 加速显现。</p><p>【例】鼓舞人心的话语，浓浓暖意让人如沐春风，铿锵力量令人意气风发。</p><blockquote><p>润物无声</p><p>指有大胸怀者，做了贡献而不张扬，默默奉献。（也指文化教育等对人的潜移默化的影响）</p></blockquote><p>【例】道德是一种力量，它春风化雨，润物无声；道德是一面旗帜，它鼓舞人们，引领方向。</p><p>【例】教书育人，润物无声。在人才培养和教学工作方面，刘老师坚持走在本科教学第一线。</p><p>【例】通过继古开今、润物无声的方式，习近平新时代中国特色社会主义思想一定能得到更好地传播，我们弥足珍贵的中华优秀传统文化一定能得到更好的传承。</p><blockquote><p>成风化人</p><p>成风，倡树一种社会风气。化人，感染人、影响人、教育人。通过倡导树立一种社会风气来影响、教 育感化社会大众。</p></blockquote><p>【例】我们要深入学习领会、认真贯彻落实这些重要论述精神，切实传承发展好中华传统美德，用中 华传统美德成风化人。</p><p>【例】以优秀历史文化铸魂，用艺术作品成风化人。</p><p>【例】既依法治理高额彩礼，也做好成风化人的文章，加强精神文明建设，发挥好村规民约、道德公 约等激励约束作用。</p><table><thead><tr><th>教育和批评</th><th style="text-align:left;">耳提面命、苦口婆心、循循善诱、诲人不倦、春风化雨、 和风细雨</th></tr></thead></table><blockquote><p>耳提面命</p><p>“耳提”，提着耳朵叮嘱；“面命”，当面指教，形容教诲殷切。多指（长辈对晚辈、上级对下级） 恳切地教导。</p></blockquote><p>【例】父亲经常教育我们兄弟俩要‘一心为公、两袖清风’，他也深知我工作的重要性，对我更是耳提面命。</p><p>【例】长时间的网课，师生之间没有那么多面对面交流的机会，老师不再像以前那样“耳提面命”。</p><blockquote><p>苦口婆心</p><p>用来形容一个人善意且耐心地反复劝导他人。</p></blockquote><p>【例】走街串巷查隐患、苦口婆心解纷争、一丝不苟办实事……在日常生活中，一群来回穿梭在社区、乡村的网格员，用他们忙碌的身影，提升了群众的安全感。</p><p>【例】今年 4 月以来，他和战友们一道，与犯罪分子斗智斗勇，对诈骗高危人员苦口婆心，已经累计 成功劝阻10 余起，为群众及时止损 70 余万元。</p><p>【例】要求大家落实好“一岗双责”，认真领会各项规章制度要求，做到“苦口婆心地讲”，对于苗 头性倾向性问题要及时谈话教育。</p><blockquote><p>循循善诱</p><p>循循：有次序的样子。诱：引导。指善于有步骤地引导别人。多形容教育得法。</p></blockquote><p>【例】社会文明的形成，需要自律，也需要他律；需要循循善诱地劝导，也需要法律制度的规范。</p><p>【例】在谈心谈话环节，落落大方、循循善诱、入情入理、用心用情，充分展现了我校辅导员扎实的 理论水平、专业技能素养和工作实践能力。![ref54]</p><p>【例】选手们需要通过良好的沟通技巧，就学业困惑、人际关系、恋爱失意、职业规划等问题循循善 诱、答疑解惑。</p><blockquote><p>诲人不倦</p><p>诲：教诲，教导。教导人时很有耐心而不知疲倦。</p></blockquote><p>【例】从经师到人师，从教书先生到大国良师，好的教师学而不厌、诲人不倦，为党育人、为国育才。</p><p>【例】他们都在三尺讲台展现出了甘于奉献、诲人不倦的师者风范和言为士则、行为世范的道德情操。</p><p>【例】新时代教师要更加坚定历史自信、文化自信，弘扬中华民族崇文重教、有教无类的传统和历代 教育家因材施教、诲人不倦的精神。</p><blockquote><p>春风化雨</p><p>宜于万物生长的和风，适时的雨。后用来比喻良好的教育。多用来称颂师长对学生及晚辈潜移默化的 教诲。（也可用来形容文化或教育等对人的影响）</p></blockquote><p>【例】把党史学习教育做到青少年心坎上，春风化雨般滋润青少年的心田，让他们与党史故事和人物 产生情感共鸣、与优秀文化与民族精神发生思想共振，汲取奋发向上的力量。</p><p>【例】优秀传统文化与核心价值观建设有机结合起来，经过每天每月耳濡目染，如春风化雨般滋润着 各地人民的心田。</p><p>【例】在科室人文建设中，极力避免形式大于内容，搞作秀式的人文活动，而是与科室特色相结合， 立足长远，春风化雨，润物无声。</p><blockquote><p>和风细雨</p><p>意思是比喻耐心地和颜悦色地批评或劝说。</p></blockquote><p>【例】周口下大力气打造精神文明建设品牌项目，开展了道德模范的感人事迹、无私奉献的志愿服务 精神、内容丰富的公益剧场等活动，这样的活动犹如和风细雨，在潜移默化中改变着市民的思想素质、生 活方式和精神面貌。</p><p>【例】一个个感人的家风家训故事，如和风细雨，滋润着现场听众的心田。</p><p>【例】群众和企业的发声有时“和风细雨”，有时也许尖锐甚至刺耳。</p><table><thead><tr><th>醒悟、唤醒</th><th>醍醐灌顶、振聋发聩</th></tr></thead></table><blockquote><p>醍醐灌顶</p><p>醍醐：从牛乳中提炼出的纯酥油，佛教比喻最高的佛法。灌：浇。顶：头顶。比喻给人灌输智慧，使 人从迷惑中醒悟或彻底觉悟。也比喻舒适畅快。</p></blockquote><p>【例】母亲漫不经心说出的一句“有钱难买回头看”，让我顿时有一种醍醐灌顶的顿悟和追悔莫及的 反思。</p><p>【例】讲授内容条理清晰、通俗易懂，令人醍醐灌顶，茅塞顿开。</p><p>【例】系统内容主要由楷模风范、廉诗警句、案例警示等组成，图文并茂，既赏心悦目，又醍醐灌顶， 全力营造崇廉、尊廉、促廉、守廉的良好氛围。</p><blockquote><p>振聋发聩</p><p>比喻唤醒糊涂麻木之人。</p></blockquote><p>【例】总书记的讲话振聋发聩，语重心长。</p><p>【例】面对民族生死存亡，这座小院率先回应了历史转折时期人民的呼声，率先发出了振聋发聩的救</p><p>国强音。</p><p>【例】人类命运共同体理念一经提出，就以振聋发聩的力量引发强烈反响，得到国际社会越来越广泛 的认同和支持。</p><hr><h3 id="【第十八组】-工作学习状态-22-个" tabindex="-1"><a class="header-anchor" href="#【第十八组】-工作学习状态-22-个"><span>【第十八组】 工作学习状态（22 个）</span></a></h3><table><thead><tr><th>工作谨慎认真</th><th>如履薄冰、兢兢业业</th></tr></thead><tbody><tr><td>尽心钻研学习</td><td>殚精竭虑、呕心沥血、苦心孤诣、废寝忘食、皓首穷经、 心无旁骛、乐此不疲、挖空心思（贬义）</td></tr><tr><td>抓紧时间、勤奋</td><td>夜以继日、焚膏继晷、夙兴夜寐、目不窥园</td></tr><tr><td>辛苦奔波</td><td>栉风沐雨、风餐露宿、筚路蓝缕、披星戴月、摩顶放踵、 胼手胝足</td></tr><tr><td>勤勉不懈怠</td><td>孜孜不倦、孜孜以求</td></tr></tbody></table><p>18.1 成语解释</p><table><thead><tr><th>工作谨慎认真</th><th>如履薄冰、兢兢业业</th></tr></thead></table><blockquote><p>如履薄冰</p><p>履：踩，走过。形容战战兢兢、小心谨慎的样子。</p></blockquote><p>【例】这任务关系着几千人的身家性命，我们如履薄冰，哪容有丝毫的疏忽大意？</p><p>【例】每一次抢救都是一场没有硝烟的战争，步步惊心、如履薄冰。患者的信任，专家团队的精诚合 作，使产妇和胎儿最终度过难关恢复健康。</p><p>【例】所谓“畏则不敢肆而德以成，无畏则从其所欲而及于祸”，始终如临深渊、如履薄冰，人生的 路才能免于灾祸，越走越宽。党规党纪是令行禁止的高压线，换个角度看也是自我保护的安全线。</p><blockquote><p>兢兢业业</p><p>原形容畏惧的样子。后多用来形容小心谨慎、不敢懈怠的样子。</p></blockquote><p>【例】要用最强有力的作风建设，引导党员干部从不为到有为、从避事到干事，形成堂堂正正为官、 兢兢业业干事的好风气。</p><p>【例】沈中明是一名老党员，扎根基层工作数十余年，工作兢兢业业、清正廉洁，在当地德高望重。 对于父亲的病逝，沈结方及家人果断简化丧事办理流程，实施一次性安葬，为当地移风易俗开了先例。</p><p>【例】这份荣誉不属于我一个人，应属于千千万万在平凡岗位上兢兢业业的快递员。苏金姬表示，将 一如既往立足本职、扎根基层，在岗位上贡献光和热，把最好的服务带给市民。</p><table><thead><tr><th>尽心钻研学习</th><th style="text-align:left;">殚精竭虑、呕心沥血、苦心孤诣、废寝忘食、皓首穷经、 心无旁骛、乐此不疲、挖空心思（贬义）</th></tr></thead></table><blockquote><p>殚精竭虑</p><p>殚：竭尽。用尽精力和心思。</p></blockquote><p>【例】王教授为了钻研这个课题，殚精竭虑，废寝忘食。</p><p>【例】一百多年前，为了唤醒中国“睡狮”，为了挽救“神州陆沉”，一群仁人志士殚精竭虑，探寻 救国救民的真理。在文化的觉醒中，我们选择了马克思主义思想和社会主义道路，同时葆有中国道路的独 特性和中华文明的独立性。</p><p>【例】徐建寅殚精竭虑，日夜攻求，又于 3 月 25 日亲手试制出数磅优质无烟火药，烧后已没有渣滓， 爆炸后产生的气体是原来旧式火药的三倍，其威力已达到当时世界领先水平。至此，中国人终于掌握了属 于自己的无烟火药技术。</p><blockquote><p>呕心沥血</p><p>呕：吐。沥：滴。形容费尽心思。</p></blockquote><p>【例】他，呕心沥血把生命最后的光辉都奉献给中国的外宣事业。</p><p>【例】运营国内首座文物保护领域多场耦合实验室、成立国内目前唯一的国家古代壁画保护工程技术 研究中心、初步建成具有行业示范和影响力的“安全管理平台及安全应急指挥中心”……保护好敦煌这座 文明交流互鉴的生动样本，几代敦煌人呕心沥血。</p><p>【例】马兰花，默默人间美丽；马兰花，静静岁月流传……”这是清华大学原创话剧《马兰花开》主 题曲中的歌词，深情歌颂了“两弹元勋”邓稼先为祖国核事业呕心沥血、忘我奋斗的不平凡人生。</p><blockquote><p>苦心孤诣</p><p>苦心：用尽心思。诣：（学业、技能等）所达到的程度。孤诣：别人达不到的程度。指尽心竭力钻研， 达到了别人所达不到的地步。也指为了达到目的而费尽心思。</p></blockquote><p>【例】要改良社会，不得不这样，别人哪里会懂我们的苦心孤诣。</p><p>【例】近些年，中国的“老三样”（手机、电脑、家用电器）和“新三样”（新能源汽车、锂电池、 光伏产品）之所以在国际市场上广受欢迎，靠的是海量细分行业中持续的技术创新和极致的效率改进，靠 的是中国企业家的苦心孤诣、精益求精。</p><p>【例】姜治莹强调，哲学社会科学始终是吉大引以为傲的闪亮名片和一流建设的重要力量，一大批创 业先贤曾在这里苦心孤诣、奋斗终身，为学科发展夯基垒台、架梁立柱，留下了伟大的足迹，镌刻下不朽 的功勋。</p><blockquote><p>废寝忘食</p><p>顾不上睡觉，忘记了吃饭。形容对某事专心致志。 【例】对于如此繁杂的调试工作，朱志强和调试团队这半年多来几乎废寝忘食地投入。</p></blockquote><p>【例】2023 年，煤矿设备需要对监测监控系统进行全面升级，由于当时的监控系统传输稳定率较差， 闪断时而产生，李岩那段时间每天与厂家交流试用新设备、学习掌握新系统的原理和性能，他废寝忘食、 手不释书，成为第一个全面熟练掌握新监测监控系统性能的人。</p><p>【例】经过连续 12 天废寝忘食、奋战在一线，他克服晕船呕吐的问题，登轮进舱作业、检测 30 多次， 从测试论证到登轮注氮作业，从船舶靠泊、油品过驳到清舱处理直至险情解除。</p><blockquote><p>皓首穷经</p><p>皓：白。穷经：彻底钻研经书。钻研经籍直到人老头白。也作“白首穷经”。（也可形容长期钻研）</p></blockquote><p>【例】古人常说，稚年入学，皓首穷经，书好像一辈子读不完似的。</p><p>【例】图书编辑，是一份严谨细致的工作，编纂一部 66 个学科门类、1.264 亿字的皇皇巨著，更要历 经皓首穷经、字斟句酌的艰辛。</p><p>【例】一些学者皓首穷经，对甲骨文研究工作孜孜以求，既以对甲骨文的深刻研究探究汉字形成和演 变的历史，完善对中国文化史的了解，又据此锚定中华文明史的起始时间，考据商代最原始、最真实的时 事和生活。</p><blockquote><p>心无旁骛</p><p>旁；另外的。骛：追求。心思没有另外的追求，形容心思集中，专心致志。</p></blockquote><p>【例】只有逐步建立与现实对接的待遇增长机制，才能让我们的干部干事创业心无旁骛、后顾无忧。</p><p>【例】“实实在在、心无旁骛做实业，这是本分。”丁世忠建议，通过深化改革，进一步优化营商环 境，用科技改造提升传统产业，坚定民营企业扎实发展的信心。从最开始的来料加工为主到现在创立自己 的品牌，进而超过国际品牌，我观察这些异军突起的企业，就是心无旁骛、一以贯之。</p><p>【例】这里不仅有优惠的政策，更有高效的服务，让我们能够心无旁骛地投入到产品研发和市场开拓 中。如今，营山县正以“聚沙成塔”的决心，一步一个脚印地推进工业发展。</p><blockquote><p>乐此不疲</p><p>因酷爱干某事而不感觉厌烦。形容对某事特别爱好而沉浸其中。</p></blockquote><p>【例】变废为宝是该公司一直以来乐此不疲的事情，但随着生产经营规模的扩大，企业遇到了融资难问题。</p><p>【例】在这个春天，观察、拍摄鸟类让不少年轻人乐此不疲。</p><p>【例】村民唐高友正在家门口的小菜园里浇水，自从村内统一开辟了小菜园，他每天乐此不疲。</p><blockquote><p>挖空心思</p><p>想方设法，费尽心机（多含贬义）。</p></blockquote><p>【例】为了持续制造爽感、快感，一些微短剧创作者挖空心思，甚至无所不用其极——有的鼓吹炫富 拜金，大肆描写不正当的婚恋观；有的宣扬以暴制暴，渲染血腥暴力；有的故意贩卖焦虑，挑起年龄对立、 性别对立、地域歧视。</p><p>【例】一些人将吃吃喝喝遁入“地下”，披上“隐身衣”，躲进“青纱帐”，挖空心思躲避监督。</p><p>【例】同时美方基于对华遏制打压和“以激烈竞争定义中美关系”的执念挖空心思，又抛出一套“中 国产能过剩威胁”的奇谈怪论，诬称中国对电动车、光伏和新能源产业的持续补贴导致“产能过剩”，并 “廉价输出海外”，对美国和其他国家的相应产业造成威胁。</p><table><thead><tr><th>抓紧时间、勤奋</th><th>夜以继日、焚膏继晷、夙兴夜寐、目不窥园</th></tr></thead></table><blockquote><p>夜以继日</p><p>日夜不停地做某事。多形容十分勤奋、勤恳、忙碌。</p></blockquote><p>【例】现在每一天的状态就是夜以继日，所做的工作事无巨细。</p><p>【例】陈庆川说，以人工智能技术在聚变领域中的研究应用为例，团队成员来自各个专业、各个领域， 夜以继日地守着装置做实验、分析实验数据、优化迭代实验方案，最终实现了装置的安全稳定和各成果的 可靠产出。</p><p>【例】凭着夜以继日的努力，一段时间以后，余林成功化解了 94 起各类纠纷，为龙潭村的和谐团结 夯实了群众基础。走万里路，进千家门，解百家难。别人“躲着走”的难事、愁事，余林积极走访，为群 众化解矛盾纠纷。</p><blockquote><p>焚膏继晷</p><p>焚：点燃；膏：灯油或蜡烛；继：接续；晷：日影、日光。意思是点燃灯烛来接替日光照明，形容夜以继日地用功读书或努力工作</p></blockquote><p>【例】人类的美好理想，都不可能唾手可得，都离不开手胼足胝、焚膏继晷的艰苦奋斗。</p><p>【例】一项项“卡脖子”技术被攻克，背后是每个科研尖兵勇做攀登高峰的开拓者，焚膏继晷、孜孜 以求……无数“每个”构成了 14 亿多人的奋进姿态，绘就奔跑的中国的壮美画卷。</p><p>【例】百年来，一代又一代的考古学人薪火相传、焚膏继晷，就人类起源、农业起源、文明起源、国 家起源等重大研究课题，交出了令世界瞩目的“中国答卷”，为构建中华文明史、弘扬中华优秀传统文化 做出了考古人的独特贡献。</p><blockquote><p>夙兴夜寐</p><p>夙：早。兴：起来。寐：睡觉。早起晚睡，形容勤奋辛劳。</p></blockquote><p>【例】他们夜以继日、夙兴夜寐，默默奋战在每一个白天和夜晚，以实际行动尽职担当，全力以赴投 入到疫情防控阻击战中。</p><p>【例】全市上下以踏石留印、抓铁有痕的精神，坚定不移强作风，持之以恒优环境，夙兴夜寐、奋勇 争先，扎扎实实抓好各项工作，为龙江全面振兴、全方位振兴贡献双鸭山力量。</p><p>【例】从夙兴夜寐的临床护理，到严谨细致的病区管理，再到科学规范的全院护理管理，从一线成长 起来的包满珍几乎对医院护理的所有事项了然于胸。“护士的工作应以专业护理为主，在实际中，大量的 琐事也会找到护士。”</p><blockquote><p>目不窥园</p><p>窥：看。形容学习专心致志，十分刻苦。</p></blockquote><p>【例】他搞这些研究已经很久了，目不窥园，终于成功了。</p><p>【例】他从唐诗下手，目不窥园，足不下楼，兀兀穷年，沥尽心血。杜甫晚年，疏懒得“一月不梳头”。 闻先生也总是头发凌乱，他是无暇及此。闻先生的书桌，零乱不堪，众物腾怨，闻先生心不在焉，抱歉地 道一声：“秩序不在我的范围以内。”饭，几乎忘记了吃，他贪的是精神食粮；夜间睡得很少，为了研究， 他惜寸阴、分阴。深宵灯火是他的伴侣，因它大开光明之路，“漂白了四壁”。</p><p>【例】作为一名企业家，要以目不窥园的眼光看待市场的整体格局，才能更好地把握商机。</p><table><thead><tr><th>辛苦奔波</th><th style="text-align:left;">栉风沐雨、风餐露宿、筚路蓝缕、披星戴月、摩顶放踵、 胼手胝足</th></tr></thead></table><blockquote><p>栉风沐雨</p><p>栉：梳头。沐：洗头。形容人辛苦地四处奔波。</p></blockquote><p>【例】栉风沐雨、砥砺奋进。前进的道路少了磨难反倒是少了亮丽的风景，唯有经历磨难，才能不畏艰险。</p><p>【例】在我国 960 多万平方公里的广袤大地上，红色资源星罗棋布，一处处革命旧址、一座座革命纪 念馆、一件件革命文物，记录和见证了我们党团结带领人民栉风沐雨、接续奋斗的红色往事，凝聚和展现 了中国共产党人抱定初心、忠贞不渝的红色精神</p><p>【例】满院字迹中，我们看到一位栉风沐雨的农民，在大地上勤劳耕耘；看到一个热爱生活的老人， 留心记录平凡日子里的幸福；看到一名操碎了心的父亲，唠唠叨叨里满是对子女的深情。</p><blockquote><p>风餐露宿</p><p>在风中吃饭，在露天住宿。形容旅途中的辛苦劳累。</p></blockquote><p>【例】该团官兵担负国防通信光缆施工任务，常年身处荒漠戈壁、深山密林，部队分散流动，生活保 障是个难题，官兵风餐露宿是常态。</p><p>【例】三年来，每年用工高峰时达 2000 余人，他们从四方汇集而来，不惧艰苦、风餐露宿，面对恶劣 的劳作环境，一锹锹、一镐镐，硬是将山川变了个样。面对万千株随风摇曳的绿苗，不由得让人肃然起敬。</p><p>【例】作为国内知名的大型建筑央企，工作的性质决定着中铁四局的工程人风餐露宿、四海为家。许 多年轻人主动推迟婚期，奋战在国家重点工程建设一线，奉献了青春的智慧和汗水。</p><blockquote><p>筚路蓝缕</p><p>筚路：柴车。蓝缕：又旧又破的衣服。意思是驾着柴车穿着破旧衣服去开辟山林。现用来形容创业的 艰难与辛苦。也作“荜路蓝缕”。（词义有拓展，也可形容事情发展的初期阶段）</p></blockquote><p>【例】随着“洋机器”的进入，才出现了第一代荜路蓝缕的企业家，他们推动着中国进入现代文明的 序列。</p><p>【例】人类从洪荒的远古走来，向着希望的明天走去，昨天与今天，看似短暂，但人类在其间荜路蓝 缕、上下求索，又是何等漫长与艰辛。,</p><p>【例】台湾是经过一代代中国人筚路蓝缕辛勤开发，才有今天的建设成就。赖清德为了其“台独”妄想，企图泯灭岛内青少年和民众的中华民族意识，把荷兰殖民者抢占台湾的 1624 年，当作“开启台湾全 球化的开端”。</p><blockquote><p>披星戴月</p><p>身披星光，头顶月色。形容早出晚归或连夜赶路。也作“披星带月”“戴月披星”。</p></blockquote><p>【例】北京建工养护集团八公路处工程部部长温冬梅介绍，洪水退后，抢修队伍披星戴月施工，人歇 设备不歇。</p><p>【例】《大漠春归》以习近平生态文明思想和习近平文化思想为指引，讲述了在党中央作出“在西北、 华北、东北地区建设防护林体系重大战略决策”背景下，时任和林格尔县县长的云福祥带领当地群众披星 戴月、战天斗地，将昔日黄沙漫漫、寸草不生的 12 万亩沙地变成绿洲的故事。</p><p>【例】和大多数新警察一样，张洋川最初的梦想是当一名刑警，“为破案绞尽脑汁，为抓捕披星戴月”。 在巡警（防暴）大队 5 年后，张洋川如愿以偿去了光荣派出所任刑警。当时的张洋川只有 20 多岁，血气 方刚。</p><blockquote><p>摩顶放踵</p><p>意思是从头顶到脚跟都擦伤了；形容不辞劳苦，不顾身体。</p></blockquote><p>【例】如今，这位 94 岁的老人仍然坚持给每年新入校的博士生上课，并亲自带博士生。从 1984 年招 收第一位博士生，至今 40 年，摩顶放踵培养人才，从未间断。</p><p>【例】判若霄壤的历史嬗变背后,是一代代国人摩顶放踵、胼手胝足铸就的两个大字:奋斗!</p><p>【例】墨家代表人物墨翟，他主张“兼爱非攻”，认为攻掠他国的战争是最大的不义行为，应该挺身 而出坚决抵制。为此他不惜摩顶放踵，长途跋涉“行十日十夜而至于郢”，完成“止楚攻宋”伟大壮举。 他既深入浅出讲“非攻”的道理，又付诸行动伸张正义，即使牺牲性命也在所不惜。</p><blockquote><p>胼手胝足</p><p>意思是手脚生茧。形容劳动十分辛勤。</p></blockquote><p>【例】哺育万物的土地，胼手胝足的农民，在习近平总书记心中有着沉甸甸的分量：“我们党成立以 后就一直把依靠农民、为亿万农民谋幸福作为重要使命。”</p><p>【例】放眼齐鲁大地，正是无数党员干部、经营主体、基层群众扎根于各个领域，奋斗在各条战线， 胼手胝足、砥砺奋进，靠勤劳智慧的双手、稳扎稳打的精神、拼搏进取的干劲，才打开了山东发展的崭新 面貌。</p><p>【例】哥德巴赫猜想具有极强的逻辑性和极为缜密的推算过程，无法用电子计算机（当时陈景润也没 有此种设备），陈景润仅靠一双手，一支笔，胼手胝足，终成大业，何其不易。</p><table><thead><tr><th>勤勉不懈怠</th><th>孜孜不倦、孜孜以求</th></tr></thead></table><blockquote><p>孜孜不倦</p><p>孜孜：勤奋的样子。形容勤奋、不知疲倦。</p></blockquote><p>【例】希望两位院士孜孜不倦投身科研，准确把握攻关方向，深入谋划科研项目，广泛培养专业人才，推动科研创新水平不断提升。</p><p>【例】通过建立青少年江南丝竹团队，挖掘和整理新泾地区江南丝竹曲目，开展江南丝竹曲目配器，一 系列孜孜不倦的探索为新泾镇的江南丝竹注入新的活力和生机，让江南丝竹在新泾镇重新绽放出夺目光彩。</p><p>【例】经过 1 年 9 个月孜孜不倦地努力，国内首个全铝合金副车架在齐嵩宇手中诞生。如今，一汽自 主品牌全系列车型都安装着这种车架。“较之钢质副车架，全铝合金副车架减重, 40%，整车减重 16 公斤。”</p><blockquote><p>孜孜以求</p><p>孜孜：勤勉的样子。意思是不知疲倦地探求。</p></blockquote><p>【例】建设现代化强国、实现中华民族伟大复兴，是中国共产党人孜孜以求的宏伟目标，凝结着毛泽 东同志等老一辈革命家的毕生心血。</p><p>【例】走在前，是习近平总书记对山东一以贯之的殷切期望，是亿万齐鲁儿女孜孜以求的奋斗目标。 党的十八大以来，总书记多次亲临山东视察，作出重要指示批示，为山东发展精准把脉、定向引航。</p><p>【例】为民初心、情暖万家，共创美好人民对美好生活的向往，是成都银行孜孜以求的努力方向。2023 年成都银行通过数字金融赋能，强化适老化服务改造，提升客户体验，进一步巩固了其“市民银行”的地 位。</p><hr><h3 id="【第十九组】-正确做法-13-个" tabindex="-1"><a class="header-anchor" href="#【第十九组】-正确做法-13-个"><span>【第十九组】 正确做法（13 个）</span></a></h3><table><thead><tr><th>分析问题</th><th>条分缕析、抽丝剥茧、深入浅出</th></tr></thead><tbody><tr><td>抓住问题关键</td><td>纲举目张、提纲挈领、以一持万</td></tr><tr><td>向他人学习</td><td>博采众长、见贤思齐</td></tr><tr><td>踏实的做法</td><td>量力而行、厚积薄发</td></tr><tr><td>勇于承担责任义务</td><td>当仁不让、义不容辞、责无旁贷</td></tr></tbody></table><ol><li>成语解释</li></ol><table><thead><tr><th>分析问题</th><th>条分缕析、抽丝剥茧、深入浅出</th></tr></thead></table><blockquote><p>条分缕析</p><p>缕：线。一条条分析。形容分析得有条理，很细致。</p></blockquote><p>【例】越是面对复杂多变的社会环境，越应该注重身教重于言传、具体强于抽象，教育引导、舆论宣 传、文化熏陶、实践养成、制度保障都应该条分缕析，在现实针对性上做足功夫。</p><p>【例】为厘清史实，年逾古稀的作者历时 5 年，搜集了 2000 多万字的资料，仔细爬梳整理，并且努 力在写作中做到条分缕析、详略得当。</p><p>【例】作者对新时代治国理政丰富宝藏深稽博考、取精用弘，既有宏观勾勒架构，也有细节深入浅出 分析，学理上条分缕析、抽丝剥茧，深入解答新时代十年伟大变革的成功奥秘。</p><blockquote><p>抽丝剥茧</p><p>意思是丝得一根一根地抽，茧得一层一层地剥；形容分析事物极为细致，而且一步一步很有层次。</p></blockquote><p>【例】从事纪检监察工作 10 多年来，罗功新一直在反腐败斗争第一线，面对重大疑难案件，他总能 从纷乱如麻的线索中抽丝剥茧，找到案件的突破口。</p><p>【例】条分缕析，寻找蛛丝马迹；抽丝剥茧，查找火灾根源。</p><p>【例】抽丝剥茧理清事实，精准适用法律，这是黄登魁办理行政诉讼案件的缩影。</p><blockquote><p>深入浅出</p><p>指讲话或文章的内容深刻，语言文字却浅显易懂。</p></blockquote><p>【例】这篇文章立论新颖，行文却深入浅出，明白易懂。</p><p>【例】作者把一些严肃重大的话题用漫谈的方式铺展开来，深入浅出，把深邃的思想、深刻的道理讲 述得通俗易懂、娓娓动听。</p><p>【例】只有坚持理论为基、内容为王，以理服人、以情动人，以更深入的理论阐释、更前沿的知识传 授、更深层的价值引导，把深刻的思想讲得深入浅出，把宏大的理论讲得有滋有味。</p><table><thead><tr><th>抓住问题关键</th><th>纲举目张、提纲挈领、以一持万</th></tr></thead></table><blockquote><p>纲举目张</p><p>指提起大网的总绳一撒，所有的网眼就都张开。比喻抓住事物的主要环节，就可带动一切。</p></blockquote><p>【例】在中央经济工作会议上，习近平总书记强调：“明年经济工作千头万绪，要从战略全局出发， 抓主要矛盾，从改善社会心理预期、提振发展信心入手，抓住重大关键环节，纲举目张做好工作。”</p><p>【例】推进经济体制改革要从现实需要出发，从最紧迫的事情抓起，在解决实践问题中深化理论创新、 推进制度创新。其他领域改革也要聚焦全局性、战略性问题谋划改革举措，实现纲举目张。</p><p>【例】政治纪律作为管方向、管立场、管根本的要求，对党组织和党员起着根本引领作用，对其他纪 律起着统领提携、纲举目张的作用，决定着党的纪律的效力和效能。</p><blockquote><p>提纲挈领</p><p>意思是抓住事物的关键部分或者核心要点，然后用简洁明了的方式进行概述或表达。</p></blockquote><p>【例】面对错综复杂的矛盾问题，要善于抓住主要矛盾，做到提纲挈领，才能精准聚焦、迎刃而解。</p><p>【例】鲁迅从理论的高度和艺术史的发展链条上，最早对中国的版画给出了提纲挈领的意见。</p><p>【例】一般而言，鸿篇巨制如《光荣与梦想》，会有一个“提纲挈领”的宏大开端。</p><blockquote><p>以一持万</p><p>用来形容抓住了关键的环节或因素，从而能够控制和影响整个局面。它强调的是在处理问题时识别并 把握住核心部分的重要性，以此来驾驭复杂的情况或者广泛的领域。</p></blockquote><p>【例】改革，如秉轴持钧，以一持万。深入基层沉到一线，以实际调研筑牢工作基础，将成为各地公 安机关下一步推动人员职能整合、业务工作融合、机制流程衔接的发力点。</p><p>【例】荀子培养人的理想目标是“圣人”“大儒”，“大儒”不仅知识渊博，而且能“以浅持博，以 古持今，以一持万”，是能实现“法后王，统礼义，一制度”的“圣人”。</p><p>【例】千头万绪，以一持万。</p><table><thead><tr><th>向他人学习</th><th>博采众长、见贤思齐</th></tr></thead></table><blockquote><p>博采众长</p><p>博采：广泛搜集采纳。从多方面吸取各家的长处。</p></blockquote><p>【例】搞科学研究，也需博采众长。</p><p>【例】在兼容并蓄、博采众长的过程中，还要把握好借鉴与自立、开放与自主的辩证关系，实现“不 忘本来”与“吸收外来”相结合。</p><p>【例】各县（市、区）要认真总结创城经验，博采众长、补己之短，实现创建文明城市工作实现大突 破，见到新气象。</p><blockquote><p>见贤思齐</p><p>指见到有才德的人就想着与他齐平</p></blockquote><p>【例】社会主义核心价值观“十进”活动和“文明城市复审”，催生出 25890 户“文明家庭”、43562 户“星级文明户”，涌现了石丽平、刘仁平、姚少军、杨正文、李春学等 23 名身边好人，使人人见贤思 齐、户户崇德向善。</p><p>【例】以榜样的作用和力量，让向善向美向上的道德风尚潜移默化，让见贤思齐成为一种社会常态和 精神自觉，才能让良好的政治生态成为社会进步的不竭之源。</p><p>【例】大家一致认为，党员干部要不忘初心、牢记使命，见贤思齐、当好先锋，为全面建设社会主义 现代化河南、谱写新时代中原更加出彩的绚丽篇章不懈奋斗。</p><table><thead><tr><th>踏实的做法</th><th>量力而行、厚积薄发</th></tr></thead></table><blockquote><p>量力而行</p><p>意思是指根据自身的实际能力和条件来决定行动的范围，做事不要超过自己的能力范围，以免出现意 外问题。</p></blockquote><p>【例】尽力而为也要量力而行，这就要求我们在推进共同富裕过程中必须具备充分考虑发展实际的科 学精神。</p><p>【例】各地确定年度改造计划应从当地实际出发，尽力而为、量力而行，不层层下指标，不搞“一刀 切”。</p><p>【例】面向未来，始终坚持以人民为中心的发展思想，既尽力而为、积极回应群众所需，又量力而行、 充分尊重客观规律，不懈奋斗、永远奋斗，我们就一定能逐步实现全体人民共同富裕，创造新的更大奇迹。</p><blockquote><p>厚积薄发</p><p>厚积：充分积累。薄发：少量地慢慢地释放。形容积累丰富的学问而不轻易表现出来。</p></blockquote><p>【例】从百年奋斗史中总结成功经验，坚定信念，确保在未来的日子里厚积薄发，永葆党的生机活力、 团结带领全国各族人民实现中华民族伟大复兴的中国梦。</p><p>【例】前 4 个年头毛竹并不急着向上生长，而是拼命地向下扎根，方能为后面的快速生长提供充足养 分，正可谓“厚积薄发”。</p><p>【例】通过“巧方法”形成鼓励、支持创新的良好氛围，集聚各方面的创新资源，才能厚积薄发、掌 握主动。</p><table><thead><tr><th>勇于承担责任义务</th><th>当仁不让、义不容辞、责无旁贷</th></tr></thead></table><blockquote><p>当仁不让</p><p>当：面对。仁：符合道义的事。后指遇到应该做的事，勇于承当，不推诿。（侧重情理上）</p></blockquote><p>【例】为战而生、向战而行，汉中舰赓续着“英雄快艇”的红色血脉，急难险重任务面前打头阵、当 先锋，以当仁不让、舍我其谁的担当，在转型备战中勇立时代潮头。</p><p>【例】陶瓷文化的传承和创新，今天的陶瓷人重担在肩、当仁不让，希望有更多朋友关注并参与进来。</p><p>【例】2020 年新冠疫情暴发，作为一名医学专业留学生，他亲眼目睹江苏大学医学专业学生，自己平 日里朝夕相处的同窗们，当仁不让，星夜驰援奔赴多地开展核酸检测。</p><blockquote><p>义不容辞</p><p>义：道义。辞：推辞。道义上不容许推辞。指理应接受。（侧重良心、道义上）</p></blockquote><p>【例】钟南山认为自己只是一个医生，须对人民的生命安全负责，他的出声是义不容辞的。</p><p>【例】呼伦贝尔作为国家重要农畜产品生产基地，提升农牧业综合生产能力，保障国家粮食安全是我 们义不容辞的责任。</p><p>【例】提升中国的国际话语权，当代中国青年义不容辞。</p><blockquote><p>责无旁贷</p><p>责：责任。贷：推卸。指自己应尽的责任，不能推给别人。（侧重责任、职责上）</p></blockquote><p>【例】作为邮件运送的起点，企业在保证邮件安全方面，可谓责无旁贷。</p><p>【例】优化消费环境对于激发消费活力、释放消费潜能至关重要，企业在切实保护消费者权益、更好 满足人民群众美好生活需要方面责无旁贷。</p><p>【例】这要求我们要始终高度重视节约粮食，节约粮食每一个人都责无旁贷，必须从餐桌抓起，从每 一份外卖抓起，让节约粮食在全社会蔚然成风。</p><hr><h3 id="【第二十组】-错误做法-10-个" tabindex="-1"><a class="header-anchor" href="#【第二十组】-错误做法-10-个"><span>【第二十组】 错误做法（10 个）</span></a></h3><table><thead><tr><th>不能这样做</th><th style="text-align:left;">过犹不及、矫枉过正、于事无补、作茧自缚、扬汤止沸、 隔靴搔痒、临渊羡鱼、有恃无恐、堂而皇之、明目张胆</th></tr></thead></table><ol><li>成语解释</li></ol><blockquote><p>过犹不及</p><p>过：过分。不及：不够。事情做得过了头，就跟做得不够一样，都不合适。</p></blockquote><p>【例】偶尔、适量的召回，可被看作车企责任感的体现。然而，过犹不及，如若因为有召回制度的存 在，便放松对于质量的要求，将召回当饭吃、当枪使，则绝非一个负责任的车企该有的态度。</p><p>【例】凡事过犹不及，宣传“用力过猛”，不仅容易让受众审美疲劳、产生抵触情绪，也会过于拉高 游客的期待值，产生“货不对板”的印象。</p><p>【例】要想使工作中多些“精准滴灌”，少些“大水漫灌”；多些“恰如其分”，少些“过犹不及”， 就必须在精准把脉问题症结上下功夫，把情况摸清、把问题找准、把对策提实。</p><blockquote><p>矫枉过正</p><p>矫枉：矫正弯曲。指纠正偏失错误，超过了应有的限度。</p></blockquote><p>【例】对于经济连续下行的走势，不必惊恐悲观，宏观政策既要加强预调微调，也要防止用力过猛， 矫枉过正。</p><p>【例】跨周期调节是指在较长的期限内，通过宏观调控减少经济顺周期风险累积，防止“政府之手” 矫枉过正，为暂时利益而牺牲长远利益。</p><p>【例】家长切不可粗暴地制止孩子，以免矫枉过正，打击孩子发展表达能力的积极性。</p><blockquote><p>于事无补</p><p>指某种言行对于解决问题没有帮助。</p></blockquote><p>【例】一些企业经常提及追赶“新赛道”，什么火就转做什么，认为只有追赶新风口，业绩才能起飞； 如果赛道老旧，那么企业再努力也于事无补。</p><p>【例】倘若不能对问题一个一个彻底整改、一条一条逐个销号，即便列出再长、再详细的问题清单， 也无异于只“诊脉”不“开方”、只“填病历”不“除病灶”，本末倒置，于事无补。</p><p>【例】直至今年初，岛内陷入“史上最长蛋荒”，民进党当局才突然宣布“专案进口鸡蛋”，然而此 举非但于事无补，反倒惹出更多祸端。</p><blockquote><p>作茧自缚</p><p>蚕吐丝作茧，把自己包在里面。比喻自己束缚了自己或使自己陷入困境。</p></blockquote><p>【例】全球化时代，人才的跨国流动推动了全球范围内科技和经济进步，盲目上演自我封闭、仇恨、 排外的闹剧，无异于作茧自缚。</p><p>【例】因为莫名的安全焦虑而打压别国、阻断合作、阻碍发展，动不动就“砌墙”，无法让一个国家 或地区的产业壮大变强，只会作茧自缚，自绝于全球化的进步大潮。</p><p>【例】在信息爆炸、思想多元、交融紧密的当今时代，封闭和对抗只会作茧自缚，只有胸怀世界、顺 势而为才能乘上大变革、大发展的时代潮流。</p><blockquote><p>扬汤止沸</p><p>把开水从锅中舀出来，再倒回去，以阻止锅中的水沸腾。比喻不能解决根本问题的做法。</p></blockquote><p>【例】“大病提取住房公积金”此种做法只是一种扬汤止沸的应急之策，而无益于从根本上解决问题。</p><p>【例】制度层面看似瞄准公平，然而依据既往经验，此类计划执行起来无异于扬汤止沸，难免漏洞百出。</p><p>【例】与其扬汤止沸不如釜底抽薪，必须坚决摒弃杀鸡取卵、竭泽而渔的发展方式和生活方式，把绿 色发展方式和生活方式作为根本之策，从源头上解决生态环境问题。</p><blockquote><p>隔靴搔痒</p><p>比喻说话、做事、写文章不中肯，没有抓住关键。</p></blockquote><p>【例】对此，有家长直呼大快人心，也有家长持悲观态度，指称在现行高考、中考制度没有根本性改 变的前提下，任何给学生减负的举措都只能是“隔靴搔痒”。</p><p>【例】虽然这是美国近 30 年首次就控枪立法，但外界普遍认为这是“隔靴搔痒”，无法从根本上解决美国枪支暴力问题。</p><p>【例】全党正在深入开展学习贯彻习近平新时代中国特色社会主义思想主题教育，要把开好专题民主 生活会作为主题教育检视整改工作的一个重要载体，以及确保主题教育成效的一项重要举措，切实防止形 式主义，防止蜻蜓点水、隔靴搔痒。</p><blockquote><p>临渊羡鱼</p><p>比喻有欲望想得到某种东西，但却没有实际行动。</p></blockquote><p>【例】临渊羡鱼，不如退而结网。做好招商、安商、稳商工作，打造一流的营商环境是重中之重。要 大力加强诚信建设，打造高效透明政务流程，让政策兑现有力度、服务企业有温度，增强招商引资区域竞 争力。</p><p>【例】临渊羡鱼，不如退而结网。2022 年 4 月，卷包车间提出“嵌入式”人才培育理念，将后备青年 人才分为积极分子、培养对象、预备人选三个阶段，实现“进阶”培养。</p><blockquote><p>有恃无恐</p><p>因有所依仗而毫不害怕。</p></blockquote><p>【例】这个过程，普通用户难以察觉，维权举证困难，在违法成本低的情况下，当顶格罚款远不及违 法违规的营收所得，企业自然有恃无恐。</p><p>【例】过去两年的事实足以证明，菲律宾将更加有恃无恐，继续走上危险的道路，威胁地区和平与稳 定。对日本而言，与菲律宾的防务协定是日本在亚洲签署的首个此类协定，这一事实本身就令人警觉。</p><p>【例】一些中介机构的不规范行为，给租户造成财务或者时间成本的损失，但是维权成本却不低，所 以很多人只能无奈选择忍让，这让一些中介更加有恃无恐。</p><blockquote><p>堂而皇之</p><p>形容公然而毫不掩饰（多含贬义），强调表面上的正当性或气势宏大，可能隐含虚伪或掩饰。</p></blockquote><p>【例】象棋职业化改革不能总是停留在浅水区，任由商品交换原则堂而皇之遍布全行业、破坏起码的赛事公平。越是推进职业化改革，越需要筑牢制度防火墙，绝不能任由商品交换那一套搬到赛事竞技中来。</p><p>【例】在养殖场内，犯罪团伙通过伪造猴牌、伪造繁殖档案等手段，“洗白”野生猕猴身份后，再披 着所谓合法外衣，堂而皇之地出售。问题三：监管漏洞出在哪里？跨省作案、多次作案，是上述案件中犯 罪团伙的显著特征</p><p>【例】湖南省宁远县纪委监委第七纪检监察室主任肖云辉介绍，有群众反映，有些公租房长期被一些 公职人员占用，有的长期空置，有的甚至堂而皇之发布出租信息。按照群众提供的线索，该县纪委监委工 作人员迅速开展调查核实。</p><blockquote><p>明目张胆</p><p>强调公开、毫无顾忌地做坏事，带有明显的贬义。</p></blockquote><p>【例】发言人指出，黎智英作为反中乱港事件主要策划者和煽动者，甘当美“以港遏华”马前卒，明</p><p>【例】这场力度空前的整治最初来源于群众举报，反映在某项目招投标过程中，几名评审专家收受了 中标企业好处，甚至在评标现场明目张胆地违规沟通。赤峰市纪委监委调取当天评标现场录像，发现果然 存在举报反映的情况。</p><p>【例】针对美国商务部以“侵犯人权”为由将两家中国高科技企业列入“实体清单”，外交部发言人 毛宁 11 日在例行记者会上表示，所谓制裁是明目张胆打着人权的幌子打压中国高科技企业。</p><hr><h3 id="【第二十一组】某些国家的错误做法-16个" tabindex="-1"><a class="header-anchor" href="#【第二十一组】某些国家的错误做法-16个"><span>【第二十一组】某些国家的错误做法（16个）</span></a></h3><table><thead><tr><th>不能这样做</th><th>画地为牢、以邻为壑、穿凿附会、亦步亦趋、唯利是图</th></tr></thead><tbody><tr><td>旁观、得利</td><td>隔岸观火、置身事外、袖手旁观、作壁上观、坐收渔利、 坐享其成</td></tr><tr><td>过度开发</td><td>竭泽而渔、杀鸡取卵、寅吃卯粮、饮鸩止渴、急功近利</td></tr></tbody></table><ol><li>成语解释</li></ol><table><thead><tr><th>不能这样做</th><th>画地为牢、以邻为壑、穿凿附会、亦步亦趋、唯利是图</th></tr></thead></table><blockquote><p>画地为牢</p><p>牢：牢狱。在地上画个圈儿，作为牢狱。比喻局限在小圈子里活动。</p></blockquote><p>【例】当前互联网巨头发展面临的集体问题：在疯狂追求“平台效应”和用户数量的盲目扩张中，为 维护自身市场优势地位，正纷纷“画地为牢”，平台内自我导流，对竞争对手选择性屏蔽。这种行为正在 动摇互联互通这一互联网的根本基石。</p><p>【例】任何国家画地为牢、搞单边主义和保护主义，或者通过打压遏制别国现代化来维护自身发展“特 权”，都将损害世界现代化进程。</p><p>【例】习近平总书记强调：“在实践中，我们要注意防范一些认识误区”，其中之一是“各自为政、 画地为牢，不关心建设全国统一的大市场、畅通全国大循环，只考虑建设本地区本区域小市场、搞自己的 小循环”。</p><blockquote><p>以邻为壑</p><p>壑：深沟。意为把邻国当成排洪水的沟壑。后用来比喻把困难、灾祸推给别人。（多宏观语境，国家、 城市、地区等）</p></blockquote><p>【例】全球化时代，以邻为壑的利己主义做法换不来独善其身，惟有秉持科学、理性和负责任的态度， 加强国际合作，携手应对挑战。</p><p>【例】但要看到，中国的产业政策并不违背市场规律，没有通过产业政策“以邻为壑”，排除别国竞 争，也没有“拔苗助长”，盲目发展产业。</p><p>【例】事实证明，以邻为壑的“逆全球化”做法不仅解决不了自身的沉疴痼疾，而且扰乱全球产业链 供应链，阻碍世界经济健康发展，导致各国利益受到损害。</p><blockquote><p>穿凿附会</p><p>意思是把讲不通的或不相干的道理、事情硬扯在一起进行解释。</p></blockquote><p>【例】事实上，改革开放以来中国人道德出现滑坡的说法既不符合事实，也不符合辩证法，是以偏概 全、穿凿附会的谬见。客观上说，互助友爱的风气是这个时代的主流，也是人们美好生活的重色。</p><p>【例】然而，一些中医研究者过度沉浸于为中医科学性正名的焦虑，动辄移花接木、穿凿附会，用不 相干的科学理论来解释中医，实则违背了科学精神和科学研究的规律。</p><p>【例】为打压其他地方的发展权利和安全利益而堆砌故事穿凿附会，是明显为服务美国自身政治利益 而创作的制成品。</p><blockquote><p>亦步亦趋</p><p>步：走。趋：快走。比喻因缺乏主见，任何事都模仿、追随他人。（侧重行动）</p></blockquote><p>【例】没有一个民族和国家可以通过依赖外部力量、跟在他人后面亦步亦趋实现强大和振兴。</p><p>【例】而那些跟在发达国家后面亦步亦趋、简单搞制度移植的国家，大都陷入发展困境。</p><p>【例】中国学术必须立足中国实际才有生命力，跟在别人后面亦步亦趋，不仅难以形成中国特色哲学 社会科学，而且解决不了中国的实际问题。</p><blockquote><p>唯利是图</p><p>唯：只有；图：图谋，追求。只要有利可图，什么事都干。</p></blockquote><p>【例】“以义取利，不唯利是图”是其中一个重要方面金融行业要树立正确的经营观，把“以义取利， 不唯利是图”的实践要求真正落到实处，坚持在有效服务经济社会发展中创造价值和利润，实现社会效益 和经济效益的统一。</p><table><thead><tr><th>旁观、得利</th><th style="text-align:left;">隔岸观火、置身事外、袖手旁观、作壁上观、坐收渔利、 坐享其成</th></tr></thead></table><blockquote><p>隔岸观火</p><p>指站在对岸观望火灾；用来比喻对别人的急难不加救助，而采取看热闹的态度。</p></blockquote><p>【例】作为二者的共同盟友，美军却选择隔岸观火，并表示由于安全局势过于恶化，停止美军在该地 区的巡逻。</p><p>【例】身处和平之际，也应学会设身处地，体谅他人战乱之苦，口下留德，对战争理性发言，切勿做 隔岸观火的低俗看客。</p><p>【例】美国挑拨俄乌之间的关系，本想只是“隔岸观火”，但其或许万万没想到还会“惹火上身”。</p><blockquote><p>置身事外</p><p>置：安放。把自己放在事情之外。形容毫不关心。</p></blockquote><p>【例】我们生活在一个紧密相连的世界中，各国之间的利益和命运紧密相连。没有哪个国家可以独善 其身，也没有哪个国家可以在国际事务中置身事外。</p><p>【例】但是，无论此次乌龙事件出于何种原因，购票平台显然都无法置身事外。倘若系统故意“跳票”， 自然不必多言。</p><p>【例】在同一片蓝天下生活，受同一方水土滋养，我们每个人都是生态环境的保护者、建设者、受益 者，没有谁是局外人，也没有人能置身事外。</p><blockquote><p>袖手旁观</p><p>袖手：把手揣在袖子里。比喻置身事外，既不过问，也不协助别人。（侧重从旁边看）</p></blockquote><p>【例】绝不在朋友落难时袖手旁观，绝不在伸出援手时夹杂私利，这就是中国之义。</p><p>【例】中方没有袖手旁观，没有拱火浇油，更没有从中渔利。我们所做的一切，都通往一个目标，就 是为止战凝聚共识，为和谈铺路搭桥。</p><p>【例】装修公司也要明白，用低价吸引眼球，再挖空心思加价，这种“把戏”并不高明，消费者不可 能被牵着鼻子走，管理部门更不会袖手旁观。</p><blockquote><p>作壁上观</p><p>原指双方交战，自己站在壁垒上旁观。后多比喻站在一旁看着，不动手帮助。</p></blockquote><p>【例】维护国家安全没有“局外人”，国家安全面前你我都不能作壁上观。</p><p>【例】在这种背景中，很多地方的文旅部门没有作壁上观，更不甘于“吃瓜”，这是好事。一定程度上 说，卷也是履职，既是分内事，也回应了当地民众的关切。想方设法让当地文旅火起来，总胜过碌碌无为。</p><p>【例】由此可看，面对那些侵权网暴商品，平台不能作壁上观，更不能任其热销而乐见其成。</p><blockquote><p>坐收渔利</p><p>比喻利用别人的矛盾而从中获利。</p></blockquote><p>【例】在欧洲因能源危机承受巨大压力的同时，美国却趁火打劫、坐收渔利，以高价向欧洲国家兜售能源。</p><p>【例】一些西方政客近来在台湾问题上不断生事，企图搞“以台制华”，借制造台海紧张局势坐收渔利。</p><p>【例】再以炒作明星八卦引发网民互相攻击为例，捕风捉影，无事生非，拉踩引战，让“饭圈”之间 互相攻诘，坐收渔利，能不让人深恶痛绝？</p><blockquote><p>坐享其成</p><p>意思为自己不出力而享受别人取得的成果。</p></blockquote><p>【例】习近平总书记在许家冲村考察时强调，乡村振兴不是坐享其成，等不来、也送不来，要靠广大 农民奋斗。</p><p>【例】中国人民自古就明白，人世间从来没有坐享其成的好事，要幸福就要奋斗。</p><p>【例】反之，没有劳动情怀、缺乏劳动锻炼，必然导致不想劳动、不会劳动，滋生坐享其成、贪图享 乐等怠惰奢靡之风。</p><table><thead><tr><th>过度开发</th><th>竭泽而渔、杀鸡取卵、寅吃卯粮、饮鸩止渴、急功近利</th></tr></thead></table><blockquote><p>竭泽而渔</p><p>排尽湖中或池中的水捕鱼。比喻取利只顾眼前，不作长远打算。</p></blockquote><p>【例】短期来看，提高景区门票会带来更多的地方财政收入，但从长远来看这种“门票经济”无异于 竭泽而渔，非但无法带动当地经济发展，甚至会严重影响中国旅游业的发展。</p><p>【例】面对短视频领域乱象频发、某些人竭泽而渔、技术应用剑走偏锋，治理当“该出手时就出手”。</p><p>【例】我们根据群众意见写了人民网三评“套娃会员”，指出“套娃会员”本质上是一种变相加价， 竭泽而渔，旗帜鲜明地为消费者撑腰。</p><blockquote><p>杀鸡取卵</p><p>卵：蛋。为了要得到蛋，不惜把鸡杀了。比喻贪图眼前微小的好处而损害长远的利益。</p></blockquote><p>【例】一面是整个社会的得不偿失，一面却是杀鸡取卵者赚得盆满钵溢，这样的怪象原因何在？</p><p>【例】可见，商家为了追逐利益明知故犯的行为无异于“杀鸡取卵”，最终只会自食其果。</p><p>【例】短期来看，利润得到了增长，长远来看，却是杀鸡取卵，透支用户信任。</p><blockquote><p>寅吃卯粮</p><p>寅、卯：古代用于纪年等的十二地支中的两个，寅在卯之前。寅年吃了卯年的粮食。比喻经济困难， 入不敷出。同“寅支卯粮”。（侧重由于有困难而提前预支）</p></blockquote><p>【例】政府财政长期入不敷出局面是美国近半个世纪以来寅吃卯粮、开支无度恶习导致的结果，也是 美国联邦政府债务不断积累膨胀的重要原因。。</p><p>【例】对各级地方政府来说，有多大财力办多大的事，决不能寅吃卯粮、透支未来，决不允许新增隐 性债务上新项目、铺新摊子。</p><p>【例】保护环境就是保护生产力，改善环境就是发展生产力，强调“保护生态环境就是保护自然价值 和增值自然资本，就是保护经济社会发展潜力和后劲”在生态环境保护上，一定要树立大局观、长远观、 整体观，不能因小失大、顾此失彼、寅吃卯粮。</p><blockquote><p>饮鸩止渴</p><p>鸩：毒酒。比喻用有害的方法解决面临的困难，而不顾后果。</p></blockquote><p>【例】当再一次为了稳增长重复扩张性政策的时候，我们不得不重申历史的教训：扩张性政策只能是 饮鸩止渴。</p><p>【例】有必要认识到，平台是流量的入口，蹭流量的行为看似可以造就短期“繁荣”，实则饮鸩止渴， 加剧的是质疑与焦虑，伤害的是信任与黏性。</p><p>【例】依赖美国和北约提供安全保护只能是饮鸩止渴，没有战略自主，欧洲只会是美国的“棋子”， 而非平等的伙伴。</p><blockquote><p>急功近利</p><p>功：成功；近：眼前的。急于求成，贪图眼前的成效和利益。</p></blockquote><p>【例】习近平总书记着眼于推动金融高质量发展、建设金融强国，深刻阐明中国特色金融文化五个方 面的实践要求，强调要做到“稳健审慎，不急功近利”。</p><p>【例】要支持青年学者从事兴趣导向的基础研究，鼓励青年学者做有兴趣的研究，挑战科学难题，不要急功近利。</p><p>【例】有的干部抓工作平时不上心、不用力，临近考察时拿出百般气力，忙忙碌碌赶场子、装样子， 急功近利招项目、造数据，表演作秀、劳民伤财；有的干部坚守初心、始终如一，兢兢业业、任劳任怨， 一步一个脚印谋发展、求突破，几十年如一日奋战在工作一线。</p><hr><h3 id="【第二十二组】-国家安定与动荡-7-个" tabindex="-1"><a class="header-anchor" href="#【第二十二组】-国家安定与动荡-7-个"><span>【第二十二组】 国家安定与动荡（7 个）</span></a></h3><table><thead><tr><th>生活不如意</th><th>颠沛流离、流离失所、背井离乡</th></tr></thead><tbody><tr><td>社会安定</td><td>安居乐业、海晏河清、国泰民安</td></tr><tr><td>国人习惯</td><td>安土重迁</td></tr></tbody></table><ol><li>成语解释</li></ol><table><thead><tr><th>生活不如意</th><th>颠沛流离、流离失所、背井离乡</th></tr></thead></table><blockquote><p>颠沛流离</p><p>颠沛：跌倒，比喻困顿，受挫折。流离：流转离散。形容生活困苦，流落异乡，无安身之所。（可指 人，也可指物）</p></blockquote><p>【例】《伯远帖》《中秋帖》《五牛图》《韩熙载夜宴图》等国宝级艺术品，都曾经颠沛流离，差一 点流失海外，直到新中国成立后才顺利回归了祖国的怀抱。</p><p>【例】在那段时间，整座城市被淹，学校停发奖学金。之后近一年，他都过着颠沛流离的生活，科研 时断时续。</p><p>【例】颠沛流离的生活，穷困潦倒的家庭状况，孩子无钱看病夭折的打击，身体饱受疾病的折磨，都 没有摧垮马克思的意志，他始终不忘初衷，以顽强的意志走向自己的目标。</p><blockquote><p>流离失所</p><p>失所：失去安身之处。形容到处流浪，没有安身的地方。（多指因灾荒、战乱等导致的失去居所）</p></blockquote><p>【例】强暴雨引发的洪水把也门部分地区房屋夷为平地，近 2 万户家庭流离失所，国际社会应行动起 来进行紧急援助。</p><p>【例】现如今我们身处和平年代，没有饥寒交迫，没有流离失所，身处和平年代的我们是幸福的，同 样我们也是惶恐的，如何守护住这份幸福往往更加难。</p><p>【例】2001 年以来，美国在阿富汗、伊拉克、利比亚、叙利亚发动的战争和军事行动造成数十万人死 亡，数百万人受伤，数千万人流离失所。</p><blockquote><p>背井离乡</p><p>背：离开。井：古制八家为井，这里指家宅。指被迫离开家乡，到外地求生。（也指主动离开家乡， 到外地生活/工作）</p></blockquote><p>【例】如今，不少老人为了帮忙碌的儿女、照顾年幼的孙辈，背井离乡来到子女工作的大城市。</p><p>【例】随着世界人口数量急剧上升、全球气候变化以及各国发展的需求日益增加，造成全球水资源供 应日趋紧张，到 2030 年，预计将有 2400 万到 7 亿人会因为缺水而背井离乡。</p><p>【例】新市民背井离乡来到大城市打拼，创业就业的保障极为重要。</p><table><thead><tr><th>社会安定</th><th>安居乐业、海晏河清、国泰民安</th></tr></thead></table><blockquote><p>安居乐业</p><p>安：安定；乐：喜爱，愉快；业：职业。指安定愉快地生活和劳动。</p></blockquote><p>【例】看到来自五湖四海的建设者在这里安居乐业，感到很高兴。城市不仅要有高度，更要有温度。 我们的社会主义就是要走共同富裕的路子。外来务工人员来上海作贡献，同样是城市的主人。</p><p>【例】各级党委和政府、各有关方面要认真贯彻落实党中央决策部署，再接再厉抓好灾后恢复重建， 确保广大人民群众安居乐业、温暖过冬。</p><p>【例】确保安全生产、维护社会安定、保障人民群众安居乐业是各级党委和政府必须承担好的重要责 任。</p><blockquote><p>海晏河清</p><p>晏：平静。黄河水清了，大海没有浪了。比喻天下太平。</p></blockquote><p>【例】我们必须清醒认识到，腐败和反腐败较量还在激烈进行，并呈现出一些新的阶段性特征，防范 形形色色的利益集团成伙作势、“围猎”腐蚀还任重道远，有效应对腐败手段隐形变异、翻新升级还任重 道远，彻底铲除腐败滋生土壤、实现海晏河清还任重道远，清理系统性腐败、化解风险隐患还任重道远。</p><p>【例】强化不敢腐的震慑，扎牢不能腐的笼子，增强不想腐的自觉，通过不懈努力换来海晏河清、朗朗乾坤。</p><p>【例】前辈奋斗不止，为我们创造了海晏河清、国富民强的中国，历史篇章仍在进行，未来的华彩应 交由吾辈书绘。</p><blockquote><p>国泰民安</p><p>泰：太平。国家太平，人民生活安定。形容社会安定，人民生活幸福。</p></blockquote><p>【例】剪纸、年画及炕围画等传统美术类非遗象征着幸福、吉祥和长寿，表达了民众祈盼万事如意、 国泰民安、民族团结等美好愿望，展现了人民群众对幸福美满生活的向往与追求。</p><p>【例】此次庆典上，资深寨佬领头进行庄重神圣的祈福，为侗寨祈求平安、丰收和国泰民安。庆典现 场，芦笙音乐悠扬响起，伴随着欢快的踩堂舞，表达了人们对丰收的喜悦和对自然恩赐的感激之情。</p><p>【例】要想国泰民安、岁稔年丰，必须善于治水。“水利兴而后天下可平，外患可息，而教化可兴矣。” 清初地理学家刘继庄说。</p><table><thead><tr><th>国人习惯</th><th>安土重迁</th></tr></thead></table><blockquote><p>安土重迁</p><p>土：乡土；重：看得重，不轻易。安于本乡本土，不愿轻易迁移。</p></blockquote><p>【例】年轻一代在外成家立业、辛勤打拼，父辈祖辈安土重迁、难离故土，老年人没有和子女共同居 住生活，是时下许多家庭的现实情况。</p><p>【例】土地供给人的生产的有限性，使得中国人很早就懂得知足、克己。土地本身的固定性，又决定 了中国人的安土重迁，也形成了中国社会的“熟人社会”特质。</p><p>【例】在当时社会条件下，针对农业社会安土重迁、家族纽带强等特点，国家与社会各司其职，有利 于合理有效配置资源，提升社会治理效率。</p><hr><h3 id="【第二十三组】-国际国内关系-15-个" tabindex="-1"><a class="header-anchor" href="#【第二十三组】-国际国内关系-15-个"><span>【第二十三组】 国际国内关系（15 个）</span></a></h3><table><thead><tr><th>同呼吸共命运</th><th>休戚与共</th></tr></thead><tbody><tr><td>融洽和谐</td><td>同舟共济、同心同德、和衷共济、水乳交融、相濡以沫</td></tr><tr><td>和邻居的关系</td><td>守望相助、一衣带水、唇齿相依、以邻为壑</td></tr><tr><td>世界局势</td><td>波诡云谲、风云变幻、纵横捭阖</td></tr><tr><td>坏人坏事</td><td>沆瀣一气、同流合污</td></tr></tbody></table><ol><li>成语解释</li></ol><table><thead><tr><th>同呼吸共命运</th><th>休戚与共</th></tr></thead></table><blockquote><p>休戚与共</p><p>休：欢乐，吉庆。戚：忧愁，悲哀。指忧喜祸福彼此共同承担。形容关系密切同甘共苦。</p></blockquote><p>【例】新形势下，着眼两国人民的光明未来，中方愿同塔方一道努力，全面提升各领域合作水平，推 动构建世代友好、休戚与共、互利共赢的中塔命运共同体。</p><p>【例】世界各国从未像今天这样互通互联、彼此依存、休戚与共。要跟上时代发展潮流，就要摒弃冷 战思维、零和博弈的旧思维、老框框，开创国际交往新格局。</p><p>【例】习近平总书记强调：“向上向善的文化是一个国家、一个民族休戚与共、血脉相连的重要纽带。”</p><table><thead><tr><th>融洽和谐</th><th>同舟共济、同心同德、和衷共济、水乳交融、相濡以沫</th></tr></thead></table><blockquote><p>同舟共济</p><p>济：渡过。同乘一条船过河。比喻齐心协力渡过困难。</p></blockquote><p>【例】抓紧抓实抓细各项防控工作，需要进一步凝心聚力，激发同舟共济、共克时艰的磅礴力量。</p><p>【例】新征程上，14 亿多中国人心往一处想、劲往一处使，同舟共济、众志成城，就没有干不成的事、 迈不过的坎。</p><p>【例】抗御突如其来的灾难，我们又一次看到了一方有难、八方支援的感人场景，又一次看到了同舟共济、守望相助的家国情怀。</p><blockquote><p>同心同德</p><p>思想统一，信念一致。形容大家团结一条心。</p></blockquote><p>【例】坚持党的全面领导，同心同德共同奋斗，我们坚信，任何困难都阻挡不了中国人民实现梦想的步伐。</p><p>【例】要坚守民族大义，站在历史正确的一边，同心同德为实现民族复兴而共同奋斗。</p><p>【例】我们要把对党负责和对人民负责高度统一起来，坚持以党的旗帜为旗帜、以党的方向为方向、 以党的意志为意志，始终做到在党言党、在党忧党、在党为党，任何时候都与党同心同德。</p><blockquote><p>和衷共济</p><p>衷：内心。和衷：指同心。大家同心渡过江河。比喻同心协力，渡过难关。现也可比喻齐心协力。</p></blockquote><p>【例】在灾难面前，总有一种力量让人感动。超强台风“威马逊”肆虐海南之时，琼岛儿女在风雨中 互帮互助，社会各界和衷共济，谱写了一曲感人肺腑的守望相助之歌。</p><p>【例】包括两岸同胞在内的所有中华儿女，要和衷共济、团结向前。</p><p>【例】推进中国式现代化是一项前无古人的开创性事业，需要全体中华儿女和衷共济、共襄盛举。</p><blockquote><p>水乳交融</p><p>水和乳汁融合在一起。比喻意气相投、感情融洽。（侧重融合）</p></blockquote><p>【例】革命胜利来之不易，主要是党和人民水乳交融，党把人民利益放在第一位。</p><p>【例】在这一新的文化生命体中，马克思主义和中华优秀传统文化是水乳交融的，我们既可以从中感 悟到马克思主义的真理力量，也可以从中体悟到中华优秀传统文化的精髓要义。</p><p>【例】此次杭州亚运会为我们生动地展现了体育与文化的水乳交融，不仅呈现了一场奋勇拼搏、万众 欢腾的体育盛宴，更提供了一扇全面展示中国、沟通连接世界的文化交流之窗。</p><blockquote><p>相濡以沫</p><p>濡：沾湿，浸润。沫：唾沫。意为泉水干涸，鱼用唾沫相互湿润。后比喻在困境中用微薄的力量相互帮助。</p></blockquote><p>【例】结婚二十载，他们相互扶持，相濡以沫，为自己打造了幸福的四口之家。</p><p>【例】战士们表情严峻，但老人的话却让我鼻子发酸，眼泪差点儿掉出来。在令人绝望的灾难面前， 相濡以沫的感情是那么温暖，那么浓烈。</p><p>【例】如今，顾诵芬院士热情丝毫未减，他说“了解航空的进展，就是我的晚年之乐”；携手走过半 个多世纪，顾诵芬院士和妻子江泽菲始终相互扶持、相濡以沫。</p><table><thead><tr><th>和邻居的关系</th><th>守望相助、一衣带水、唇齿相依、以邻为壑</th></tr></thead></table><blockquote><p>守望相助</p><p>守：防守。望：瞭望。指彼此关照，互相帮助。</p></blockquote><p>【例】亿万人民同舟共济、守望相助，筑起一道抗击疫情的钢铁长城。</p><p>【例】《风雨落坡岭》用电影手法记录了 K396 次列车被困滞留 105 个小时救援全程，坚守，逆行， 挺身而出，守望相助，风雨落坡岭，情暖落坡岭，真实富有感染力促使作品在短视频平台实现了长视频的 破圈传播。</p><p>【例】2023 中国正能量网络精品征集展播活动通过全媒体传播手段，大力弘扬昂扬向上、仁爱勇敢、 守望相助的良好风尚，讴歌气象万千的魅力中国，用丰富多彩的精品力作，有力激励人、鼓舞人、引领人。</p><blockquote><p>一衣带水</p><p>原形容像一条衣带那样狭窄的河流。现比喻仅隔一水，极其邻近。</p></blockquote><p>【例】在大灾大难面前，两国能够携手并进，同舟共济，这正是一衣带水的邻邦情谊的真实体现，也 是未来中日关系持续改善发展的重要基础。</p><p>【例】一衣带水同心守“沪”，武汉爱心企业捐赠物资驰援上海。</p><p>【例】东西协作对口帮扶甘肃的天津市、青岛市、厦门市、福州市，分别在会上“秀”东部援甘“一 衣带水”的两地情缘，并表示下一步的结对帮扶还将升级加力。</p><blockquote><p>唇齿相依</p><p>嘴唇与牙齿互相依存。比喻关系极为密切。</p></blockquote><p>【例】经济建设与国防建设是唇齿相依的两个方面。</p><p>【例】粤港澳三地唇齿相依、血脉相连，历史底蕴深厚、中西文化荟萃，是我国最具发展活力的地区 之一，也是独具文化特色的地区。</p><p>【例】中国与中亚国家是唇齿相依、安危与共的命运共同体，在安全领域有着广泛的共同诉求和利益 汇合点。建交以来，中国与中亚五国的安全合作成果斐然，成为彼此高水平互信的集中体现。</p><blockquote><p>以邻为壑</p><p>壑：深沟。意为把邻国当成排洪水的沟壑。后用来比喻把困难、灾祸推给别人。（多宏观语境，国家、 城市、地区等）</p></blockquote><p>【例】全球化时代，以邻为壑的利己主义做法换不来独善其身，惟有秉持科学、理性和负责任的态度， 加强国际合作，携手应对挑战。</p><p>【例】但要看到，中国的产业政策并不违背市场规律，没有通过产业政策“以邻为壑”，排除别国竞 争，也没有“拔苗助长”，盲目发展产业。</p><p>【例】事实证明，以邻为壑的“逆全球化”做法不仅解决不了自身的沉疴痼疾，而且扰乱全球产业链 供应链，阻碍世界经济健康发展，导致各国利益受到损害。</p><table><thead><tr><th>世界局势</th><th>波诡云谲、风云变幻、纵横捭阖</th></tr></thead></table><blockquote><p>波诡云谲</p><p>好像云彩和水波那样，形态不可捉摸。</p></blockquote><p>【例】双方你来我往、见招拆招：干扰与反干扰、侦察与反侦察、空袭与反空袭、机降与反机降、突 击与反突击……没有了导演部一步步给定条件，战局陡然升级，变得波诡云谲。</p><p>【例】没想到支撑宏大叙事的只是 13 个平均年龄 28 岁的“一大代表”，没想到熟悉的建党故事竟还 有波诡云谲的国际视角，没想到建党历史有那么多惊心动魄的时刻。</p><p>【例】外部市场环境波诡云谲，中国汽车的出路在哪里。</p><blockquote><p>风云变幻</p><p>风、云：风和云，比喻变幻动荡的局势。变幻：变化不定。像风和云那样变化莫测。比喻局势多变或 情况复杂。</p></blockquote><p>【例】中特关系历经半个世纪国际风云变幻的考验，为双方进一步深化各领域合作打下了坚实基础。</p><p>【例】风光秀美的广袤国家，经历过反抗斗争、民生凋敝的苦难岁月；劳苦大众遭遇过内外交困的悲 惨境遇，开展了抵御外敌、反抗压迫的英勇斗争，知识分子经历过苦闷和觉醒，勇于自我反思，极具生命 张力；中国人民在历史激荡、风云变幻的中华大地上砥砺前行。</p><p>【例】我们要从中获取智慧和启迪，树立对彼此的正确认知，把牢友好合作的大方向，推动中澳全面 战略伙伴关系在国际风云变幻中不断乘风破浪、行稳致远。</p><blockquote><p>纵横捭阖</p><p>纵横：用游说来联合。捭阖：开合。指在政治、外交上运用手段进行联合或分化。</p></blockquote><p>【例】当今世界风云激荡，中国外交纵横捭阖。</p><p>【例】从深入交流的双边会见，到纵横捭阖的多边外交，习近平主席向各方介绍党的二十大取得的重 大成果和确定的中心任务，重点结合中国式现代化的五个特征，阐述中国发展的世界意义：中国 14 亿多 人口实现现代化将给世界提供超大规模市场</p><p>【例】实控人能支配或实际支配上市公司行为，通过持股表决权优势或股权纵横捭阖。</p><table><thead><tr><th>坏人坏事</th><th>沆瀣一气、同流合污</th></tr></thead></table><blockquote><p>沆瀣一气</p><p>泛指臭味相投的人结合在一起。</p></blockquote><p>【例】无论民进党当局怎样歪曲事实、颠倒黑白，都掩盖不了与台独分裂势力沆瀣一气的真实面目。</p><p>【例】如果任凭不良商贩和失职市场管理方沆瀣一气，消费者受伤害，法律也会蒙羞，而当地的营商 环境和公共形象也会受到影响。</p><p>【例】然而，仍有一些消费者反映，个别票务平台和“黄牛”沆瀣一气，扰乱市场秩序，存在实名代 拍现象，“秒光”等成为常态。</p><blockquote><p>同流合污</p><p>原指言行与不良的习俗、世道相合。后指跟坏人一起干坏事。</p></blockquote><p>【例】各国在同美国开展安全合作的同时，应当以史为鉴、擦亮眼睛、明辨是否、吸取教训，不搞同流合污、为虎作伥那套，不走对抗较量、零和博弈老路，寻求和深化合作并进、互利共赢的最大公约数，让合作的互信之基、发展之路行稳致远。</p><p>【例】中国是绝不会与霸权主义同流合污。 【例】白鲢鱼查出禁用兽药，蜂蜜也“同流合污”，这样的结果太残酷！</p><hr><h3 id="【第二十四组】-政府优秀做法-10-个" tabindex="-1"><a class="header-anchor" href="#【第二十四组】-政府优秀做法-10-个"><span>【第二十四组】 政府优秀做法（10 个）</span></a></h3><table><thead><tr><th>有针对性</th><th>因地制宜、量体裁衣、对症下药、有的放矢</th></tr></thead><tbody><tr><td>发挥职能</td><td>因势利导、保驾护航、穿针引线</td></tr><tr><td>正确做法</td><td>广开言路、集思广益、正本清源</td></tr></tbody></table><ol><li>成语解释</li></ol><table><thead><tr><th>有针对性</th><th>因地制宜、量体裁衣、对症下药、有的放矢</th></tr></thead></table><blockquote><p>因地制宜</p><p>根据不同地方的基本情况，制定相应的办法。</p></blockquote><p>【例】发展农村商品经济必须因地制宜，多种办法，多种路子。</p><p>【例】2022 年以来，宜川—黄龙地区开展整体研究，深入分析两个地区成藏条件差异，因地制宜刻画 不同层系有利区，针对马家沟组、山西组目标实施钻探。</p><p>【例】衡水市提出，加快构建区域生态保护格局，全面构建城市蓄排平衡体系，提升污水收集处理能 力，因地制宜将“+海绵”理念融入城市绿地、建筑、道路、广场、水系联通等新建改建项目中，整体提升城市对雨水的蓄滞、净化能力</p><blockquote><p>量体裁衣</p><p>按身材裁剪衣服。也比与按实际情况办事。</p></blockquote><p>【例】大企业是经济发展的顶梁柱，往往也存在着跨区域、多税种的复杂涉税诉求，这就更需要我们 聚焦精细服务，做到量体裁衣。</p><p>【例】围绕打造现代化区域中心城市，实现“首善之地”目标，呼和浩特市发展改革委出台《2024 年 支持呼和浩特市高质量发展工作要点》，推出 54 条新政策、新举措，“量体裁衣”配套梳理出 187 条具体落实措施。</p><p>【例】近年来，农行重庆分行持续加大对文旅产业重点项目支持力度，积极打造“金融+文旅”有效 路径，为不同项目量体裁衣，提供创新性解决方案，催生了一系列具有浓郁地方特色的文旅项目，让山城 文化底蕴和独特魅力焕发新光彩。</p><blockquote><p>对症下药</p><p>医生针对病症开方用药。比喻针对问题所在确定解决方法。</p></blockquote><p>【例】破除顽疾，关键在于对症下药、精准施策。</p><p>【例】今年以来，云南各级监察机关在日常监察和调查过程中，针对监察对象所在单位廉政建设和履 行职责存在的问题等，“对症下药”向直接责任单位发出监察建议书。</p><p>【例】此次活动共解答法律咨询问题 15 个，梳理后续法律需求事项 2 个，有效提升了企业经营者守 法经营意识，真正做到了针对企业法律需求“对症下药”，帮助企业防范法律风险。</p><blockquote><p>有的放矢</p><p>的：箭靶。矢：箭。对着靶子射箭。比喻目的性强，有针对性。</p></blockquote><p>【例】我们在评论工作时要有的放矢。</p><p>【例】因地制宜发展新质生产力，首先要深入调查研究，摸清家底，明确发展的优势和短板，这样才 能精准施策、有的放矢。</p><p>【例】写读书笔记时要扣住原文，有的放矢，这样才能写出自己的真感受。</p><table><thead><tr><th>发挥职能</th><th>因势利导、保驾护航、穿针引线</th></tr></thead></table><blockquote><p>因势利导</p><p>因：顺着。势：趋势。利导：引导。指顺着事物的发展趋势而加以引导。（褒义）</p></blockquote><p>【例】红寺堡区近年来采取大禹治水的办法，变堵为疏，因势利导，对一个个群众反映强烈的信访硬 案采取“软着陆”的方式，实现了群体上访和信访积案双下降。</p><p>【例】胡丽梅建议，应因势利导，寻找或设置替代游戏，满足孩子们“同伴游戏”的需求，逐渐远离 烟卡。“避免强制或粗暴制止，这样只会让孩子更隐秘地玩耍，引发亲子矛盾。”烟卡游戏背后确实潜藏 有一定的不良影响。</p><p>【例】近年来，祁阳市纪委监委因势利导，将家风建设、警示教育、清廉单元建设与廉洁文化阵地建 设有机结合，积极探索打造陶铸廉洁文化建设阵地、潘市镇文旅精品路线等一批廉洁文化建设新地标。</p><blockquote><p>保驾护航</p><p>保护某事物能让其能正常发展。</p></blockquote><p>【例】做好机构改革组织实施工作，必须以铁的纪律保驾护航。</p><p>【例】习近平总书记走进北京航天飞行控制中心，同神舟十二号航天员亲切通话。当时，为这场“天 地对话”保驾护航的航天科技人员许多是“80 后”“90 后”，让人们看到了青年一代的蓬勃力量。</p><p>【例】“我们将始终尊商重商、亲商护商，为广大企业发展保驾护航。”山东向香港和澳门各界友好 人士发出邀约，携手并肩、奋楫向前，共同打造鲁港澳合作更加美好的明天，在新时代续写鲁港澳合作新 篇章。</p><blockquote><p>穿针引线</p><p>使线的一头通过针眼。比喻从中联系、拉拢。</p></blockquote><p>【例】一年来，按照党中央统一部署，国务院用制度“穿针引线”，补短板、兜底线、保基本，确保 “民生安全网”网线结实、网底不破。</p><p>【例】长期以来，中国人民对外友好协会贯彻中国奉行的独立自主的和平外交政策，在国际社会和世 界各国广交深交朋友，为加深人民友谊深耕细作，为促进国家关系铺路架桥，为推动国际合作穿针引线， 做了大量卓有成效的工作，发挥了不可替代的作用。</p><p>【例】日前，依托后方的资源和技术优势，为助力本地人才培养和技术提升，经过上海援疆医疗队的 穿针引线，一场疑难危重病例远程 MDT多学科会诊讨论在巴楚县人民医院进行。</p><table><thead><tr><th>正确做法</th><th>广开言路、集思广益、正本清源</th></tr></thead></table><blockquote><p>广开言路</p><p>广：扩大；言路：进言的道路。指尽量给下面创造发表意见的条件。</p></blockquote><p>【例】发展社会主义协商民主，要把民主集中制的优势运用好，发扬“团结—批评—团结”的优良传 统，广开言路，集思广益，促进不同思想观点的充分表达和深入交流。</p><p>【例】兰考教育实践活动落实了中央要求，把学习弘扬焦裕禄精神作为主线，分层分类抓好学习，广 开言路汇集民意，原汁原味反馈意见。</p><p>【例】近年来，辽宁省以 16252 个“村（居）民评理说事点”为基础，以 1355 个乡镇（街道）矛盾 调解中心为主阵地新邱区将每个周六固定为群众说事日，社区“两委”成员、老党员、乡贤等围绕社区发 展、公德、矛盾纠纷等问题广开言路，一个个矛盾在这里随之化解。</p><blockquote><p>集思广益</p><p>集：积重。思：想法，智慧。广：扩大。益：益处，效果。指集中众人的智慧，可以收到更好的效果。</p></blockquote><p>【例】打开宫门、集思广益才是上佳之选，也才能更知民意、通民心，且吉祥物的设计，也会得到更 多的创意和灵感。</p><p>【例】参会的村（社区）党总支书记、各煤矿企业负责人、政协委员、群众代表、县直相关部门有关 负责人面对面协商，畅所欲言谈想法，集思广益寻良策，推动矿区大治理、矿村大发展。</p><p>【例】近年来，为有效巩固拓展脱贫攻坚成果，推动乡村振兴产业振兴，驻村工作队及村“两委”干 部集思广益，多措并举，在“谋羊业、做羊事、发羊财”上下“苦”功夫，大力推进特色产业发展，促进 脱贫人口增收。</p><blockquote><p>正本清源</p><p>正本：从根本上整顿；清源：从源头上清理。从根本上整顿，从源头上清理。比喻从根本上加以整顿清理。</p></blockquote><p>【例】山东大学儒学高等研究院教授、孔子研究院原院长杨朝明讲述了他用数十年来的研究，为孔子、 为儒学“正本清源”的心路历程。</p><p>【例】习近平总书记指出，要在推动学习贯彻新时代中国特色社会主义思想走深走实上下功夫，教育 引导党员、干部从思想上正本清源只有“学真理论”“真学理论”，才能正本清源、固本培元。</p><p>【例】张汉晖表示，乌克兰危机有着复杂的历史经纬和现实原因，要彻底化解危机，必须正本清源、 标本兼治。</p><hr><h3 id="【第二十五组】-合理规划-4-个" tabindex="-1"><a class="header-anchor" href="#【第二十五组】-合理规划-4-个"><span>【第二十五组】 合理规划（4 个）</span></a></h3><table><thead><tr><th>节约成本</th><th>开源节流、精打细算、细水长流、量入为出</th></tr></thead></table><ol><li>成语解释</li></ol><blockquote><p>开源节流</p><p>开发财源，节省支出，以储蓄财力。</p></blockquote><p>【例】经济下滑压力下，国务院国资委关于央企要“开源节流、降本增效”的调门提高。</p><p>【例】对粮食而言，日常生活中的每一次“开源节流”，都是在加重安全的砝码、安心的保障。作为 人口众多的大国，解决好吃饭问题始终是治国理政的头等大事。</p><p>【例】开源节流是让钱袋子鼓起来的根本途径。通过构建完善的农民工职业技能培训体系，帮助农民 工积累和提高人力资本，无疑是开源的重要手段。</p><blockquote><p>精打细算</p><p>意思是精密地计划，详细地计算。指在使用人力、物力时计算得很精细。</p></blockquote><p>【例】这种精打细算过日子的方式，让许多煤矿尝到甜头。许厂煤矿副总会计师梁奉越告诉记者，如 今，许厂煤矿每个月节约的材料费就超过 400 万元，煤炭综合成本也大为降低。</p><p>【例】我国纪念今年“世界水日”“中国水周”的活动主题是“精打细算用好水资源，从严从细管好水资源”。</p><p>【例】习近平总书记指出：“建城市、搞工业、保生态都要用地，必须精打细算，排出优先序，绝不 能占用耕地和违背自然规律去搞造林绿化。”</p><blockquote><p>细水长流</p><p>比喻节约使用财物，使经常不缺用。也比喻一点一滴不间断地做某件事，精细安排，长远打算。</p></blockquote><p>【例】这种细水长流式的方法不显山露水，但可以聚少成多，冯大才以这种方式收受回扣达 140 余万元。</p><p>【例】师者的大爱既体现在千钧一发之际的义无反顾，也体现在日常教育教学工作中的细水长流。</p><p>【例】伴随着这趟夯实传统友谊、推动务实合作、推进和平发展之旅，美丽塞纳河、蓝色多瑙河将与 长江黄河一道，静水流深、细水长流，浇筑出更加稳定、更加光明、更加美好的未来。</p><blockquote><p>量入为出</p><p>入：收入。根据收入的情况来安排支出。</p></blockquote><p>【例】很多大学生目前还是“伸手党”，并没有实现经济独立，更应该做到量入为出，理性网购。</p><p>【例】对信用卡持卡人而言，避免陷入财务困境的治本之策是学会科学理财，做到量入为出，并在平时为生活中可能发生的意外支出做好一定的储蓄和财务准备。</p><p>【例】坚持过“紧日子”，量入为出是前提，改进作风是保障。</p><hr><h3 id="【第二十六组】-政策措施的评价-8-个" tabindex="-1"><a class="header-anchor" href="#【第二十六组】-政策措施的评价-8-个"><span>【第二十六组】 政策措施的评价（8 个）</span></a></h3><table><thead><tr><th>有效果</th><th>立竿见影、一劳永逸、行之有效、一蹴而就</th></tr></thead><tbody><tr><td>解决问题</td><td>迎刃而解、事半功倍、釜底抽薪、标本兼治</td></tr></tbody></table><ol><li>成语解释</li></ol><table><thead><tr><th>有效果</th><th>立竿见影、一劳永逸、行之有效、一蹴而就</th></tr></thead></table><blockquote><p>立竿见影</p><p>在阳光下竖起竹竿，立刻就能看到它的影子。比喻效果显著迅速。</p></blockquote><p>【例】掌握了正确的学习方法，便可收到立竿见影的效果。</p><p>【例】“巡察的最终目的就是推动问题解决，我们要通过边巡边改、立行立改实现快速纠偏，以‘立 竿见影’的效果赢得群众的信任、彰显巡察监督的震慑力。”</p><p>【例】我们今年的重大项目落地非常快，政府职能部门推进助企政策立竿见影，非常直观地感受到吉 林营商环境正在越来越好。</p><blockquote><p>一劳永逸</p><p>逸：安逸。指辛苦一次把事情办好，以后就不用费力了。</p></blockquote><p>【例】此物长生，种者一劳永逸。</p><p>【例】脑起搏器治疗开机后并不是一劳永逸，想在术后达到理想恢复状态，除了患者要定期接受随访 和程控，还需要患者和家人注重术后的运动康复、饮食和一些生活细节。</p><p>【例】不少近视患者抱着“近视了大不了做个近视手术，跟好眼一样”的想法，认为近视手术可以一 劳永逸。事实上，近视手术并非很多人想象得那么简单。</p><blockquote><p>行之有效</p><p>之：代词，它，指办法、措施等；效：成效，效果。实行起来有成效。指某种方法或措施已经实行过， 证明很有效用</p></blockquote><p>【例】要增强战略的前瞻性、全局性、稳定性，把历史、现实、未来贯通起来，把中国和世界连接起 来，使我们制定的战略符合实际、行之有效，为中国式现代化提供强大的战略支撑。</p><p>【例】要以公平正义为理念引领全球治理体系变革，维护以世界贸易组织为核心的多边贸易体制，在 充分协商基础上，为人工智能、数字经济等打造各方普遍接受、行之有效的规则，为科技创新营造开放、公正、非歧视的有利环境，推动经济全球化朝着更加开放、包容、普惠、平衡、共赢的方向发展，让世界 经济活力充分迸发出来。</p><p>【例】中国不断发展全过程人民民主，建设行之有效的、具有中国特色的民主，取得了重要成就。</p><blockquote><p>一蹴而就</p><p>蹴：踏；就：成功。踏一步就可以获得成功，比喻事情很容易做。同“一蹴而得”。（多用于否定句中）</p></blockquote><p>【例】他们认为胜利不能一蹴而就，必须用持久战。</p><p>【例】然而，牙齿的移动并不是一蹴而就的，它需要一定的时间和过程。在这个过程中，牙齿周围的 牙周组织也在逐渐适应新的牙齿位置。因此，即使牙套已经摘除，牙周组织仍然需要一段时间来巩固牙齿 的新位置。</p><p>【例】生态文明素养其实是人类生存和持续发展的重要能力，但要提升生态文明素养，不能一蹴而就， 还得久久为功。生态科普应该面向更广泛的群体，通过更生动灵活的形式来开展。</p><table><thead><tr><th>解决问题</th><th>迎刃而解、事半功倍、釜底抽薪、标本兼治</th></tr></thead></table><blockquote><p>迎刃而解</p><p>刃：刀刃。解：分开。把竹子劈开口，下面的一段竹子就迎着刀刃裂开了。比喻主要问题解决了，其他的问题就很容易解决。（常搭配“问题”）</p></blockquote><p>【例】笔者以为，现在《解释》的出台，将使以上“三大难题”迎刃而解。</p><p>【例】企业正在进行生产许可证申报，时间紧迫，周耀斌的到来让难题“迎刃而解”。</p><p>【例】[广粤路广中路公交站大变样，拥堵难题迎刃而解！</p><blockquote><p>事半功倍</p><p>事：所要做的事情，指措施。功：功效。形容费力小，收效大。</p></blockquote><p>【例】从这个意义上讲，弘扬丝路精神，高标准推进境外经贸合作区建设，将使开放共赢变得事半功倍。</p><p>【例】针对不同人群、不同犯罪类别，因人施策，制定教育矫正方法和内容，在促进社区矫正对象融入社会、 预防和减少违法犯罪等工作中取得“事半功倍”。)</p><p>【例】很多看似不起眼的小点子、巧办法，来自基层干部的亲身实践，简便易行，往往能起到事半功 。</p><blockquote><p>釜底抽薪</p><p>釜：锅。薪：柴。从锅底下抽出柴火。比喻从根本上解决问题。</p></blockquote><p>【例】扬汤止沸不如釜底抽薪，必须深化体制机制改革，自觉转变职能、简政放权，从根子上铲除滋 生“四风”问题的土壤。</p><p>【例】一个国家、一个民族要是没有自己的精神独立性，政治、思想、文化、制度等方面的独立性就 会被釜底抽薪，就不可能走出、走稳、走好自己的道路。</p><p>【例】要坚持多管齐下、多策并举，严格落实防范管控措施，做到釜底抽薪、标本兼治。</p><blockquote><p>标本兼治</p><p>标本：事物的枝节和根本。从枝节和根本方面都得到治理</p></blockquote><p>【例】必须对非法“灵修”类活动保持高压严打，深挖背后利益链条，寻求标本兼治。</p><p>【例】标本兼治，把“当下改”与“长久立”结合起来，瞄准形式主义官僚主义之弊，对症施策、精 准发力，方能做到真减负、见真效。</p><p>【例】治理网络暴力，需要破立并举、标本兼治，既切除病灶，也铲除病根。</p><hr><h3 id="【第二十七组】-成功与失败-11-个" tabindex="-1"><a class="header-anchor" href="#【第二十七组】-成功与失败-11-个"><span>【第二十七组】 成功与失败（11 个）</span></a></h3><table><thead><tr><th>轻松成功</th><th>触手可及、唾手可得、手到擒来、信手拈来、游刃有余</th></tr></thead><tbody><tr><td>没能成功</td><td>束手无策、一筹莫展、功亏一篑、适得其反、功败垂成、 失之交臂</td></tr></tbody></table><ol><li>成语解释</li></ol><table><thead><tr><th>轻松成功</th><th>触手可及、唾手可得、手到擒来、信手拈来、游刃有余</th></tr></thead></table><blockquote><p>触手可及</p><p>伸手便可接触到。形容距离很近。</p></blockquote><p>【例】触手可及的金融服务，背后依靠的是科技的投入。</p><p>【例】阜阳市颍东区积极落实就业促进暖民心行动，通过完善“三公里就业圈”和常态化开展就业技 能培训，让群众高质量就业“触手可及”。</p><p>【例】随着数字技术的不断运用，人民群众对数字法院的需求逐步提升，但就司法实践来看，数字法 院建设距离通过群众考核、满足群众现代化司法期待、真正实现司法服务触手可及，还需过好统筹关、能 动关和推广关。</p><blockquote><p>唾手可得</p><p>唾手：往手上吐唾沫。比喻极容易得到。</p></blockquote><p>【例】如今，各种信息唾手可得，获取知识的门槛变低了。</p><p>【例】创新成果，并非唾手可得。“良率在 50%时好像遇到了瓶颈，每次都要推倒重来，很痛苦。”</p><p>【例】技术的导入拓展了阅读的疆域。互联网消除时空界限，使得各类信息资源唾手可得，数字技术 通过个性化推送与大数据分析，助力精准定位知识，提升阅读效率与针对性。</p><blockquote><p>手到擒来</p><p>擒：捉拿。一出手就把人捉住。形容做事毫不费力或很有把握。</p></blockquote><p>【例】抓落实不是探囊取物，不会手到擒来。</p><p>【例】群众的“急难愁盼”不少也是“老大难”的事，解决这些问题，不可能手到擒来、易如反掌，急于求成可能适得其反，问题没有解决，反而造成新的问题。</p><p>【例】无论从整体实力还是个人能力上来说，中国男足亚运队想要小组出线几乎是手到擒来，但球队 想要走得更远，特别是希望实现甚至赶超 1994 年广岛亚运会亚军的历史最佳战绩，就不能轻视任何对手。</p><blockquote><p>信手拈来</p><p>信手：随手；拈：用两三个手指头捏东西。随手拿来，多指写文章时能自由纯熟地选用词语或应用典 故、素材，用不着怎么思考。（多用于引用资料、数据、典故等）（也可形容做事不费力）</p></blockquote><p>【例】王老师讲课时，许多历史小故事信手拈来。</p><p>【例】读书时保持对话的心智，活化为自己的思想，写作时才能将读过的书信手拈来。</p><p>【例】从《偶遇成诗》到《只此青绿》《一壶老茶》《一路芬芳》，无论是歌颂家乡风物还是抒写时 代，别样的视角和细腻的笔触，于工整中求灵韵，看似信手拈来，其实匠心独运，为歌词创作提供了一种 新的文本。</p><blockquote><p>游刃有余</p><p>比喻工作熟练，实际经验很丰富，解决困难问题毫不费事。［近］应付自如。</p></blockquote><p>【例】比赛现场，萌娃们全副武装，有的在蜿蜒的赛道里游刃有余、一路领先，有的跌倒了迅速站起， 鼓足勇气冲向终点，家长和老师在场边暖心陪伴，献上加油和呐喊。</p><p>【例】要学会将复杂的职业技能拆解成一个个可管理的小单元，做到“庖丁解牛，游刃有余”。二要 带上“显微镜”，刻意练习。</p><p>【例】它不仅能够让我们在人际关系中更加游刃有余，还能够让我们在职业发展中更加成功。当我们 能够坦诚地表达自己的想法和意见时，我们不仅能够得到他人的理解和支持，还能够激发出更多的创新思 维和解决方案。</p><table><thead><tr><th>没能成功</th><th style="text-align:left;">束手无策、一筹莫展、功亏一篑、适得其反、功败垂成、 失之交臂</th></tr></thead></table><blockquote><p>束手无策</p><p>策：办法。手被捆住，无法应对。形容遇到问题没有解决的办法。</p></blockquote><p>【例】当危急情况出现时，有多少人并不是见义不为，而是束手无策？</p><p>【例】像登录验证、选择细分功能、关掉广告弹窗、人脸识别等操作，常常会让视障者束手无策。</p><p>【例】很多时候，遇到紧急情况，周围人即便很想施以援手，可限于技能缺乏，又不会操作急救设备， 往往束手无策，只能干着急。</p><blockquote><p>一筹莫展</p><p>筹：计策。展：施展。一点计策也施展不出。形容没有一点办法。</p></blockquote><p>【例】几个农民要搞科研，想解决专家都一筹莫展的难题，谈何容易。</p><p>【例】日前，包头稀土高新区万水泉镇安泰社区居民刘大爷想以“信用积分”兑换礼品，正当他因信 用积分不够一筹莫展之际，张老三粮油门市部的收银员接过刘大爷递来的积分卡，计算器快速操作一番后 说道：“再补 3 元钱就可以了。”</p><p>【例】就在他一筹莫展之际，家人的一句话，便让他想起前不久祁东农商银行工作人员正向其推荐过一款“惠农快贷”信贷产品。</p><blockquote><p>功亏一篑</p><p>篑：盛土的筐，指一筐土。后用来比喻做事情只差最后一点而前功尽弃。</p></blockquote><p>【例】目前已到冲刺阶段，须防功亏一篑。</p><p>【例】以色列对停火提议的拒绝令达成停火协议的所有努力功亏一篑。</p><p>【例】或许由于中西文化差异和中美两国国情的不同，基辛格起初对毛泽东的这一想法未能完全理解， 但随着交往的加深，他最终不得不承认：中国历史上不乏克服他人无法想象的深重苦难，但最终以文化优 势或幅员辽阔而让侵略者功亏一篑的事例。</p><blockquote><p>适得其反</p><p>适：恰好。结果与愿望恰好相反。（侧重取得反面结果）</p></blockquote><p>【例】这么看来，商家封杀职业打假人只能适得其反，不仅阻挡不了职业打假者的行动，更会让自己 的丑行败露，落得个拒绝监督、袒护问题商品的恶名。</p><p>【例】给孩子施压的手伸进了医疗技术领域容易适得其反，可能导致家长期望的效果没有出现，却遭 到医疗技术的反噬。对此，家长应当保持足够的谨慎。</p><p>【例】美国对华贸易战徒劳且适得其反，不但并未改变美对华依赖程度，反而使全球供应链更加复杂 脆弱。</p><blockquote><p>功败垂成</p><p>垂：接近，将要。事情在将要成功的时候失败了。含惋惜意。同“事败垂成”。</p></blockquote><p>【例】虽然离成功只有一毫之差、一步之遥,但终归不免功败垂成、留下缺憾。</p><p>【例】古人早就认识到了最后阶段路难走的问题，“为山九仞，功亏一篑”，堆九仞之高的山，只欠 最后一筐土，胜利在望却功败垂成，类似事情在古今中外历史上可谓不可胜数。</p><p>【例】十余年里，他们追寻总冠军的道路上满是艰辛，一次次闯进决赛圈，却又一次次功败垂成。</p><blockquote><p>失之交臂</p><p>交臂：胳膊碰胳膊，指擦肩而过。形容当面错过。</p></blockquote><p>【例】我心里一直憋着这口气，在里约奥运会上失之交臂的金牌，我一定要把它夺回来！”为了夺回 这枚金牌，刘洋五年没有回过家，即使是带伤上阵，也要发挥到最完美！</p><p>【例】上海女排已连续三年打进决赛，但都与冠军失之交臂。回顾这个赛季，上海女排既有高光时刻，也有遗憾瞬间。</p><hr><h3 id="【第二十八组】-领导力-14-个" tabindex="-1"><a class="header-anchor" href="#【第二十八组】-领导力-14-个"><span>【第二十八组】 领导力（14 个）</span></a></h3><table><thead><tr><th>眼界高考虑全</th><th>高屋建瓴、高瞻远瞩、深谋远虑、审时度势</th></tr></thead><tbody><tr><td>筹划全面</td><td>运筹帷幄、统筹兼顾</td></tr><tr><td>以身作则</td><td>身体力行、亲力亲为、克己奉公、以身作则、上行下效、 事必躬亲</td></tr><tr><td>始终坚持</td><td>一以贯之、矢志不渝</td></tr></tbody></table><ol><li>成语解释</li></ol><table><thead><tr><th>眼界高考虑全</th><th>高屋建瓴、高瞻远瞩、深谋远虑、审时度势</th></tr></thead></table><blockquote><p>高屋建瓴</p><p>建：倾倒。瓴：水瓶。从高屋脊上往下倒瓶中的水。形容居高临下，不可阻挡的形势。</p></blockquote><p>【例】这些形象大都是从战争年代的叱咤风云的指挥员身上脱颖出来的，具有气壮山河的英雄气概和 高屋建瓴的雄才大略。</p><p>【例】大家表示，习近平总书记的重要讲话高屋建瓴、精辟深邃，具有很强的政治性、思想性、指导 性，对进一步全面深化改革、扎实稳健推进中国式现代化具有重大意义，要认真学习领会、深入贯彻落实。</p><p>【例】“习近平总书记对统筹‘五对关系’的重要论述高屋建瓴、内涵丰富，深刻回答了新时代如何 推动旅游高质量发展、建设旅游强国的重大时代命题。”</p><blockquote><p>高瞻远瞩</p><p>瞻：望。瞩：注视。登高望远。形容目光远大。（更宏观）</p></blockquote><p>【例】亚投行、“一带一路”等宏大计划，显示出习近平的高瞻远瞩，也凸显了中国作为负责任大国 的国际担当。</p><p>【例】面对中美关系进一步发展的时代机遇，面对国际社会对中美加强合作的普遍期待，两国应高瞻远瞩，相互照顾彼此利益和关切，妥善化解分歧和矛盾，共同推进中美合作。</p><p>【例】“习近平主席提出的共建‘一带一路’、构建人类命运共同体等重大倡议和重要理念，在促进 各国共克时艰、共享机遇、共同繁荣等方面发挥了重要作用，习近平主席具有高瞻远瞩的战略眼光。”</p><blockquote><p>深谋远虑</p><p>计谋深远，考虑周密。</p></blockquote><p>【例】综合国力竞争说到底是人才竞争。人才是衡量一个国家综合国力的重要指标。这是一项深谋远虑的重大改革。</p><p>【例】以大开放促进大开发，是“立足一隅谋全局”的深谋远虑。</p><p>【例】大食物观是以习近平同志为核心的党中央基于我国经济社会发展需要和人民群众对美好生活向 往而提出的，体现了新时代中国共产党人的全局视野和长远眼光，是对中国发展方略的深邃思考和对人民 根本利益的深切关怀，充分反映了对我国现代农业发展路径的深谋远虑，有力彰显了对中国粮食安全等“国 之大者”的远见卓识。</p><blockquote><p>审时度势</p><p>审：详查，观察。时：当前情况。度：估计。势：发展趋势。观察时机，估计发展趋势。用来指对形势有洞察力。也作“审时定势”、“审几度势”。</p></blockquote><p>【例】同时，也为企业发展提出了一些中肯的建议：一要审时度势，强化核心竞争力；二要保持战略 定力，坚守使命愿景，执着创新；三要坚守合规与现金流底线，确保稳健发展；四要趋势研判，形成产业 链合力，共同应对挑战。</p><p>【例】面对影响党长期执政、国家长治久安、人民幸福安康的突出矛盾和问题，党中央审时度势、果 敢抉择，锐意进取、攻坚克难，团结带领全党全军全国各族人民撸起袖子加油干、风雨无阻向前行。</p><p>【例】各方要审时度势、趋利避害，不断深化对新时代就业工作规律的认识，总结、丰富、发展以往 的宝贵经验，强化就业优先政策，健全就业促进机制，推动就业形势持续改善。</p><table><thead><tr><th>筹划全面</th><th>运筹帷幄、统筹兼顾</th></tr></thead></table><blockquote><p>运筹帷幄</p><p>筹：谋划。帷幄：古代军队中的帐幕。指在帐幕中谋划计策。后指在后方指挥、筹划。</p></blockquote><p>【例】我们的目标是为各行各业的专业人士提供整合的信息解决方案，帮助他们更快地作出决策、作 出更明智的决策，从而在全球化的竞争中，运筹帷幄、决胜千里。</p><p>【例】习近平运筹帷幄，带领中国人民在全面建设社会主义现代化国家开局之年迈出了新气象，创造 了新辉煌。</p><p>【例】褒斜道见证了楚汉相争的“明修栈道，暗度陈仓”，古汉台奠定了汉室四百多年的煌煌基业。 历经秦风汉雨，又见三国传奇，不论是金戈铁马的运筹帷幄，还是碧血丹心的江湖驰骋，日夜东流的一江 汉水是无数历史变迁的见证。</p><blockquote><p>统筹兼顾</p><p>意思是统一筹划，全面照顾。</p></blockquote><p>【例】必须树立系统观念，以正确的历史观、大局观把握大势、统筹兼顾、掌握主动。</p><p>【例】统筹兼顾是中国共产党的一个科学方法论。它的哲学内涵就是马克思主义辩证法。</p><p>【例】优化营商环境牵一发动全身，是一项涉及经济社会改革和对外开放众多领域的系统工程，尤其 需要坚持系统观念、增强系统思维，在统筹兼顾中实现整体推进。</p><table><thead><tr><th>以身作则</th><th style="text-align:left;">身体力行、亲力亲为、克己奉公、以身作则、上行下效、 事必躬亲</th></tr></thead></table><blockquote><p>身体力行</p><p>身：亲身。体：体验。指亲身体验，努力实践。</p></blockquote><p>【例】增强安全意识、注意风险提示，敬畏自然、敬畏生命，每个人都该身体力行。</p><p>【例】伴着续写《雷锋日记》的阵阵热潮，官兵学习雷锋、争做雷锋，用实际行动追寻雷锋同志的足 迹，在身体力行中深刻感悟雷锋精神、赓续优良传统。</p><p>【例】建设新青海绿色家园，我们每个人既是良好生态环境的“乘凉者”，更要身体力行、做好造福后人的“栽树者”。</p><blockquote><p>亲力亲为</p><p>亲自参与，不由别人来代替。</p></blockquote><p>【例】大到治安防范，小到邻里琐事，李柚池无不亲力亲为。</p><p>【例】领导干部要亲力亲为，坚决杜绝侥幸心理，拿出“一竿子插到底”的劲头，以科学严肃的态度 及时处置各类隐患事故，做到防患于未然、化解于萌芽。</p><p>【例】要在作风建设上突出亲力亲为、求真务实、不图形式、依法按规矩办事，以良好精神状态和过 硬工作作风保障工作落实。</p><blockquote><p>克己奉公</p><p>克己：约束自己；奉公：以公事为重。克制自己的私心，一心为公。</p></blockquote><p>【例】全党同志都要明大德、守公德、严私德，清清白白做人、干干净净做事，做到克己奉公、以俭 修身，永葆清正廉洁的政治本色。</p><p>【例】大家心中要始终装着老百姓，先天下之忧而忧，后天下之乐而乐，做到不谋私利、克己奉公。 对个人的名誉、地位、利益，要想得透、看得淡，自觉打掉心里的小算盘。</p><p>【例】与腐败惯性作坚决斗争，是涵养克己奉公、清廉自守精神境界最重要、最根本、最艰难的任务，构成涵养克己奉公、清廉自守精神境界的本质性规定。</p><blockquote><p>以身作则</p><p>身：自身。则：榜样。自己以行动给大家做出榜样。</p></blockquote><p>【例】遵守交通规则是每个人的责任，尤其是偶像明星，更要时刻慎独自省，以身作则，为社会做好示范。</p><p>【例】“总书记以身作则，出行考察关心每一个细节，教育身边的人真正按照中央八项规定去做，给所有人树立了标杆，起到榜样作用。”</p><p>【例】多年来，她一直以身作则，家庭美满和睦，热心帮助友邻，让文明家风代代相传。</p><blockquote><p>上行下效</p><p>行：做。效：效法。指上面的人怎么做，下面的人就跟着怎么干。</p></blockquote><p>【例】以上率下是管党治党责任具体化的第一步，只有上下齐心、上行下效才能在落实上见成效。</p><p>【例】很多时候，问题在下面，源头在上面，作风养成历来是上行下效。</p><p>【例】要树立队伍管理永远在路上，永远是零起点的思想和理念，干部骨干要率先垂范、上行下效， 严防“灯下黑”，紧盯“人、车、酒、网、赌”等重点环节，精准发力、精细管理、查漏补缺、堵塞漏洞。</p><blockquote><p>事必躬亲</p><p>躬亲：亲自做。凡事都一定要自己亲自去做。</p></blockquote><p>【例】从邀请国内顶级专家学者，确定具体讲座时间、审查主讲人简介、讲座内容，到组织学生撰写 主持词和大讲堂简报等，每一件事祁欢都竭尽全力、事必躬亲。</p><p>【例】为了确保实景剧的质量和进度，马新明事必躬亲，要求下属“当日事当日毕”“只能说如何行，不能说不行”。</p><p>【例】作为民宿“东澄山庄”主理人，她事必躬亲，茶园、果园、田地，餐厅、后厨、前台，都活跃 着她的身影。</p><table><thead><tr><th>始终坚持</th><th>一以贯之、矢志不渝</th></tr></thead></table><blockquote><p>一以贯之</p><p>指做人做事，按照一个道理，从始至终都不会改变。贯：一直，习惯。</p></blockquote><p>【例】加强纪律建设是全面从严治党的治本之策，必须把加强党的纪律建设作为永恒课题，把严的基 调、严的措施、严的氛围长期坚持下去，推动管党治党一以贯之、一刻不停、一严到底。。</p><p>【例】陈吉宁强调，要在学习党章中更加深刻领悟党的纪律建设的内在逻辑，旗帜鲜明、一以贯之坚 持严的导向，自觉维护党章党规党纪的严肃性、权威性。</p><p>【例】“中央八项规定试出了人心向背，厚植了党的执政根基，已经成为我们党的一张‘金色名片’， 要一以贯之地践行。”</p><blockquote><p>矢志不渝</p><p>矢志不渝（矢：誓；渝：改变）指立下志愿，绝不改变。</p></blockquote><p>【例】夏一代代矢志不渝的治沙人，用越来越广阔的“人工绿洲”抗衡、收拢甚至取代着曾经漫无边 际的黄色丘脊。</p><p>【例】“中国天眼”奠基人南仁东 22 年扎根深山，矢志不渝铸造大国重器。</p><p>【例】忠诚是每一个重庆人的个性特点，重庆人不仅仅只是忠诚，而且还有不怕困难险阻、矢志不渝的坚韧。</p><hr><h3 id="【第二十九组】-正义精神和做法-9-个" tabindex="-1"><a class="header-anchor" href="#【第二十九组】-正义精神和做法-9-个"><span>【第二十九组】 正义精神和做法（9 个）</span></a></h3><table><thead><tr><th>形容英勇无畏</th><th>前仆后继、视死如归、大义凛然</th></tr></thead><tbody><tr><td>形容振作奋发</td><td>踔厉奋发、笃行不怠、赓续前行、奋楫争先</td></tr><tr><td>形容克服困难前进</td><td>砥砺前行、勇毅前行</td></tr></tbody></table><ol><li>成语解释</li></ol><table><thead><tr><th>形容英勇无畏</th><th>前仆后继、视死如归、大义凛然</th></tr></thead></table><blockquote><p>前仆后继</p><p>意思是指前面的人倒下了，后面的人继续跟上去。英勇斗争，不怕牺牲。</p></blockquote><p>【例】我们不能忘怀视死如归、前仆后继的抗争。历史不会因时代变迁而改变，事实也不会因巧舌抵 赖而消失。</p><p>【例】岁月流转，匆忙如梭，无数个青年前仆后继地谱写祖国和民族的华章。</p><p>【例】为了实现中华民族复兴这个伟大的理想，无数仁人志士前仆后继，赴汤蹈火，只为了能够早日 实现民族复兴。</p><blockquote><p>视死如归</p><p>把死看得像回家一样平常。形容不怕牺牲生命。</p></blockquote><p>【例】今天，为了拯救人民群众的生命财产我们义无反顾，明天，当那一天来临时，我们同样视死如归。</p><p>【例】一百多年来，在理想信念的感召下，一大批视死如归的革命烈士、一大批顽强奋斗的英雄人物、一大批忘我奉献的先进模范，书写了感天动地的伟大史诗，谱写了气吞山河的英雄壮歌。</p><p>【例】正是在党的领导下，中华儿女同仇敌忾，视死如归，用生命和鲜血打败了日本侵略者。</p><blockquote><p>大义凛然</p><p>大义：正义。凛然：令人敬畏的样子。形容为了坚持真理而表现出的严峻不可侵犯的样子。</p></blockquote><p>【例】在敌人的法庭上，王若飞同志大义凛然，慷慨陈词。</p><p>【例】他大义凛然，从文城监牢到校场坡沿途数里，一路高呼“打倒国民党反动派！”“共产党万岁!” 等口号，英勇就义，牺牲时 28 岁。</p><p>【例】难忘在鲁迅名篇《记念刘和珍君》中，作为北京学生运动领袖之一的刘和珍率领着一群和自己 一般大的同学们，为了国家正义和思想解放，大义凛然、誓死不屈，最终用自己的热血开辟了一条中国革 命的光明之道。</p><table><thead><tr><th>形容振作奋发</th><th>踔厉奋发、笃行不怠、赓续前行、奋楫争先</th></tr></thead></table><blockquote><p>踔厉奋发</p><p>踔chuō，形容精神振奋，斗志昂扬。</p></blockquote><p>【例】面对又一次重大历史机遇，全区农牧系统干部职工纷纷表示要奋勇担当善作为，踔厉奋发闯新 路，加快推进农牧业现代化，努力提升国家重要农畜产品生产基地综合生产能力。</p><p>【例】在重温或加深红色记忆的过程中，不忘我们从哪里走来，于“初心”中牢记使命，于“知行合 一”中接过英烈旗帜，在新的历史征程下，踔厉奋发、笃行不怠。</p><p>【例】我们唯有踔厉奋发、笃行不怠，方能不负历史、不负时代、不负人民。</p><blockquote><p>笃行不怠</p><p>意思是切实履行自己所学的内容，不感到倦怠。</p></blockquote><p>【例】一路砥砺奋进，增强了一起向未来的信心和底气；向着更高目标进发，尤需我们踔厉奋发、笃行不怠。</p><p>【例】其美多吉用自己的持之以恒与笃行不怠，在茫茫雪山上写下了独属于坚持的答案，将平凡一笔 一划镌刻成伟大。</p><p>【例】奋斗者追风赶月，贵在笃行不怠。</p><blockquote><p>赓续前行</p><p>意思是指不断地延续和发展，保持进步的态度和生活方式。</p></blockquote><p>【例】深入宣传好、贯彻好、落实好福建省两会精神，赓续前行、奋楫争先，为在推进中国式现代化 中展现福建作为、谱写福建篇章开好局、起好步。</p><p>【例】赓续前行，不断推进高水平气象科技自立自强。</p><p>【例】以“吾将上下而求索”的恒心，做赓续前行的传承者。</p><blockquote><p>奋楫争先</p><p>意思是奋力划动船桨的人争先恐后走在前头。</p></blockquote><p>【例】情牵人民，福暖中国，这个难忘的场景充分彰显了中国共产党为人民谋幸福的初心使命，也是 “福”文化深入人心、沁入血脉的生动写照，激发起全省上下奋楫争先、为民造福的磅礴力量和坚定拥护 “两个确立”、坚决做到“两个维护”。</p><p>【例】时代在变，但时代对青年的殷殷关切不变；年轻的面孔在变，但其胸怀的奋进之心不变，青年 永远保持奋楫争先的壮志豪情。</p><p>【例】端午节到来之际，许多地方举行赛龙舟活动，人们在奋楫争先的热闹氛围中，感受端午的文化 魅力。</p><table><thead><tr><th>形容克服困难前进</th><th>砥砺前行、勇毅前行</th></tr></thead></table><blockquote><p>砥砺前行</p><p>经历磨炼，克服困难，往前进步的意思。也作“砥砺奋进”。</p></blockquote><p>【例】在推进中国式现代化的征程上，脚踏实地，砥砺前行，一点一滴地进步里，是一个越来越好的中国。</p><p>【例】以英雄为榜样，从他们身上汲取力量，接续奋斗、砥砺前行，这是对他们最好的致敬。</p><p>【例】中朝双方将继续风雨同舟，奋楫破浪，砥砺前行，续写两国友谊更加美好和精彩的新篇章。</p><blockquote><p>勇毅前行</p><p>意思是勇敢并且坚毅地向前进步或发展。</p></blockquote><p>【例】习近平全票当选国家主席、中央军委主席，极大鼓舞和激励着亿万中华儿女在新征程上踔厉奋 发、勇毅前行。</p><p>【例】厚植实现民族复兴的精神力量，始终保持锐意进取、敢为人先、迎难而上的奋斗姿态，保持战 略定力、增强必胜信心，铆足干劲、勇毅前行，就一定能汇聚无坚不摧的磅礴伟力，胜利推进强国建设、 民族复兴的历史伟业。</p><p>【例】“我们要不畏浮云遮望眼，准确认识历史发展规律，不为一时一事所惑，不为风险所惧，勇敢 面对挑战，向着构建人类命运共同体的目标勇毅前行。”越是困难时刻，越要坚定信心、激流勇进，向着 构建人类命运共同体的目标勇毅前行。</p><hr><h3 id="【第三十组】干部易犯错误-22个" tabindex="-1"><a class="header-anchor" href="#【第三十组】干部易犯错误-22个"><span>【第三十组】干部易犯错误（22个）</span></a></h3><table><thead><tr><th>爱慕名利不务实</th><th>好高骛远、好大喜功、沽名钓誉、附庸风雅</th></tr></thead><tbody><tr><td>只顾自己、自扫门前雪</td><td>独善其身、明哲保身</td></tr><tr><td>虚伪敷衍谄媚</td><td>虚与委蛇、委曲求全、敷衍塞责、曲意逢迎、投其所好、 阿谀奉承、趋炎附势、如蚁附膻</td></tr><tr><td>错误政策</td><td>兴师动众、劳民伤财、朝令夕改</td></tr><tr><td>不认真</td><td>粗枝大叶、大而化之、具体而微</td></tr><tr><td>其他错误</td><td>哗众取宠、凌空蹈虚</td></tr></tbody></table><ol><li>成语解释</li></ol><table><thead><tr><th>爱慕名利不务实</th><th>好高骛远、好大喜功、沽名钓誉、附庸风雅</th></tr></thead></table><blockquote><p>好高骛远</p><p>骛：通“务”，追求。指不切实际地追求过高、过远的目标。</p></blockquote><p>【例】有读者并不认同，指出青年人好高骛远、物质至上、个人主义的“青春短板”，并认为这些可 能会压垮青春梦想。</p><p>【例】不好高骛远、不脱离实际，力戒形式主义、官僚主义，切实提高解决实际问题能力，才能更好 肩负起新时代的职责和使命。</p><p>【例】青年时期是苦练本领、增长才干的黄金时期，最忌好高骛远、浮于空想，最怕做事三天打鱼、 两天晒网。</p><blockquote><p>好大喜功</p><p>喜欢做大事、立大功。多形容浮夸、不切实际的行为。</p></blockquote><p>【例】在现实生活中，有少数领导干部好大喜功，乐于做面子工程、政绩工程，喜欢“作秀”或者工 作上务虚不务实，“雷声大、雨点小”。</p><p>【例】急躁冒进、好大喜功、违背科学，实际上是对时间资源的任性挥霍。</p><p>【例】过去一段时间，一些地方在发展教育的时候好大喜功，热衷于建设豪华学校，不仅给地方财政 带来了巨大的压力，也透支了地方教育发展的空间和未来。</p><blockquote><p>沽名钓誉</p><p>比喻故意矫情做作，用手段猎取名声或赞誉。</p></blockquote><p>【例】但对领导干部而言，沉溺于追名逐利，醉心于沽名钓誉，就无异于打开“潘多拉”盒子，万般 恶事多因此而起。</p><p>【例】做公益事业，最要避免的就是沽名钓誉。</p><p>【例】书法不是沽名钓誉的工具，不是贪污腐败的渊薮，不是少数人的小圈子游戏，而是属于每一个中国人的宝贵艺术存在，是必须严肃对待的书写文化。</p><blockquote><p>附庸风雅</p><p>附庸：依傍，追随；风雅：泛指诗歌。指缺乏文化修养的人为了装点门面而结交文人，参加有关文化 活动，以示自己有一定的文化素养（装文化人）。</p></blockquote><p>【例】经查，蒋科荣身为党外领导干部，把党外当法外，毫无法律规矩意识，利用政协副主席的政治 光环牵线搭桥、交朋结友，在附庸风雅的吹捧中迷失自我，挖空心思变现手中的权力，把政协资源当作大 肆敛财的工具。</p><p>【例】已近耳顺之年，江耀进认为，写诗不是为了附庸风雅，而是彻底地打开自己，让诗句穿透血液， 敲击骨髓，获得内心的抚慰和解放。这些作品创作于2020 年至 2022 年的三年间。</p><p>【例】唐朝人姚汝能写过一本《安禄山事迹》，里面还有一则故事：话说叛臣史思明不识字却很喜欢 附庸风雅，没事就爱作几首打油诗。</p><table><thead><tr><th>只顾自己、自扫门前雪</th><th>独善其身、明哲保身</th></tr></thead></table><blockquote><p>独善其身</p><p>意思是不得志时也要注意自身的修养。后指只顾保持自身修养而不顾他人或全局。（语境要素：个人与外部的对比、保全保护）</p></blockquote><p>【例】如果整个社会不改好，个人想独善其身是不可能的。</p><p>【例】构建海洋命运共同体理念启示我们，没有国家能够凭一己之力解决海洋生态环境问题，也没有 国家能够袖手旁观、独善其身，各国唯有增强责任意识、加强团结合作，才能推动海洋可持续发展。</p><p>【例】习近平总书记指出：“面对生态环境挑战，人类是一荣俱荣、一损俱损的命运共同体，没有哪 个国家能独善其身。”</p><blockquote><p>明哲保身</p><p>明哲：聪明有智慧。指聪明有智慧之人，善于趋安避危，保全自身。也指为保全个人利益而回避原则 问题的处世态度。</p></blockquote><p>【例】科研只能实事求是，不能明哲保身，否则受害的将是患者。</p><p>【例】广大党员干部应该认识到，如果采取“只要不出事，宁可不干事”的明哲保身态度，因为不敢 担风险而索性不作为，那耽误的是党和国家事业发展，损害的是民生福祉。</p><p>【例】“好好先生”看似与人为善，实则明哲保身，看似与世无争，实际上打的全是“小算盘”，说 到底，就是怕得罪人，怕自己的利益受影响，是私心作祟、私利作怪。</p><table><thead><tr><th>虚伪敷衍谄媚</th><th style="text-align:left;">虚与委蛇、委曲求全、敷衍塞责、曲意逢迎、投其所好、 阿谀奉承、趋炎附势、如蚁附膻</th></tr></thead></table><blockquote><p>虚与委蛇</p><p>虚：不真实，假意。与：跟。委蛇：敷衍。指对人虚情假意，敷衍应酬。</p></blockquote><p>【例】批评贵在坦诚真挚，来不得虚与委蛇，甚或苦心算计。</p><p>【例】但也有一些人往往会用力过猛或准备过度，变成了尽说那些经过一再打磨的“正确的废话”， 满口标准的“普通话”，没有个性，更没有棱角；有的还常常说那些虚与委蛇或顺杆爬的场面上的话，说些应景式的套话、空话、拜年话。</p><p>【例】据了解，该剧的策划、创作、参演人员主要由民警、辅警组成，讲述了原本心地善良的小伙杨 洋在被骗入诈骗集团后，为了生存，从起初的与诈骗份子虚与委蛇，到逐渐偏离正轨逐渐黑化成为诈骗分 子骨干，在反诈中心民警谷宇所率领的小队的层层推进下最终弃暗投明。</p><blockquote><p>委曲求全</p><p>曲意迁就，以求事成，或保全大局。</p></blockquote><p>【例】爱好和平并不是纵容罪恶，更不等于委曲求全。</p><p>【例】相比起调解的结果，更值得关注的是调解过程，以及产生争议的原因——劳动者到底是发自内 心感到满意，还是迫于压力委曲求全？在一片口诛笔伐之中，涉事医院表示将积极整改，并更新了补偿方 案。</p><p>【例】委曲求全更需智慧誓死捍卫真理固然勇敢，而为了真理委曲求全，体现的则是另一种勇气。</p><blockquote><p>敷衍塞责</p><p>敷衍：马虎应付。塞责：搪塞责任。做事马虎，应付一下以搪塞责任。</p></blockquote><p>【例】严禁拒不执行组织决定和安排，严禁敷衍塞责、临危退缩等违反纪律行为，要积极主动参与救 援，全力搜救失联人员，切实解决灾害处置中的困难问题。</p><p>【例】实际工作中，面对不断变化的形势、艰巨繁重的任务、人民群众的期盼，倘若踟蹰不前、放松 懈怠，甚至敷衍塞责，就可能耽误工作、贻误发展。</p><p>【例】美方的声明内容，避重就轻、敷衍塞责，完全无法满足国际社会的真正关切。</p><blockquote><p>曲意逢迎</p><p>曲意：违背本意。违背自己的本心，千方百计迎合或讨好别人。</p></blockquote><p>【例】微笑也是人与人之间最短的距离。达到这种结果的前提是发身内心的微笑，而不是曲意逢迎的。</p><p>【例】要保持政治上的清醒，严格按照党的原则和政策办事，对上不曲意逢迎、不出卖原则，敢讲真 话；对下不封官许愿，不以权谋私；对同级不讨好卖乖，不跑风漏气。</p><p>【例】党员干部无论职务高低、权力大小，都要时刻以“君子检身，常若有过”的态度崇德戒俗，摒 弃曲意逢迎的媚气，涵养无欲则刚的骨气。</p><blockquote><p>投其所好</p><p>意思是迎合别人的喜好。</p></blockquote><p>【例】可以看出，投其所好、表达心意是年轻人送礼物更为在意的方面，重视礼物承载心意、展现尊 重等情感价值，但对拿得出手有面子也同样重视，而这往往更直接地反映在礼物的经济价值上。</p><p>【例】他们瞄准老年人警惕性不强、对视频剪辑知识匮乏等弱点，投其所好，编织骗局。无论骗谁， 都于法不容，但“收割”老年人，更显卑劣。报道提供一个细节，2023 年 8 月，一名受害者在短视频平台 上刷到了一条视频</p><p>【例】当前，网上直播方兴未艾，网上演出蔚然成风，当越来越多的年轻人习惯于网络化生存，文艺 院团不妨主动接近，投其所好，开辟新赛道，挖掘第二舞台，赋予传统演出以活力。这是一次令人欣慰的 双向奔赴。</p><blockquote><p>阿谀奉承</p><p>阿谀：为讨好而说好听的话。奉承：恭维别人。为讨好而说好听的话恭维别人。也作“阿谀逢迎”。</p></blockquote><p>【例】在影片中商纣王为了王权之争，宠信妲己，在一声声阿谀奉承中迷失自我，斩杀忠诚之士，修 鹿台纵享酒池肉林之乐。这就要求基层干部要严守纪律规矩，自觉把好行为之舵，扭紧思想的方向盘，扣 紧每一粒廉洁的“纽扣”。</p><p>【例】形成规规矩矩的上下级关系，意味着上级对下级不能颐指气使，下级对上级不能阿谀奉承。严 格党的纪律，要坚持党纪面前党员人人平等。</p><p>【例】对党忠诚、光明磊落的老实人，从不在党性原则问题上打折扣、搞变通，也从不屑于搞拉拉扯 扯、吹吹拍拍、阿谀奉承，当没有公心只有私心、没有正气只有俗气的“老好人”，而是在言行一致、表里如一中讲真话讲实话、干实事求实效。</p><blockquote><p>趋炎附势</p><p>趋：奔走；炎：热，比喻权势。奉承和依附有权有势的人。</p></blockquote><p>【例】作为青年团的成团大戏，该剧将家庭伦理的“拜寿”故事巧妙地融合在忠奸斗争中，描绘人情 冷暖、世道人心，颂扬忠义温良的真善美，抨击趋炎附势的假恶丑。</p><p>【例】翻译成今天的话就是：端庄稳重、严谨自律，不追求浮华，不趋炎附势，即使身居高位，也常 常谦逊得像普通百姓一样。</p><p>【例】民间传说中，陆游因其强烈的爱国精神，死后被赋予“阎王”身份，象征其精神永存，专门惩 治生前阿谀奉承、趋炎附势之徒。地藏殿壁画。</p><blockquote><p>如蚁附膻</p><p>附：趋附；膻：羊肉的气味。像蚂蚁趋附羊肉一般。比喻许多臭味相投的人追求不好的事物。也比喻许多人依附有钱有势的人。</p></blockquote><p>【例】正是看到有机可乘，不法商人们如蚁附膻，无所不用其极。老板姚某为了承接水务集团的工程 项目，千方百计讨好单国平，经常给他送钱送物。</p><table><thead><tr><th>错误政策</th><th>兴师动众、劳民伤财、朝令夕改</th></tr></thead></table><blockquote><p>兴师动众</p><p>意思是指大规模出兵。现多指动用很多人力做某件事。</p></blockquote><p>【例】怕就怕一大帮子人兴师动众地下去应景应酬，检查方徒造声势，接待方“意思意思”，在走走 转转、吃吃喝喝中败坏了党风、政风。</p><p>【例】今年 10 月份，700 多亩中稻收完后，刘峰花了一万多块钱，专门添置了一台油菜一体化播种机， 兴师动众，准备大干一场。</p><p>【例】有的没有认识到调查研究的必要性和重要性，对于调查研究或避而不谈，或浅尝辄止，或走马 观花；有的带着框子找例子、带着论点找论据，把调查研究变成结论的预设；有的放不下架子，总是热衷 于前呼后拥、兴师动众，这样的调查研究难以听到真话。</p><blockquote><p>劳民伤财</p><p>意思是既使人民劳苦，又耗费钱财；现也指滥用人力物力。</p></blockquote><p>【例】支持保护改革创新的同时，江西严禁打着改革创新旗号搞劳民伤财的“政绩工程”“形象工程”， 坚决惩治借改革创新之名徇私舞弊、贪污受贿、假公济私以及严重侵害群众利益等行为。</p><p>【例】应坚持节俭务实原则，切忌贪大求洋，不搞脱离实际、铺张浪费、劳民伤财的面子工程、形象 工程。</p><p>【例】现实中，个别领导干部“不是自己开头的不为，一定要刻上自己政绩印记的才干”，热衷搞“形 象工程”“政绩工程”，既破坏工作稳定性和连续性，又劳民伤财，损害的是人民群众的根本利益。</p><blockquote><p>朝令夕改</p><p>早上颁布的政令，晚上就改了。形容政令无常。也形容主张、办法等经常改变。</p></blockquote><p>【例】或许有人会说，改革时代政策多变，政府“朝令夕改”在所难免。但是要知道，根据形势调整 政策、实施改革，并不是不看实际，罔顾民意，想改就改。</p><p>【例】习近平总书记指出：“大国政贵有恒，不能朝令夕改，不要折腾”“不要一换届领导就兜底翻，更不要为了显示所谓政绩去另搞一套，不要空洞的口号满天飞”。</p><p>【例】中消协有关负责人表示，平台和商家应认识到，规则简化不等于无规则，先提价再降价套路要 不得，二次降价的朝令夕改也会伤及消费者感情和品牌形象，希望平台和商家遵循市场规律和契约精神， 坚持合法合规，制定优惠政策。</p><table><thead><tr><th>不认真</th><th>粗枝大叶、大而化之、具体而微</th></tr></thead></table><blockquote><p>粗枝大叶</p><p>绘画，画树木粗枝大叶，不用工笔。比喻工作粗糙，不认真细致。</p></blockquote><p>【例】调查研究要坚持实事求是的原则。坚持实事求是，最基础的工作就是了解实际、掌握实情。这 就要求我们必须作深入系统而不是粗枝大叶的调查研究，使思想、行动、决策符合客观实际。</p><p>【例】粗枝大叶、自以为是的主观主义作风，就是党性不纯的第一个表现；而实事求是，理论与实际 密切联系，则是一个党性坚强的党员的起码态度。</p><p>【例】绝不可粗枝大叶，或是避而不管。为民服务时，不妨多问一句。更好满足群众需求、更快解决 群众困难，都需要认真细致的工作作风。</p><blockquote><p>大而化之</p><p>化：感化。原指把真诚、善良、完美的品德发扬光大，使人的思想品德得以完美。后用来形容做事大 大咧咧，不谨慎，不细致。</p></blockquote><p>【例】注重调研成果转化运用，在调查的基础上深化研究，提高调研成果质量，切实把调研成果转化 为解决问题、改进工作的实际举措，防止调查多研究少、情况多分析少，提出的对策建议大而化之、空洞 抽象、不解决实际问题。</p><p>【例】空喊口号、好大喜功、胸中无数、盲目蛮干不行，搞大水漫灌、走马观花、大而化之、手榴弹 炸跳蚤也不行，必须在精准施策上出实招、在精准推进上下实功、在精准落地上见实效。</p><p>【例】要防止调查多研究少、情况多分析少，提出的对策措施大而化之、空洞抽象。</p><blockquote><p>具体而微</p><p>具体：各部分已大体具备；微：微小。指事物的各个组成部分大体都有了，不过形状和规模比较小些。</p></blockquote><p>【例】中国梦是国家的梦、民族的梦，也是每一个中国人的梦。“得其大者可以兼其小。”“宏大叙 事”的国家梦，也是“具体而微”的个人梦。历史告诉我们，每个人的前途命运都与国家和民族的前途命 运紧密相连。</p><p>【例】在中国社会科学院考古学论坛上，本次入选的“六大”，共同为我们勾勒出具体而微的古人生 产生活的生动画卷。</p><p>【例】普通人身边具体而微的变化，彰显着我们不断深化改革开放取得的巨大成就，印证着“改革开放是当代中国发展进步的活力之源”。</p><table><thead><tr><th>其他错误</th><th>哗众取宠、凌空蹈虚</th></tr></thead></table><blockquote><p>哗众取宠</p><p>用浮夸的言词或做作的行动去迎合群众，以博取好感和支持。</p></blockquote><p>【例】这是民进党当局倚美谋独企图的又一次充分暴露，终将是一场哗众取宠的闹剧。</p><p>【例】经公安机关核查，渝北区范围内未发现相关案件和警情，系甘某某道听途说，为了哗众取宠， 故意编造不实信息。甘某某对其违法行为供认不讳，南岸区公安分局已依法对甘某某处以行政处罚。</p><p>【例】一类是假冒仿冒官方组织、新闻媒体、专家名人等账号，在一些社会事件进程中抢先发声，散 布夸大其词、哗众取宠、未经证实的消息，带节奏炒热点，借以吸粉引流变现。</p><blockquote><p>凌空蹈虚</p><p>形容不切实际、虚浮空泛。</p></blockquote><p>【例】要以自我革命精神引领履职方法创新发展，切实把政协党的建设新成效体现到防止凌空蹈虚、 保证求实务实。</p><p>【例】坐而论道、凌空蹈虚与马克思主义学风格格不入，也不符合干事创业的实践要求。理论只有与 实际相结合，才能真正发挥对实践的指导作用，才能在理论的引领下做好实际工作。</p><p>【例】没有哪一种文学创新可以完全凌空蹈虚，从零开始，与文学传统毫无关联。相反，只有熟知文 学传统，才能改造文学传统，青出于蓝而胜于蓝。哪怕是激烈反对文学传统的作家也从文学传统之中获得 反作用力。</p><hr><h3 id="【第三十一组】对待错误的态度-12个" tabindex="-1"><a class="header-anchor" href="#【第三十一组】对待错误的态度-12个"><span>【第三十一组】对待错误的态度（12个）</span></a></h3><table><thead><tr><th>谦虚接受建议</th><th>从谏如流、从善如流、闻过则喜、虚怀若谷</th></tr></thead><tbody><tr><td>遮掩错误</td><td>文过饰非、怙恶不悛、欲盖弥彰</td></tr><tr><td>不考虑别人建议</td><td>刚愎自用、固执己见</td></tr><tr><td>彻底改变</td><td>洗心革面、脱胎换骨、改头换面</td></tr></tbody></table><p>31.1 成语解释</p><table><thead><tr><th>谦虚接受建议</th><th>从谏如流、从善如流、闻过则喜、虚怀若谷</th></tr></thead></table><blockquote><p>从谏如流</p><p>谏：直言规劝。听从规劝像流水一样自然。形容乐于接受别人的批评意见。</p></blockquote><p>【例】在我们的领导干部队伍中，有的同志虚怀若谷、从谏如流，热忱欢迎监督。</p><p>【例】党的领导机关和领导干部对各种不同意见都必须听取，领导干部特别是高级干部必须带头从谏如流、敢于直言。</p><p>【例】面对批评，要保持从谏如流的谦逊态度，彰显党性修养。忠言逆于耳，良药苦于口。对待批评 意见要虚怀若谷、闻过则喜，正确对待、虚心接受，有则改之、无则加勉，不搞无原则的纷争。</p><blockquote><p>从善如流</p><p>从：听从；善：好的，正确的；如流：好像流水向下，形容迅速。形容能迅速地接受别人的好意见。</p></blockquote><p>【例】开展批评和自我批评要出以公心、严肃认真，自我批评不遮不掩、触及灵魂，相互批评坦诚相 待、推心置腹，对待批评意见闻过则喜、从善如流，做到咬耳扯袖、红脸出汗。</p><p>【例】这也启示我们，提高治理能力和治理水平，既要坚持眼睛向下，善于倾听、从善如流，拓宽群 众反映利益诉求的渠道，更要善于携手共进，上下同心、协同发力，勇于尝试、大胆突破，最大限度释放 政策红利。</p><p>【例】相关方面认真倾听、从善如流、有则改之无则加勉，企业感到安全安心了，预期就好了，信心 就强劲了。优化营商环境，让作为用户的企业来实事求是地、善意地“找茬”“纠错”，是很有必要的。</p><blockquote><p>闻过则喜</p><p>过：过失；则：就。听到别人批评自己的缺点或错误，表示欢迎和高兴。指虚心接受意见。</p></blockquote><p>【例】对各种意见和批评，只要坚持党的基本理论、基本路线、基本方略，就要让大家讲，哪怕刺耳、 尖锐一些，我们也要采取闻过则喜的态度，做到有则改之、无则加勉。</p><p>【例】对同志的提醒批评，要闻过则喜、虚心接受。</p><p>【例】同时，以闻过则喜的态度，经常听取一线官兵的意见建议，及时调整抓工作的思路，始终做到 为党尽职尽责工作，为基层服务、为战斗力建设服务。处理好近和远的关系，科学思维是关键。</p><blockquote><p>虚怀若谷</p><p>虚：谦虚；谷：山谷。胸怀象山谷一样深广。形容十分谦虚，能容纳别人的意见。</p></blockquote><p>【例】要有平等待人、与人为善的真诚态度，有虚怀若谷、海纳百川的宽阔胸襟，力争把各方面的真 实意见掌握全、掌握准，进行反复研究、反复比较、择善而从。</p><p>【例】民主党派在提高政党协商水平中担负着重要责任，但中国共产党担负着首要责任，因为我们是 执政党，应该更加自觉地做到虚怀若谷、集思广益。</p><p>【例】领导干部要做好“守门员”，把控好组织成员的素质，在选人方面，要坚持“德才兼备”；在 育人方面，要将理论学习和实践锻炼相结合，以虚怀若谷的心态，广泛学习最新理论和知识，积极用新思 维、新思路和新方法去分析、解决新情况新问题。</p><table><thead><tr><th>遮掩错误</th><th>文过饰非、怙恶不悛、欲盖弥彰</th></tr></thead></table><blockquote><p>文过饰非</p><p>文、饰：掩饰；过、非：错误。用漂亮的言词掩饰自己的过失和错误。</p></blockquote><p>【例】只要我们党牢牢坚持立党为公、执政为民，牢牢坚持为中国人民谋幸福、为中华民族谋复兴， 不断检视自己，不掩饰缺点，不文过饰非，坚决同一切弱化党的先进性和纯洁性、危害党的肌体健康的现 象作斗争，就一定能够始终立于不败之地。</p><p>【例】领导干部要坚决反对事不关己、高高挂起，明知不对、少说为佳的庸俗哲学，坚决克服文过饰 。</p><p>【例】马克思主义政党的性质宗旨，决定了它始终代表最广大人民的根本利益，没有任何自己特殊的 利益，从来不代表任何利益集团、任何权势团体、任何特权阶层的利益。不谋私利就能谋根本、谋大利， 就能坚持真理、修正错误，敢于检视自身、常思己过，不讳疾忌医、不文过饰非，及时发现和解决自身存在的问题，就能摆脱一切利益集团、权势团体、特权阶层的“围猎”腐蚀，并向党内被这些集团、团体、 阶层所裹挟的人开刀。</p><blockquote><p>怙恶不悛</p><p>怙：依靠，依仗；悛：改过，悔改。坚持作恶，不肯悔改。</p></blockquote><p>【例】针对美国国会众议长佩洛西不顾中方一再警告，执迷不悟、怙恶不悛，悍然窜访中国台湾地区， 向“台独”分裂势力发出严重错误信号、严重违反一个中国原则和中美三个联合公报规定、严重冲击中美 关系政治基础、严重侵犯中国主权和领土完整，外交部驻港特派员公署发言人 3 日表示坚决反对和强烈谴 责。</p><p>【例】《联合布告》指出：“对土匪中的首要分子，务必归案法办，如能率众投降，交出武器，准予 将功折罪，保证其生命安全，从轻处理，或免予处分；对于胁从分子，只要脱离匪帮，交出武器，改恶从 善，准予回家生产，不咎既往；凡怙恶不悛，继续祸害人民的土匪，必须坚决消灭，并严惩首恶，决不姑 息。各地人民如能缉拿匪首、密报匪情，检举土匪隐藏的武器，协助剿匪有功者，分别予以奖赏。如有通 藏匪、庇匪行为的，一律严办。”</p><p>【例】台湾地区领导人无视“双少数执政”的现实，罔顾岛内主流民意，怙恶不悛、一意孤行，极力 渲染大陆对台“威胁”，煽动“反中抗中”，蓄意升高两岸对立对抗。</p><blockquote><p>欲盖弥彰</p><p>盖：遮掩；弥：更加；彰：明显。想掩盖坏事的真相，结果反而更明显地暴露出来。</p></blockquote><p>【例】经济工作有自身不可违背的客观规律，舆论引导不能说空话、说假话、说外行话，否则会适得 其反，必须坚持实事求是，是成绩就是成绩，是问题就是问题，承认问题并分析清楚、提出解决思路是市 场利好，遮遮掩掩反而欲盖弥彰。</p><p>【例】陈斌华说，台湾地区领导人在讲话中，刻意包装、欲盖弥彰地宣称“中华民国台湾是一个主权独立的国家”“中华民国与中华人民共和国互不隶属”、台湾是“我们国家的名称”。</p><p>【例】这完全是日本对其暗度陈仓发展航母、损害地区安全环境行径的欲盖弥彰。分析人士指出，通 过技术改造，两艘出云级驱逐舰能各搭载至少 10 架 F-35B战斗机，将成为具备较强攻击能力的轻型航母。</p><table><thead><tr><th>不考虑别人建议</th><th>刚愎自用、固执己见</th></tr></thead></table><blockquote><p>刚愎自用</p><p>愎：任性；刚愎：强硬固执；自用：自以为是。十分固执自信，不考虑别人的意见。</p></blockquote><p>【例】他已经预感到蒋介石不可靠，曾说蒋介石是一个刚愎自用之人，不可共事，这可能也是他退党的原因。</p><p>【例】钟松此人刚愎自用，自恃援榆有功，异常骄傲。他在 8 月 13 日进入榆林城，不仅不休整补充 部队，还把辎重留在榆林，14 日便马不停蹄地率 2 个旅南下，孤军冒进，扬言“一战结束陕北问题”。</p><p>【例】如果不先做学生，而是刚愎自用，先做先生，那就免不了误人子弟，装模作样。邓小平同志在 谈到家庭联产承包责任制的创新时强调：“绝不是一个人脑筋就可以钻出什么新东西来。”</p><blockquote><p>固执己见</p><p>顽固地坚持自己的意见，不肯改变。</p></blockquote><p>【例】但在承办法官前两次的耐心劝解下，张某与黄某仍然态度坚决、固执己见，对法律生效判决文 书拒不承认，认为自己不应该归还申请执行人闻某案涉土地。</p><p>【例】“老董呀，一辈子就是这个脾气，固执己见、不知变通，我们私下都叫他‘老古董’‘犟拐拐’， 认定了的事很难改变。”他的妻子裴清莲在一旁打趣道。</p><p>【例】甚至在家人提醒之下，一些老人仍固执己见，意识不到其中的问题，在偏执的路上越走越远。 看到类似现象的年轻人，不妨多多提醒家中的老人，让他们尽量远离一些对生活无意义乃至有害的手机 App。</p><table><thead><tr><th>彻底改变</th><th>洗心革面、脱胎换骨、改头换面</th></tr></thead></table><blockquote><p>洗心革面</p><p>洗心：指清除坏思想。革面：改变旧面貌。比喻彻底悔改。</p></blockquote><p>【例】一个文明开放的社会是容许犯错的，抵制劣迹网红并非要将其一棒子打死，问题在于他们复出 后的言行中毫无洗心革面的痕迹。</p><p>【例】受疫情等因素影响，一些经营者在守信方面出现了“无心之失”，又或在失信后洗心革面，他 们都渴望尽快恢复商誉，以健康状态获得更好的发展空间。</p><p>【例】“针对未成年人犯罪，惩罚不是目的，通过教育、感化、挽救让失足少年洗心革面、回归社会 做个有用的人才是司法的终极目标。”新北区法院院长任志清告诉记者。</p><blockquote><p>脱胎换骨</p><p>原为道教用语。指修道者得道以后，就转凡胎为圣胎，换凡骨为仙骨。现比喻通过教育，思想得到彻底改造。</p></blockquote><p>【例】昔日“天上不飞雀，地上不长草，风吹砂砾满地跑”的干沙滩，真的脱胎换骨成了金沙滩，闽 宁村升级成闽宁镇，村民收入翻了20 倍。</p><p>【例】老工业基地支柱产业要通过创新实现优化升级、脱胎换骨，进行深入改革创新，实现适应经济 新常态的战略性调整。</p><p>【例】经历改革成长的脱胎换骨，兵团国资国企焕发新活力，展现新气象，高质量发展的“龙头”高 高昂起。改在“真”处农业产业链托起致富梦顶层基层良性互动，就能最大程度聚合改革效能。</p><blockquote><p>改头换面</p><p>①原为佛教语。指众生在轮回中形变神不变。②指改变面目。③指改正错误。④比喻只改变形式，而 内容、实质不变。</p></blockquote><p>【例】反腐高压之下，“四风”的公开表现少了，但改头换面、隐形变异仍不少见。</p><p>【例】联合市场监管部门重点清理互联网改头换面、变相销售电子烟信息及虚假违法广告，强化短视频、营销号、自媒体等社交平台电子烟销售信息清理，推动互联网和电子烟生产经营企业落实主体责任， 主动清理相关信息，全面停止网络销售及广告宣传行为。</p><p>【例】以“智”提质万物互联 1G到 5G的变化，不仅是两代人的生活记忆，更是产业改头换面的历程。 2019 年 10 月 31 日，中国正式进入 5G商用时代。</p><hr><h3 id="【第三十二组】-改变与不变-18-个" tabindex="-1"><a class="header-anchor" href="#【第三十二组】-改变与不变-18-个"><span>【第三十二组】 改变与不变（18 个）</span></a></h3><table><thead><tr><th>不变</th><th>一成不变、亘古不变</th></tr></thead><tbody><tr><td>改变</td><td>改弦更张、改弦易辙</td></tr><tr><td>变化多，快</td><td>变幻莫测、瞬息万变</td></tr><tr><td>长时间繁荣昌盛</td><td>经久不衰、长盛不衰</td></tr><tr><td>变化巨大</td><td>今非昔比、天翻地覆、时过境迁、沧海桑田、斗转星移、 白云苍狗、恍如隔世</td></tr><tr><td>难以改变</td><td>根深蒂固、积习难改、积重难返</td></tr></tbody></table><ol><li>成语解释</li></ol><table><thead><tr><th>不变</th><th>一成不变、亘古不变</th></tr></thead></table><blockquote><p>一成不变</p><p>成：形成。指事物一形成就不再改变。多用于否定句中。</p></blockquote><p>【例】要做好提醒指示跟其他交通管理措施一样，长实线不是独立存在的交通标线，也并非一成不变。 “在交通管理领域，包括虚实线线型变化在内的交通标志和交通组织肯定是动态调整的。</p><p>【例】该研究发现地球地幔的运转模式并非一成不变：现代深俯冲板片可以进入下地幔以及接近全地 幔对流的格局只是地球演化近期的过渡状态，不完全代表地球早期的动力学特征。</p><p>【例】对于习近平经济思想的学理化阐释也应该是一个动态的、开放的、不断完善、不断发展的过程， 而不是一成不变的。</p><blockquote><p>亘古不变</p><p>从古至今永远也不会改变。多褒义</p></blockquote><p>【例】中国与巴基斯坦的友谊亘古不变。</p><p>【例】夜幕吞没了最后的红光，天地间，暮色勾勒出群山的轮廓。我明白自己什么也带不走，相较于 亘古不变的高原，我所驻留的时间多么不值一提。</p><p>【例】在绝大多数人心中，家乡到最后都会成为一个点，如同一座亘古不变的孤岛。</p><table><thead><tr><th>改变</th><th>改弦更张、改弦易辙</th></tr></thead></table><blockquote><p>改弦更张</p><p>更：改换；张：给乐器上弦。改换、调整乐器上的弦，使声音和谐。比喻改革制度或变更计划、方法。</p></blockquote><p>【例】中国特色社会主义道路，开拓于中国人民共同奋斗，扎根于中华大地，是给中国人民带来幸福 安宁的正确道路。无论遇到什么风浪，在坚持中国特色社会主义道路这个根本问题上都要一以贯之，决不 因各种杂音噪音而改弦更张。</p><p>【例】周武王灭商后，天下安定，声威显赫，八方来朝。有西蕃进贡一只名犬，召公奭（shì）担心 武王玩物丧志，便写了一篇题为《旅獒》的文章进行劝谏，并说出了上面那番警示的话。武王听从了召公 的劝告，改弦更张，守住了社稷。</p><p>【例】奉劝民进党当局改弦更张，不要再用一些政治话术欺骗岛内民众，只有真正从台湾民众福祉出 发，承认“九二共识”，以实际行动改善两岸关系，才是正途。</p><blockquote><p>改弦易辙</p><p>改弦：更换琴弦。易：改变。辙：车轮压过的痕迹，指道路。比喻改变方向或做法。同“更弦易辙” “改辕易辙”。</p></blockquote><p>【例】他接受了同志们的批评，从此改弦易辙，半年来工作大有起色。</p><p>【例】“台独”是一条走不通的死路，即将上任的台湾地区新领导人应分清轻重缓急，改弦易辙、回 头是岸。</p><p>【例】美方应该改弦易辙，选择与中方相向而行，相互尊重，公平竞争，和平共处。一个健康稳定的 中美关系不仅符合双方利益，也是国际社会的共同期盼。</p><table><thead><tr><th>变化多，快</th><th>变幻莫测、瞬息万变</th></tr></thead></table><blockquote><p>变幻莫测</p><p>变幻：没有规则地改变。变化奇特，不可预测。</p></blockquote><p>【例】早春的天气真是变幻莫测，刚刚还是风和日丽，现在却雨雪交加。</p><p>【例】如果说位于川西高原的阿坝州黄龙沟是一曲气势恢宏的交响乐章，那么丹云峡就是一道变幻莫 测的色彩画廊。</p><p>【例】三千里汉江的变幻莫测与中国时代风云变幻相互映衬，融入秦巴大山的美景、汉江航运的变迁 和独具特色的民俗遗风，展现出向伟大中华文学传统和现实主义文学的回归和久违的经典小说的神奇叙事 与诗意。</p><blockquote><p>瞬息万变</p><p>瞬：一眨眼。息：一呼吸。在极短时间里就发生很多变化。形容变化很多很快。</p></blockquote><p>【例】信息时代的标志是瞬息万变，要与一日千里的技术经济发展同步，要为攻坚克难的社会改革实践出力。</p><p>【例】战场态势瞬息万变，警惕智能化作战新挑战。</p><p>【例】港中大更特设通识教育，以拓宽学生视野及培养综合思考能力，使学生在瞬息万变的现代社会 中能内省外顾，成为出色的领袖人才，贡献社会。</p><table><thead><tr><th>长时间繁荣昌盛</th><th>经久不衰、长盛不衰</th></tr></thead></table><blockquote><p>经久不衰</p><p>①精神饱满的状态，身体健壮的外表，或幼年期到成年期之间的时期的精神特征；青年人的活力或朝 气。②形容某事或某人经历很长时间仍旧保持较高的旺盛状态事例。</p></blockquote><p>【例】玉文化，历经八千年经久不衰，无可替代，是源于玉之可贵；而玉之最可贵在于你用心养玉，最终玉来养心。</p><p>【例】年轻人的耐力经久不衰，能够适应长久的训练。</p><p>【例】走过二十二载，这一展会何以经久不衰？作为中国农业会展的金字品牌之一，长春农博会是吉 林省现代农业的展示窗口，而农业发展的底气与信心又为展会注入持久活力。底气与信心从何而来？</p><blockquote><p>长盛不衰</p><p>长久兴盛而不衰败，比喻长时间保持旺盛的势头。</p></blockquote><p>【例】国乒教练组竞聘也已展开，对标奥运 5 金的战略目标，长盛不衰将是一代代国乒人最大的追求。</p><p>【例】作为海上丝绸之路的始发港之一，广州港是我国唯一历经 2000 年长盛不衰的世界大港。</p><p>【例】习近平总书记在青海考察时指出：“在中华民族大家庭中，大家只有像石榴籽一样紧紧抱在一 起，手足相亲、守望相助，才能实现民族复兴的伟大梦想，民族团结进步之花才能长盛不衰。”</p><table><thead><tr><th>变化巨大</th><th style="text-align:left;">今非昔比、天翻地覆、时过境迁、沧海桑田、斗转星移、 白云苍狗、恍如隔世</th></tr></thead></table><blockquote><p>今非昔比</p><p>现在不能和过去相比。形容变化大。（多形容现在比过去好）</p></blockquote><p>【例】西藏医疗救治水平今非昔比。</p><p>【例】自古以来，人与海的对抗背后，恰恰体现了人类对于海洋的畏惧与无奈。然而近些年来，地球 海洋的整体含氧量下降了 2%，而低含氧量的沿岸海洋死区数量从 50 个左右增加到了近 500 个，海洋已然 今非昔比。</p><p>【例】11 月 1 日，在荣县老城区，看看今非昔比的蜕变。</p><blockquote><p>天翻地覆</p><p>覆：翻过来。形容变化巨大。也形容闹得很凶。</p></blockquote><p>【例】一首首经典歌曲、一段段精湛表演，让人们重温风云激荡、天翻地覆的发展奇迹。</p><p>【例】70 年砥砺奋进，我们的国家发生了天翻地覆的变化。</p><p>【例】这位见证西藏农业天翻地覆变化的老人，依然珍惜粮食，家里的餐桌上从来不见剩饭。他说： “攥在自己手里的糌粑，是幸福的味道。</p><blockquote><p>时过境迁</p><p>境：环境。迁：变迁。时间过去了，环境或情况也随之改变了。</p></blockquote><p>【例】时过境迁，我们往往只有回到经典作品产生的历史语境中，才能更清楚地看到其创新性之所在。</p><p>【例】每段生命，都有别离的那一天，如同东寨港的“石禁碑”，时过境迁，刻碑的人早已远去，碑 上的信念依然永续。</p><p>【例】1968 年 12 月，中国人自己设计建造的第一座铁路、公路两用长江大桥南京长江大桥建成通车。 这座举全国之力建成的“争气桥”，极大地鼓舞了中国人民自力更生建设国家的信心。时过境迁，今天的 南京，过江通道已从1 座增加至 11 处 13 条。</p><blockquote><p>沧海桑田</p><p>沧海：大海。桑田：种桑树的地。大海变为桑田，桑田变为大海。比喻世事变化非常大。</p></blockquote><p>【例】多年来，从无到有，从有到优，他们亲眼见证了一片又一片的大居发生了沧海桑田、翻天覆地 的巨大变化。</p><p>【例】18 项全球重要农业文化遗产，见证沧海桑田，蕴藏古人智慧，镌刻着中华民族的精神追求，融 生态美、农业美、人文美为一体。</p><p>【例】从茹毛饮血到发出“人生三问”，百万年沧海桑田，似乎倏忽而过。</p><blockquote><p>斗转星移</p><p>斗：北斗星。1.表示一夜之间时间的推移。后也指岁月流逝。</p></blockquote><p>【例】本次活动将读书现场搬到了新华书店阅览室，面对琳琅满目的书籍，浓厚的读书氛围迎面扑来， 干警们在历史长河中感受斗转星移，在社科文学中体味百态人生，在法律知识中思考实践与应用……伴随 着书页翻动的沙沙声，共赴一场阅读盛宴。</p><p>【例】百年岁月，斗转星移、潮起潮落。中国共产党历经风雨洗礼，正带领中国人民意气风发地奋进 在全面建设社会主义现代化国家、实现中华民族伟大复兴的新征程上。</p><p>【例】从酸溜溜、香辣辣、筋道道的擀面皮里品味关中宝鸡农村一户人家四十多年来从饥饿、贫穷、 挣扎到生存、发展、壮大的命运故事，展现出我国改革开放斗转星移、沧海桑田的巨大变化。</p><blockquote><p>白云苍狗</p><p>苍：青色，泛指青黑色。比喻世事变化无常。也作“白衣苍狗”。</p></blockquote><p>【例】白云苍狗，世事无常，悠悠时光看似漫长，不过是白驹过隙，忽然而已。</p><p>【例】白云苍狗，时过境迁，唯有对玉器的痴迷不变。</p><p>【例】时至今日，历经两个甲子，白云苍狗，风云变幻，比较研究的主题也随时代而变易，从解读中 国落后于西方的原因，到客观评判中西历史文化优劣、重塑文化自信，再到为中华民族复兴大业提供中外 历史镜鉴。</p><blockquote><p>恍如隔世</p><p>恍：仿佛；世：三十年为一世。仿佛隔了一个时代。指一种因人事或景物变化很大而引起的感触。</p></blockquote><p>【例】那里山大沟深、破屋烂衫，只能靠天吃饭，“种下一袋子，收上一帽子”。习近平总书记接过 照片仔细端详，不由感慨：“今非昔比，恍如隔世啊！”</p><p>【例】如今的青和居社区已是高楼林立、环境优美、配套齐全的现代化社区。习近平说，生活在今天 这个时代，同几十年前相比，真是沧桑巨变，恍如隔世。</p><p>【例】如今，两人再次忆起当时的情景，都有恍如隔世之感。</p><table><thead><tr><th>难以改变</th><th>根深蒂固、积习难改、积重难返</th></tr></thead></table><blockquote><p>根深蒂固</p><p>蒂：草木之根。比喻基础稳固。（中性）（侧重深）</p></blockquote><p>【例】美国执法领域存在着根深蒂固的种族偏见。</p><p>【例】一些西方媒体却罔顾事实真相、背离科学精神，恶意抹黑中国防疫政策调整，完全丧失了媒体 的职业操守，毫无公信力可言，这是其根深蒂固政治偏见的充分暴露。</p><p>【例】枪支改革推进缓慢，党争加剧立法分歧，枪支暴力问题在美国早已根深蒂固，悲剧或难以避免。</p><blockquote><p>积习难改</p><p>积习：指多年养成的习惯。长期形成的某种习惯，很难改变。</p></blockquote><p>【例】官僚腐败从古到今一直存在，积习难改，反腐倡廉的工作要艰难而持久地进行下去。</p><p>【例】“一些作风顽疾还没有彻底解决，形式主义、官僚主义问题积习难改、查而不绝，影响高质量 发展质效、损害群众利益的问题易发多发。建议把督导督办作为促进作风转变的有力举措，推动压力传导 一贯到底，推动上下同频共振，防止出现松劲、麻痹思想和前紧后松、上热下冷、高开低走等问题。”</p><p>【例】节日前后，“四风”最爱冒头。“节点”即是“考点”。节日将至，党员领导干部尤其要自觉 抵制不良风气侵蚀，在小事小节上严以修身、正心明道，严守纪律底线，坚决防范“节日腐败”，坚决破 除积习难改、隐形变异的顽瘴痼疾。</p><blockquote><p>积重难返</p><p>积：积习。返：返回，回头。积习深重，难以改变。多指长期存在的恶习，弊端已经发展到了难以革 除的地步。（侧重时间久）</p></blockquote><p>【例】一旦躺在过去的功劳簿上，安于现状、止步不前，就会暮气丛生、积弊日深，最终积重难返。</p><p>【例】当地舆论认为，美国枪支暴力问题积重难返，枪支泛滥严重威胁美国民众生命安全，控枪却成 为无解命题。</p><p>【例】在长期执政条件下，各种弱化党的先进性、损害党的纯洁性的因素无时不有，各种违背初心和 使命、动摇党的根基的危险无处不在，如果不严加防范、及时整治，久而久之，必将积重难返，小问题就 会变成大问题、小管涌就会沦为大塌方，甚至可能酿成全局性、颠覆性的灾难。</p><hr><h3 id="【第三十三组】-顺势造势-7-个" tabindex="-1"><a class="header-anchor" href="#【第三十三组】-顺势造势-7-个"><span>【第三十三组】 顺势造势（7 个）</span></a></h3><table><thead><tr><th>顺势</th><th>顺势而为、大势所趋、势在必行、随波逐流、因陋就简</th></tr></thead><tbody><tr><td>造势</td><td>推波助澜、顺水推舟</td></tr></tbody></table><ol><li>成语解释</li></ol><table><thead><tr><th>顺势</th><th>顺势而为、大势所趋、势在必行、随波逐流、因陋就简</th></tr></thead></table><blockquote><p>顺势而为</p><p>顺应形势做事情。</p></blockquote><p>【例】我们要因势而谋、应势而动、顺势而为，加快推动媒体融合发展，使主流媒体具有强大传播力、 引导力、影响力、公信力。</p><p>【例】要加快建设以实体经济为支撑的现代化产业体系，既要逆势而上，在短板领域加快突破，也要 顺势而为，在优势领域做大做强。</p><p>【例】高标准高质量推进数字中国建设，既要顺势而为，大力推进数字产业化、产业数字化，构建以 数据为关键要素的数字经济，加快推进数字政府、新型智慧城市、数字乡村建设，提升全民数字素养与技能；也要乘势而上，以数字化转型整体带动生产方式。</p><blockquote><p>大势所趋</p><p>大势：整个局势。趋：趋向。整个局势发展的方向。</p></blockquote><p>【例】台湾是我国领土不可分割的一部分，实现祖国和平统一，是大势所趋，人心所向。</p><p>【例】正值中越建立全面战略合作伙伴关系 15 周年之际，双方提升关系新定位可谓大势所趋、水到渠成，必将为两国关系开辟更加光明的前景。</p><p>【例】不论从何种维度来讲，调降存量房贷利率已是民心所向、大势所趋。</p><blockquote><p>势在必行</p><p>势：形势。客观形势决定必须这样做。</p></blockquote><p>【例】我相信农村不久就会出现一个全新的局面，一切恐怕都势在必行了。</p><p>【例】网络微短剧规范化可谓势在必行。只有相关部门制定更加详细的规范，落实监管责任，提升监 管能力，才有可能减少网络微短剧领域的诸多乱象。</p><p>【例】纪法教育已经成为关乎青年党员干部茁壮成长和健康发展的关键，加强青年党员干部纪法教育 势在必行。</p><blockquote><p>随波逐流</p><p>随：跟着。逐：追赶。随着波浪起伏，跟着流水漂荡。比喻自己没有主见，只是随着别人走。同“随 波逐浪”。</p></blockquote><p>【例】对于社会上有些不好的风气，我们的报纸不能听之任之，更不能随波逐流，而要善加引导，加 强正面的教育。</p><p>【例】即使现在“躺平”和“摆烂”之辞盛行，坚持越来越成为所谓难事，作为大学生的我们依然不 可随波逐流，得过且过。</p><p>【例】身处其中的我们更应看清世界发展之动向，不随波逐流，理应有自己的思考，在世界局势和中 国变化中实现自己的理想和信念。</p><blockquote><p>因陋就简</p><p>指在条件有限的情况下，利用现有简陋条件将就办事，强调适应环境而非追求完善。该成语褒贬取决 于“简”的合理性—灵活务实则褒，降低标准则贬。</p></blockquote><p>【例】坑道战、破袭战、掀帘战、地道战、地雷战、堡垒战、自卫队、联防队、工作队、武工队等因 陋就简因地制宜的战术战法层出不穷，都是沦陷区民众的发明创造，更属“兵民是胜利之本”的真实体现。</p><p>【例】当前部分幼儿园一味追求低投入、低成本，因陋就简，一部分则盲目追求高投入、高成本、“贪 大求洋”，进而导致普惠质量不高。笔者看来，其根本原因在于没有科学核定学前教育实际成本。</p><p>【例】针对这样的资源条件，这里的古人因陋就简，就地取材，对多种石材加以利用，开采方法简单 实用、不拘一格。</p><table><thead><tr><th>造势</th><th>推波助澜、顺水推舟</th></tr></thead></table><blockquote><p>推波助澜</p><p>澜：大的波浪。比喻推动、助长事物的声势及发展。（中性词）</p></blockquote><p>【例】在经济利益的驱动下，借助于网络、手机、微博等新媒体的推波助澜，各式广告无孔不入。</p><p>【例】至今秋十月，全国各铁路局承揽货物运输业务量和营业额均有不同程度的增长，让百姓客户享 受到高效能的运输服务，为新经济时期内的“货畅其流”推波助澜。</p><p>【例】少数国家奉行霸权强权，拉小圈子，立潜规则，搞干涉打压、“脱钩断链”，甚至对地区“三 股势力”推波助澜，目的就是企图压制“全球南方”战略自主，封堵新兴市场和发展中国家的振兴之路。</p><blockquote><p>顺水推舟</p><p>舟：船。顺着水流的方向推船。比喻顺应某个趋势说话办事。</p></blockquote><p>【例】在市场自然增长的时候，营销的作用是顺水推舟；在市场遭遇困境的时候，营销的作用是逆水 行舟，更需体现担当，展现价值。</p><p>【例】一些互联网公司和科技企业相中了这里的人口红利和成本优势，顺水推舟将部分研发机构和制造基地搬到了这里，带动了当地经济发展。类似的还有“好客山东”。</p><p>【例】以工代赈顺水推舟，村民家门口打工增收，30 多名村民还学会了建筑、电焊等技术。</p><hr><h3 id="【第三十四组】-自然会发生-5-个" tabindex="-1"><a class="header-anchor" href="#【第三十四组】-自然会发生-5-个"><span>【第三十四组】 自然会发生（5 个）</span></a></h3><table><thead><tr><th>事情发生合乎情理</th><th>理所当然、顺理成章、天经地义、水到渠成、顺其自然</th></tr></thead></table><ol><li>成语解释</li></ol><blockquote><p>理所当然</p><p>指从道理上讲，应该如此。</p></blockquote><p>【例】“我才知道，原来我在生活中遇到的很多事是可以拿起法律武器保护自己的，而不是一味地回 避或忍受，甚至认为理所当然。这个讲座真是为我送来了一场‘及时雨’！”活动结束后，张女士含着泪 感慨道。</p><p>【例】他们在海外受困遇险，生命和财产受到威胁，我们感同身受，理所当然竭尽全力相助。</p><p>【例】这是在中国这一国际法主体没有发生变化情况下的政权更替，中国的主权和固有领土疆域没有 改变，中华人民共和国政府理所当然地完全享有和行使中国的主权，其中包括对台湾的主权。</p><blockquote><p>顺理成章</p><p>意思是写文章、做事情顺着条理就能做好。比喻随着某种情况的发展而自然产生的结果。</p></blockquote><p>【例】这么看，两岸融合“这副担子”落在福建的肩上，顺理成章。两岸融合是福建一直在做的事情， 听起来有点儿“老调重弹”，但去年的情况还真不一样。</p><p>【例】小满预示着小麦等农作物籽粒将熟未熟，但毕竟正是青黄不接的时候，而田间地头的野菜正蓬 勃生长，旧时采食野菜度过饥荒，自然顺理成章。“苦苦菜，带苦尝，虽逆口，胜空肠。”</p><p>【例】各种音乐、文艺在丝路上，乃至中原地区，长期相互影响、借鉴、充实、融通，酝酿着微妙的 变局；等到隋唐大一统局面的到来，音乐、文艺会找寻合适的时机，发生重大而美妙的“化学反应”，而 文艺高峰就顺理成章地出现了。</p><blockquote><p>天经地义</p><p>指天地间理所当然而不能改变的道理；也指理所当然，不容置疑的问题</p></blockquote><p>【例】解放军维护国家主权和领土完整，开展有关军事行动天经地义。</p><p>【例】一个共产党员，要为党、为国家、为人民的事业奉献自己的一切，这是天经地义的，不需要任何理由！</p><p>【例】任何人没有凌驾于法律之上的特权，无论是何身份背景，只要违反法律，就必须受到应有惩治， 这在任何国家都是天经地义的。</p><blockquote><p>水到渠成</p><p>意思是当水流到某处时，自然会形成一条水道。这个成语用来比喻当条件成熟时，事情会自然成功， 并不需要强求。</p></blockquote><p>【例】上海市绿化管理指导站专家表示，近期发现的古树主要有两种情况：在持续“海选”中新发现 的古树；前期的古树名木资源普查中已被绿化部门关注，今年年纪到了，入选古树大家庭便水到渠成。</p><p>【例】正是凭借优质服务，兴业银行厦门分行赢得了有关部门的广泛认可，后续合作也就水到渠成。</p><p>【例】中国与乌拉圭建立全面战略伙伴关系是水到渠成。</p><blockquote><p>顺其自然</p><p>意思是顺着事物本来的性质自然发展。</p></blockquote><p>【例】这是一本在微信群“首发”的书，先前没有周密的计划，启动后也没有详细的提纲，完全是“草 鞋没样，边打变像”的过程，一切都是顺其自然、水到渠成的。</p><p>【例】“比如对待长辈，我用同心话喊‘姨娘’，拉近关系，用心解答老人们关心的养老金、医保报 销等问题，只要真诚，很多事就会顺其自然”。信任不是突如其来的“破壁”，而是日积月累的信服。</p><p>【例】目前阶段，不能急功近利，训练中主要还是打好基础，至于成绩，顺其自然就好。新疆体育局 体育训练一大队田径项目短跳组主教练张洋对记者说。</p><hr><h3 id="【第三十五组】提早准备和补救-10个" tabindex="-1"><a class="header-anchor" href="#【第三十五组】提早准备和补救-10个"><span>【第三十五组】提早准备和补救（10个）</span></a></h3><table><thead><tr><th>前期准备</th><th style="text-align:left;">未雨绸缪、常备不懈、防患未然、防微杜渐、居安思危、 曲突徙薪</th></tr></thead><tbody><tr><td>准备得当没有忧虑</td><td style="text-align:left;">高枕无忧</td></tr><tr><td>出现问题进行补救</td><td style="text-align:left;">临渴掘井、江心补漏、亡羊补牢</td></tr></tbody></table><ol><li>成语解释</li></ol><table><thead><tr><th>前期准备</th><th style="text-align:left;">未雨绸缪、常备不懈、防患未然、防微杜渐、居安思危、 曲突徙薪</th></tr></thead></table><blockquote><p>未雨绸缪</p><p>绸缪：缠绕，引申指修缮。比喻事先做好防备工作。</p></blockquote><p>【例】面对错综复杂的宏观经济环境，我们既要看到推动经济发展的有利条件，坚定信心，积极作为， 又要充分估计影响经济发展的不利因素，未雨绸缪，防范风险。</p><p>【例】前进道路上，自觉用习近平新时代中国特色社会主义思想武装头脑，善于运用这一重要思想防 范化解重大风险，增强底线思维，做到未雨绸缪，我们一定能把我国发展进步的命运牢牢掌握在自己手中，推动中国式现代化劈波斩浪。</p><p>【例】防范城市内涝，郫都区同样未雨绸缪。“每年夏季的强降雨，郫温路下穿成灌高速处都会因雨 水排放不畅而发生路面积水，影响有轨电车运行及行人车辆通行安全，存在防汛隐患。”</p><blockquote><p>常备不懈</p><p>意思是时刻准备着，毫不松懈。</p></blockquote><p>【例】要改革完善疾病预防控制体系。坚决贯彻预防为主的卫生与健康工作方针，坚持常备不懈，将 预防关口前移，避免小病酿成大疫。要健全公共卫生服务体系。</p><p>【例】要强化责任担当，真抓实备，常备不懈，确保一旦有事能上得去、打得赢。要坚持实战实训、 联战联训，加强对抗性、针对性、协同性训练，提升训练质量和水平。</p><p>【例】要深入贯彻落实习近平总书记关于疫情防控的重要指示精神，切实把思想和行动统一到党中央 决策部署上来，坚持人民至上、生命至上，坚持外防输入、内防反弹，突出防控重点、抓牢关键环节，进 一步提高工作的预见性、前瞻性和大局观，下好“先手棋”、打好“主动仗”，常备不懈提升疫情应急处 置能力。</p><blockquote><p>防患未然</p><p>患：灾祸。未然：没有这样，指没有发生。在祸患还没有发生之前就进行防备。</p></blockquote><p>【例】治理宰客还应防患未然。</p><p>【例】把问题想得充分些，把准备做得精细些，把预案做得周全些，只有防患未然，把“丰收在望”变成“丰收到手”，“小满”方得“圆满”。原熟，天下足。“打赢‘三夏’攻坚战，争取丰产又丰收！”</p><p>【例】信息安全攻防战将是一场长期博弈，必须始终坚持安全至上的理念，以先进的信息安全技术手 段为支撑，以科学完备的法律规范为制约，对军事数据实施全方位、全周期动态管理，防患未然、警钟长 鸣。信息安全，人人有责。</p><blockquote><p>防微杜渐</p><p>微：细小，指事物的苗头。杜：杜绝，堵塞。渐：事物的开头。指在不良的事物刚刚露出苗头时，就 加以制止和杜绝，不让它发展下去。</p></blockquote><p>【例】安全生产，关键在于防患于未然、防微杜渐，把安全隐患发现在前、解决在先。</p><p>【例】丹江街道纪工委选取《二零二三年查处严重违纪违法党员干部忏悔录》中的典型案例，以案释 法，开展廉洁从业警示教育，强化党员干部纪法意识，增强纪法思维，做到防微杜渐，警钟长鸣。![ref54]</p><p>【例】党员干部要以在全党开展党纪学习教育为契机，深刻认识严守生活纪律的重要意义、全面把握 生活纪律的内在要求，坚持把生活纪律融入日常、抓在经常，防微杜渐，不断增强政治定力、纪律定力、 道德定力、抵腐定力，始终老老实实做人。</p><blockquote><p>居安思危</p><p>处在安定的环境时而能想到可能会出现的危难。</p></blockquote><p>【例】居安思危，思则有备，有备无患。</p><p>【例】“君子以惩忿窒欲”，即使身处顺心的境遇，也不忘居安思危。“君子以思患而豫防之”，以 长远的眼光判断形势变化，做好危机的预防和处理，“是故君子，安不忘危，存不忘亡，治不忘乱，是以身安而国家可保也”。</p><p>【例】新中国成立后，党中央居安思危，高度重视党的工作纪律建设。1949 年 11 月，中共中央发出《关于成立中央及各级党的纪律检查委员会的决定》。随即，从中央到地方县以上、军队团以上的各级纪 律检查委员会相继成立。</p><blockquote><p>曲突徙薪</p><p>原义是把烟囱改建成弯的，把灶旁的柴草搬走；比喻事先采取措施，才能防止灾祸；在句子中可充当 作谓语、定语。</p></blockquote><p>【例】一定要把排查放在首位，曲突徙薪，未雨绸缪，防患于未然。悦来投资集团领导高度重视隐患 排查工作。</p><p>【例】见兔顾犬、亡羊补牢，是为下策；积谷防饥、曲突徙薪，方为上策。</p><p>【例】且料定新疆来日必然生事，急宜曲突徙薪，预为之计。林则徐一口气说到这里，突然起身激动 地说：西北与新疆乃是中华安定的屏障，与国运息息相关，吾数年来留心人才，欲将此重任托付。</p><table><thead><tr><th>准备得当没有忧虑</th><th>高枕无忧</th></tr></thead></table><blockquote><p>高枕无忧</p><p>垫高枕头睡觉，无所忧虑。</p></blockquote><p>【例】今年雨水大，不要以为大堤修整过一次，就可以高枕无忧了。</p><p>【例】城西的大堤上新建了大闸，调节里面的水位，使得每个内湖都能一年四季荡漾轻波细浪，市民 们这才高枕无忧。一项新的决策更是应运而生：开挖几条河道，把湖泊连成一体，使得全城步步见水、家 家亲水。</p><p>【例】虽然我国粮食连年丰收，并不意味着可以高枕无忧。需要注意的是，2015 年粮食产量突破 1.3 万亿斤，并在之后连续 9 年稳定在 1.3 万亿斤以上。</p><table><thead><tr><th>出现问题进行补救</th><th>临渴掘井、江心补漏、亡羊补牢</th></tr></thead></table><blockquote><p>临渴掘井</p><p>临；接近。感到口渴才挖井。比喻平时没有准备，事到临头才想办法。</p></blockquote><p>【例】“临渴掘井”，拖到“问题扎堆”再改革万万要不得。</p><p>【例】第二，为了能够对付临时的紧急任务，完成与自己研究无关或关系不大的文章，必须平时多多 接触实际、联系群众，注意时事、研究政策，广泛阅读书报、随时积累材料，以免临渴掘井，手足无措。</p><p>【例】等不来，要不来，“临渴掘井”讨不来。平日里营造好创新氛围，悉心呵护科创企业，才能在 关键时刻涌出汩汩活水、汇起千顷澄碧。</p><blockquote><p>江心补漏</p><p>指船到江心才补漏洞。比喻临到紧急关头才设法补救，为时已晚。偏正式结构；在句中一般作谓语、定语。</p></blockquote><p>【例】事态已严重到了这种境地，你再怎么补救也是江心补漏，为时已晚。</p><p>【例】当海马汽车仍在复制马自达老路，毫无研发突破之时，彼时的吉利已收购沃尔沃、奇瑞自研发 动机打破技术壁垒，而海马汽车的技术储备已严重不足。等到海马意识到自主研发的重要性时，犹如江心补漏，已经来不及了。</p><p>【例】领导干部读书学习水平，在很大程度上决定着工作水平和领导水平。要想获得做官、做人、做 事的不竭动力，就要善于学习。关键要掌握理论知识、政策知识、法律知识、业务知识，培养哲学素养、 历史素养、文学素养。这样才会厚积而薄发，形成“一桶水和一滴水”关系，否则“书到用时方恨少”“船到江心补漏迟”。</p><blockquote><p>亡羊补牢</p><p>意指丢失了再去修补羊圈，还不算晚。后用来比喻出了问题以后及时想法补救，以免继续受损失。也 指出了问题才想法补救，已经太晚了。</p></blockquote><p>【例】纠正也好，改进也罢，都是“亡羊补牢”。说出口的话犹如泼出去的水，即便马上收回，可能 已造成严重的影响。</p><p>【例】亡羊补牢，为时未晚。</p><p>【例】“我们办理案件，既要亡羊补牢，办准、办好已经发生的案件，又要未雨绸缪，以案为鉴，预 防和减少这类案件的发生……”近日，在河南省新蔡县检察院第一检察部业务分析会上，该部主任张芯华 结合自己办理的李某诈骗案深有感触地说。</p><p>【例】李鹏指出，民进党当局必须尽快公布事实真相，满足遇难者家属合理诉求，并郑重道歉，才可 能亡羊补牢，平复两岸同胞义愤填膺的情绪。</p><hr><h3 id="【第三十六组】-办公、管理相关-6-个" tabindex="-1"><a class="header-anchor" href="#【第三十六组】-办公、管理相关-6-个"><span>【第三十六组】 办公、管理相关（6 个）</span></a></h3><table><thead><tr><th style="text-align:center;">形容工作开展的局面、 形势不好</th><th>尾大不掉、各行其是、各自为政</th></tr></thead><tbody><tr><td style="text-align:center;">形容人工作的状态</td><td>如鱼得水、面面俱到、独当一面</td></tr></tbody></table><ol><li>成语解释</li></ol><table><thead><tr><th style="text-align:center;">形容工作开展的局面、 形势不好</th><th>尾大不掉、各行其是、各自为政</th></tr></thead></table><blockquote><p>尾大不掉</p><p>掉：摇动，摆动。比喻属下势力强大，不服从指挥调度。也比喻事情前轻后重，难以驾驭的现象。</p></blockquote><p>【例】从晚清到民国，国家的行政体制始终未能一致，指臂不灵，尾大不掉，中央政府始终不能有效 地号令全国。</p><p>【例】拼贴、印章、题跋与过度装饰问题等，往往面临着“法不责众”与“尾大不掉”的难题。 【例】由此，美国财政赤字不断膨胀，财政纪律逐步废弛，美债遂呈尾大不掉之势。</p><blockquote><p>各行其是</p><p>指各人按照自己认为正确的去做。</p></blockquote><p>【例】各自为政、各行其是，党就会变成一盘散沙，党的领导就会成为一句空话。</p><p>【例】推动各领域各方面改革举措同向发力、形成合力，增强整体效能，防止和克服各行其是、相互 掣肘的现象。</p><p>【例】自觉在思想上政治上行动上同党中央保持高度一致，及时校正偏差，不打折、不变通、不走样， 决不能各行其是、各自为政。</p><blockquote><p>各自为政</p><p>各人按自己的主张办事。（多用来形容工作，单位之间、部门之间）</p></blockquote><p>【例】各部门互相协作，互相支援，消除了过去一定程度上存在的各自为政的现象。</p><p>【例】自觉在思想上政治上行动上同党中央保持高度一致，及时校正偏差，不打折、不变通、不走样， 决不能各行其是、各自为政。</p><p>【例】“大课程观”的树立，要求学校、家庭、社会三者从各自为政走向密切配合，实现同向同行。</p><table><thead><tr><th>形容人工作的状态</th><th>如鱼得水、面面俱到、独当一面</th></tr></thead></table><blockquote><p>如鱼得水</p><p>好像鱼得到水一样。比喻得到跟自己非常投合的人或对自己很适合的环境。</p></blockquote><p>【例】优化营商环境，让各种类型的经营主体“如鱼得水”。不止在南京，江苏各地积极行动。</p><p>【例】全省市场监管系统将坚持稳字当头、稳中求进，着力营造一流营商环境，服务构建全国统一大 市场，加快建设质量强省和知识产权强省，让各类市场主体在江苏如鱼得水、如鸟归林。</p><p>【例】政策“红利”换来企业效益“增量”，务实举措让市场主体“如鱼得水”。</p><blockquote><p>面面俱到</p><p>各方面都照顾得很周到。也指不仅各方面都照顾到，而且每一个方面都处理得很得当。</p></blockquote><p>【例】多措并举、面面俱到，才能把一个个问号拉直，让辛苦打拼一年的人们收获满满的获得感和幸福感。</p><p>【例】工作多年，他依然是大家眼中那个面面俱到的“细节怪”。</p><p>【例】展览的空间是有限的，不能面面俱到，只能通过一次次展览向观众呈现其所蕴含的丰富信息。</p><blockquote><p>独当一面</p><p>意思是指单独负责一个方面的工作或任务。</p></blockquote><p>【例】与地勤机械师多人合作的工作形式不同，每趟动车的随车机械师只有一人，必须独当一面，特 别是在遇到突发情况的时候。</p><p>【例】既有理科的基础，也有工科的动手能力，还能面向交叉领域，视野开阔，独当一面，这正是我 们新工科人才的培养目标。</p><p>【例】他明显感受到了自己的飞速成长，从一个只会纸上谈兵的职场小白成长到能够独当一面。</p><hr><h3 id="【第三十七组】-空谈与虚幻-8-个" tabindex="-1"><a class="header-anchor" href="#【第三十七组】-空谈与虚幻-8-个"><span>【第三十七组】 空谈与虚幻（8 个）</span></a></h3><table><thead><tr><th>不靠谱的做法</th><th>纸上谈兵、无根之木、坐而论道、水中捞月、画饼充饥</th></tr></thead><tbody><tr><td>实现不了</td><td>空中楼阁、镜花水月、海市蜃楼</td></tr></tbody></table><ol><li>成语解释</li></ol><table><thead><tr><th>不靠谱的做法</th><th>纸上谈兵、无根之木、坐而论道、水中捞月、画饼充饥</th></tr></thead></table><blockquote><p>纸上谈兵</p><p>兵：用兵之道。比喻空谈理论，不能解决实际问题。（侧重只有理论而没有行动）</p></blockquote><p>【例】书生都喜欢纸上谈兵，只说而不去实行。</p><p>【例】脱贫攻坚、乡村振兴从来不是“纸上谈兵”，而是深入全国各地，落在了每家每户的生活实处。</p><p>【例】搞研究不能“纸上谈兵”，只有真正了解非洲，才能真正做出实事。每到一个国家，我们都坚 持“沉下去”，了解当地居民在农业上存在的实际困难与需求。</p><blockquote><p>无根之木</p><p>意思是比喻没有根据或基础的事物。</p></blockquote><p>【例】技术源于思想，切断思想，技术就沦为无根之木，非死不可。</p><p>【例】奋斗不是无根之木、无源之水，只有立根在祖国和人民最需要的地方，青春才能绽放绚丽之花。 奋斗也不只是响亮的口号，而是要在做好每一件小事、完成每一项任务、履行每一项职责中见精神、显担 当。</p><p>【例】正因如此，我们的一切奋斗才不是无水之萍、无根之木，而是有着坚实的群众基础。</p><blockquote><p>坐而论道</p><p>意思是坐着空谈大道理。指口头说说，不见行动。</p></blockquote><p>【例】坚定文化自信不能止步于坐而论道，必须体现在文化自强的行动上。</p><p>【例】两会治霾，既要坐而论道，更待起而行之。</p><p>【例】党把干部放在各个岗位上是要大家担当干事，决不能坐而论道、光说不练。</p><blockquote><p>水中捞月</p><p>指在水中捞月亮；比喻去做根本做不到的事情，只能徒劳无功。含贬义</p></blockquote><p>【例】美国人眼里只有自身利益，民进党当局想依靠对美军购从根本上改变台军装备匮乏、军力羸弱 的局面，不过是画饼充饥、水中捞月。</p><p>【例】目前中国的护士总量严重不足，质量也不高，在这样的前提下，网约护士试点实施起来像是水中捞月一样。</p><p>【例】对“正确”育儿方式的追求本身就是水中捞月，那些所谓“过去几个世纪以来主流的育儿方式” 实际上并非主流，只不过它们已经历史足够悠久，又重新被现代的人发掘和使用。</p><blockquote><p>画饼充饥</p><p>画个饼子来解饿。本比喻徒有虚名而于实际无好处。后多用来比喻以空想来安慰自己。</p></blockquote><p>【例】冲绳国际大学教授前泊博盛表示，虽然日美达成了《日美地位协定》有关环境问题的补充协定， 但日方进入美军基地调查需要得到美方许可，同时美方没有义务对日方实地调查要求作出回应，这无异于 “画饼充饥”。</p><p>【例】如果像有的国家那样政党纷争，只说不做，朝令夕改，再好的蓝图也只是画饼充饥、空中楼阁。 中国式现代化进程，是世界和平、正义、进步力量的壮大。</p><p>【例】而蔡英文六年前就职演说，提到要为年轻人打造一个更好的台湾，但现在碳费 2024 年才要课 征，净零路径 8 年 8800 亿元，推给下一届才要开始编列预算，可见当局对年轻人的承诺，只是画饼充饥。</p><table><thead><tr><th>实现不了</th><th>空中楼阁、镜花水月、海市蜃楼</th></tr></thead></table><blockquote><p>空中楼阁</p><p>悬在空中的楼阁。比喻崇高通达。后多用来比喻虚构的事物或脱离实际的空想。</p></blockquote><p>【例】反观今天的中国篮球、足球等大球项目，关注职业联赛的“球评家”遍地，多是空中楼阁、纸 上谈兵，真正呕心沥血致力于基层普及和幼苗培养的人寥寥无几。</p><p>【例】必须深刻认识到，不注重抓落实，不认真抓好落实，再好的规划和部署都会沦为空中楼阁。</p><p>【例】产业发展不是空中楼阁，特别是对特色产业来说，首先必须立足自身条件和优势。新疆乌伦古 湖，渔民身穿皮袄、脚踩厚靴，在冰封湖面上凿洞下网，吸引无数游客前来观赏体验。</p><blockquote><p>镜花水月</p><p>镜子里的花，水里的月亮。比喻虚幻的东西。</p></blockquote><p>【例】4K似乎正在沿着 3D的足迹，成为看上去很美却让消费者够不到摸不着的镜花水月。</p><p>【例】野生动物跟人类在城市共存的话题并不是镜花水月，而是每天都在切实发生。用源自身边的小 视角，打开环保叙事的大视野，《一路前行》让观众不自觉地走入到生态保护的语境中。</p><p>【例】对于领导干部来说，不谋远，工作就会缺乏预见性和前瞻性，犹如盲人骑瞎马、夜半临深池； 不顾近，对当下形势和情况不了解，缺乏善作善为的能力，再好的蓝图也可能成为镜花水月、空中楼阁。</p><blockquote><p>海市蜃楼</p><p>比喻虚无缥缈而不实际存在的事物，也比喻不可企及的虚无的梦想。</p></blockquote><p>【例】沈阳雾霾指数为中度污染，市区部分地区如“海市蜃楼”。</p><p>【例】奋斗，无需固执于海市蜃楼般空洞的宏伟“字眼”，新时代的广大青年，当以脚步丈量祖国的 每一寸土地，感知这个盛世的来之不易；用眼睛发现赓续于血脉中的中国精神，坚定那鲜血与汗水浇灌出 的信念</p><p>【例】泉州市区上空被一层薄薄的雾笼罩着，在清源山上往市区看，高高的紫帽山被雾遮挡了一大半， 只露出一个山顶，恍若“海市蜃楼”。</p><hr><h3 id="【第三十八组】-来者众多-13-个" tabindex="-1"><a class="header-anchor" href="#【第三十八组】-来者众多-13-个"><span>【第三十八组】 来者众多（13 个）</span></a></h3><table><thead><tr><th>形容到来</th><th>接踵而至、络绎不绝、纷至沓来、蜂拥而至</th></tr></thead><tbody><tr><td>形容人多、人少</td><td>门庭若市、无人问津、门庭冷落、门可罗雀、人迹罕至</td></tr><tr><td>偶遇</td><td>不期而遇、不期而至、萍水相逢</td></tr><tr><td>一同出现</td><td>如影随形</td></tr></tbody></table><ol><li>成语解释</li></ol><table><thead><tr><th>形容到来</th><th>接踵而至、络绎不绝、纷至沓来、蜂拥而至</th></tr></thead></table><blockquote><p>接踵而至</p><p>意思是指人们前脚跟着后脚，接连不断地来，形容人接连而来或事情持续发生。</p></blockquote><p>【例】这些年来，我们面临的各种风险挑战接踵而至，大仗一个接一个，每一仗都是靠全体人民团结 奋斗、顽强斗争闯过来的。</p><p>【例】深圳将加快推动体育事业和体育产业高质量发展，品牌赛事、全民健身活动接踵而至，城市活 力跃动、生机勃发的景象将全面展开。</p><p>【例】今年以来，各国政要密集访华，中国发展高层论坛、博鳌亚洲论坛、中国国际消费品博览会、 中国进出口商品交易会、2023 年服贸会先后举办，国际宾客接踵而至……</p><blockquote><p>络绎不绝</p><p>络绎：前后相接、连续不断的样子。形容车船人马等前后相接，往来不断。</p></blockquote><p>【例】每年到了这时候，从全国各地，跋山涉水特意来韶山参观的人都络绎不绝。 【例】莫斯科动物园，前来观赏大熊猫“如意”和“丁丁”的游客络绎不绝。</p><p>【例】位于沅江江畔的常德河街历史悠久，惜乎毁于战火。近年来，常德复原老河街风貌，成为重要 的文化展示窗口。老街焕发新生，游客络绎不绝。</p><blockquote><p>纷至沓来</p><p>纷：众多。沓：又多又重复。形容连续不断地纷纷到来。</p></blockquote><p>【例】旷日持久的总统选举，致使阿富汗国内局势受到严重影响，诸多挑战纷至沓来。</p><p>【例】国庆档之后，各种类型片纷至沓来，令人目不暇接。</p><p>【例】世界超市浙江义乌国际商贸城龙年新春开市后，各地客商纷至沓来，洽谈采购小商品，市场人 气兴旺，一片繁荣景象。</p><blockquote><p>蜂拥而至</p><p>像一窝蜂似地一拥而来。形容很多人乱哄哄地朝一个地方聚拢。</p></blockquote><p>【例】尽管当时很多村民听不懂汉语对白，每当有电影在村子放映，十里八乡的人依然会蜂拥而至。</p><p>【例】位于南京市建邺区的紫金 1912·玖悦荟港风夜市，在 2023 年刚开业时曾一度成为南京最热门 的夜市和网红打卡点之一，不仅消费者蜂拥而至，也吸引了“郭阿姨月亮馍”和“朱姐烤苕皮”等众多南 京网红摊主入驻。</p><p>【例】在加盟店遍布全国 300 多个城市基础上，现在有意向加盟淮北碳锅的申请更是蜂拥而至。”淮 北市碳锅协会负责人任爱全坦言，未来将淮北碳锅推广到五湖四海，他们信心满满。</p><table><thead><tr><th>形容人多、人少</th><th>门庭若市、无人问津、门庭冷落、门可罗雀、人迹罕至</th></tr></thead></table><blockquote><p>门庭若市</p><p>门庭：门口和庭院。若：如，好像。门口和庭院就像集市一样，热闹非凡。形容往来的人很多。</p></blockquote><p>【例】早上 8 点，在上海中华路的梦花街馄饨店门口早已门庭若市。</p><p>【例】艺术教育普遍存在“两头热中间冷”的问题——在很多地方，少儿艺术教育琳琅满目，老年大 学门庭若市，唯独处于中间地带的成年人艺术教育几近空白。</p><p>【例】畲乡还经历了从“人迹罕至”到“门庭若市”的蜕变。近年来，当地大力发掘红色文旅、绿色 产业、蓝色畲乡的三色资源，举办畲族特色活动，打造国家森林公园、省级森林康养基地，2023 年，吸引 游客十万余人次。</p><blockquote><p>无人问津</p><p>津：渡口。没有人来询问渡口。比喻没人过问、受到冷落。（形容某事物受冷落、无人理睬。程度较重）</p></blockquote><p>【例】那些书皮五光十色的侠怪小说，渐渐变得无人问津。</p><p>【例】居民对精神文化需求比较强烈，而仅有的一处休闲健身苑点处，部分宣传墙面脱落，休闲座椅 破损，个别居民还会在周边乱扔垃圾，久而久之，这里成了无人问津之地。</p><p>【例】曾经无人问津的“空心村”，在创业青年、年轻艺术家的改造运营下，成为精致考究的民俗村。</p><blockquote><p>门庭冷落</p><p>形容十分冷落，宾客稀少。</p></blockquote><p>【例】门庭冷落、乏人问津，广州 4 家民俗博物馆的烦恼，想必能寻觅到不少知音。</p><p>【例】近来不少仿古镇经营冷淡，这些靠仿造景观售卖高价门票的仿古镇门庭冷落、渐显颓势，一些 仿古镇正在被年轻人抛弃。</p><p>【例】进入新世纪，国内图书市场生机勃勃，但马克思主义哲学通俗读物却“门庭冷落”。</p><blockquote><p>门可罗雀</p><p>罗：张网捕捉。大门之前可以张起网来捕麻雀。形容十分冷落，宾客稀少。</p></blockquote><p>【例】她发现，尽管近年来“博物馆热”成为热门话题，一些大博物馆一票难求成为常态，但是许多 中小博物馆仍然存在门可罗雀的情况，当地人看到有外地游客到博物馆参观还会说“还有人到这里？”</p><p>【例】作为科技创新的孪生姊妹和创新发展的一体两翼，科普要求通俗易懂，但有些科普文章堪比专 业论文，常常让人“丈二和尚摸不着头脑”，部分科普场馆、科普展览高端大气，却往往门可罗雀。</p><p>【例】走进复兴集镇的老街，只见昔日门可罗雀的老供销社又恢复了往日“门庭若市”的热闹场景， 顾客们争相购物，不一会货架上的商品被一抢而空。“变‘闲’为‘现’，物尽其用。”</p><blockquote><p>人迹罕至</p><p>罕：少。指偏僻荒凉、少有人到的地方。（侧重偏僻荒凉）</p></blockquote><p>【例】历史上曾人来人往的丝绸古迹，如今已变成人迹罕至的沙漠。</p><p>【例】过去，人们若想目睹全缘叶绿绒蒿的风姿，往往需要长途跋涉，深入高山峻岭之间，在那些人迹罕至的地方，才有机会与它不期而遇。</p><p>【例】很多田野文物都在人迹罕至的地方，部分古遗址、古墓葬所处位置甚至无路可达。</p><table><thead><tr><th>偶遇</th><th>不期而遇、不期而至、萍水相逢</th></tr></thead></table><blockquote><p>不期而遇</p><p>没约定而意外相遇。</p></blockquote><p>【例】广大青年不仅是现在的生力军，也是未来的主力军。同向同心，当代青年与最鲜明的时代主题 不期而遇。对当代青年来说，这是最大的人生际遇，也是最大的人生考验。</p><p>【例】相比吴贺玲母子的满怀遗憾，来自滦县榛子镇的李震云姐妹则在心里庆幸同习近平总书记的不期而遇。</p><p>【例】扎根人民，才能和正能量不期而遇。</p><blockquote><p>不期而至</p><p>指事先没有约定而意外到来，没有预料地到来。</p></blockquote><p>【例】面对不期而至的螃蟹卡，很多人都忍不住想打开看一看，稍不留神就可能一步步落入骗子精心 设置的圈套。</p><p>【例】那些“不期而至”的过敏性皮肤病。</p><blockquote><p>萍水相逢</p><p>比喻从不认识的人偶然相遇。</p></blockquote><p>【例】我所衷心敬佩的人，不是那种什么事都可以找兄弟帮忙、仿佛大半个中国都有他哥们的人，而 是那种跟所有萍水相逢的人都友善宽和、时时都在传播着正能量的人。</p><p>【例】这些年，浙江小百花越剧团深入基层演出时，在当地引发了热烈反响；出国交流时，收获了外 国友人热烈的掌声和喝彩。我时常能在观众席看到一些熟悉的面孔，有一些观众不远万里赶来看一场戏； 有的观众，愿意自己花钱，请萍水相逢的学生戏迷看戏。</p><p>【例】《爱的教育》中，把爱比成很多东西，的确是这样，又不仅仅是这些。我想，“爱是什么？” 不会有明确的答案，但我明白，“爱”是没有限制的。同学之间的友好交谈，老师对学生的鼓励，父母对 孩子无微不至地关爱，甚至萍水相逢的人们的一个微笑……</p><table><thead><tr><th>一同出现</th><th>如影随形</th></tr></thead></table><blockquote><p>如影随形</p><p>好像影子总是跟着身体一样。比喻两个人关系亲密，常在一起。</p></blockquote><p>【例】监督和约束如同行驶中的安全带和登山时的防护索，是一种对党员干部如影随形的爱护。</p><p>【例】然而，随着网络市场的迅速扩张，不正当竞争行为也如影随形，出现了利用数据、算法、平台 规则以及网络技术实施的不正当竞争行为。山寨模仿、虚假宣传、网络黑公关等行为严重扰乱了市场竞争 秩序，影响了市场公平交易。</p><p>【例】税助发展之“安心服务”“2023 年 2 月份园区开工建设以来，税务部门工作团队一直如影随形， 我们一期工程成功引进了 9 家航空配套制造企业，其中 2 家已经投产。</p><hr><h3 id="【第三十九组】-人多人少-9-个" tabindex="-1"><a class="header-anchor" href="#【第三十九组】-人多人少-9-个"><span>【第三十九组】 人多人少（9 个）</span></a></h3><table><thead><tr><th>形容人多，热闹</th><th>熙熙攘攘、人山人海、车水马龙、川流不息、万人空巷</th></tr></thead><tbody><tr><td>形容孤独</td><td>孑然一身、茕茕孑立、形影相吊、形单影只</td></tr></tbody></table><p>1.成语解释</p><table><thead><tr><th>形容人多，热闹</th><th>熙熙攘攘、人山人海、车水马龙、川流不息、万人空巷</th></tr></thead></table><blockquote><p>熙熙攘攘</p><p>熙熙：和乐的样子。攘攘：乱纷纷的样子。形容人来人往，纷杂拥挤。</p></blockquote><p>【例】集市上熙熙攘攘，热闹非凡。</p><p>【例】夜晚的上海更是别有一番风味。灯火辉煌的高楼大厦、熙熙攘攘的人群，构成了一幅繁华的都市画卷。</p><p>【例】熙熙攘攘的游客穿梭在林间小道，听花语、拍花照、晒花照，享受着这山间的春日美景。</p><blockquote><p>人山人海</p><p>形容聚集的人非常多。</p></blockquote><p>【例】习近平抵达黎明大街路口，换乘敞篷车，与金正恩同车前往锦绣山太阳宫广场。街道两旁，人山人海，盛况空前。</p><p>【例】一个人拥有自信，我们便可以在他的身上感受到人格魅力的光芒，纵使在人山人海之中，也可 以一眼领略他的光彩。</p><p>【例】近年来，假日旅游“热辣滚烫”，休闲街区人山人海，“网红”景点层出不穷，文化游、红色 游、研学游、非遗游等越来越热，不断丰富着文旅新业态、新模式，成为我国旅游发展的亮丽风景。</p><blockquote><p>车水马龙</p><p>车像流水，马如游龙。形容车马来来往往的热闹景象。同“马龙车水”。</p></blockquote><p>【例】这座沿海城市自从被列为经济开发区之后，一时华盖云集，车水马龙，一派繁忙景象。</p><p>【例】冬日的天津，道路两旁灯笼高挂，大街小巷车水马龙。</p><p>【例】大街上车水马龙、人来人往，这座城市正逐渐恢复到喧嚣热闹的场景。</p><blockquote><p>川流不息</p><p>形容行人、车马很多，像水流一样连续不断。</p></blockquote><p>【例】历史川流不息，精神代代相传。</p><p>【例】时间之河川流不息，每一代青年都有自己的际遇和舞台。</p><p>【例】这是绵延万里的时代机遇。追赶机遇、把握机遇，热火朝天的工地、川流不息的车站，还有一座桥的修建、一条铁路的改造……面貌日新月异。</p><blockquote><p>万人空巷</p><p>空巷：街道里弄里的人全部走空。指家家户户的人都从巷里出来了。多形容庆祝、欢迎等盛况。</p></blockquote><p>【例】1974 年，北京电影制片厂译制的影片《瓦尔特保卫萨拉热窝》放映，万人空巷。一年后，又一 部南斯拉夫影片《桥》在中国上映，其主题曲《啊，朋友再见》即便今天的00 后也耳熟能详。</p><p>【例】文旅共享龙舟民俗引盛况 6 月 6 日—10 日，2024 中国·皖美山水暨第四届长三角体育节、中 国·桃花潭第十一届龙舟赛在泾县桃花潭精彩上演，营造了万人空巷的场景，活动吸引了数万名游客前来 观看和体验。</p><p>【例】武契奇总统再一次回忆起 2016 年习近平主席到访钢厂时，万人空巷的盛况：“我永远不会忘记您对斯梅戴雷沃钢厂职工发表的讲话。”“看到他们的期盼、友好，看到他们淳朴地去讲述愿望，我当 时也是深受感动。”</p><table><thead><tr><th>形容孤独</th><th>孑然一身、茕茕孑立、形影相吊、形单影只</th></tr></thead></table><blockquote><p>孑然一身</p><p>孑然：孤单的样子。指孤身一人。（常用褒义，形容追寻事业或理想）</p></blockquote><p>【例】李桓英一生孑然一身,把毕生的精力都献给了麻防事业,在国内外麻风病防治领域取得了常人难 以超越的成绩,赢得了国内外学术界的高度评价。</p><p>【例】他离家几十年了，仍然是孑然一身，两手空空。</p><p>【例】我在世上,如同天空的弯月,孑然一身,但是我却不能把任何东西照亮。</p><blockquote><p>茕茕孑立</p><p>茕茕：孤单，无依靠。孑立：孤立。形容一个人孤苦伶仃，无依无靠。</p></blockquote><p>【例】在恢弘的历史主旋律之外茕茕孑立、喃喃自语,只能被淘汰和淹没。</p><p>【例】人们在这里一边品味咖啡，一边观看展览，还有不少市民带着家人来这里感受一周一次的非遗 课堂……曾经茕茕孑立的老建筑，如今内里满是欢声笑语。</p><p>【例】在所有富裕国家中，有一个国家茕茕孑立，在 4 个月内反复遭受严重且不间断的暴发。这个国 家就是美国</p><blockquote><p>形影相吊</p><p>吊：慰问。只有自己的身体和影子相互慰问。形容无依无靠，非常孤单。</p></blockquote><p>【例】在通往胜利的道路上,选手们茕茕孑立、形影相吊,而在王者之巅,他们更是独孤求败、高处不胜寒。</p><p>【例】小山从小没了父母，一个人形影相吊，生活得很艰难。</p><p>【例】这位老人，没有子女，没有至亲，落得茕茕孑立，形影相吊。</p><blockquote><p>形单影只</p><p>只：指单独。形容孤独，没有伴侣。</p></blockquote><p>【例】作为世界第三大老龄化国家,德国 1/4 人口超过 60 岁,却只有 8%的老人与子女共同生活,三代同 堂的比例仅为 1%,独居老人比例高达 40%,形单影只的老人随处可见。</p><p>【例】从最初的形单影只，到如今村民自发加入护林队伍；从零星的防火标语，到整片林区的生态自 觉，冯再华用行动点亮了众人心中的绿色明灯。</p><p>【例】除夕夜里，万绿园迎来了不少市民游客参观，人来人往中，一个形单影只的男孩引起了民警王 明的注意。</p><hr><h3 id="【第四十组】-好坏掺杂-7-个" tabindex="-1"><a class="header-anchor" href="#【第四十组】-好坏掺杂-7-个"><span>【第四十组】 好坏掺杂（7 个）</span></a></h3><table><thead><tr><th>好坏都有</th><th style="text-align:left;">参差不齐、泥沙俱下、良莠不齐、鱼龙混杂、鱼目混珠、 滥竽充数</th></tr></thead><tbody><tr><td>不分好坏</td><td style="text-align:left;">买椟还珠</td></tr></tbody></table><ol><li>成语解释</li></ol><table><thead><tr><th>好坏都有</th><th style="text-align:left;">参差不齐、泥沙俱下、良莠不齐、鱼龙混杂、鱼目混珠、 滥竽充数</th></tr></thead></table><blockquote><p>参差不齐</p><p>参差：高低、长短、大小不齐的样子。形容不一致、有差别。（多搭配水平、质量、进展等）</p></blockquote><p>【例】记者发现，虽说众多政府机构和部门都已开通了微博，但政务微博的运营情况良莠不齐，影响 力参差不齐。</p><p>【例】目前，宣称可用于儿童的化妆品琳琅满目，但质量安全水平参差不齐。 【例】测评方法和标准五花八门，测评质量参差不齐，有的涉嫌“以商养测”及“以测养商”。</p><blockquote><p>泥沙俱下</p><p>俱：都。泥土和沙子一同随水冲了下来。比喻人或事物好坏混杂在一起。（侧重量大且好坏都有）</p></blockquote><p>【例】鼓励内容创业的百花齐放，不等于支持内容市场的泥沙俱下。内容创业者在时代风口上受益， 就应该为自己的影响力负责。</p><p>【例】站在新的历史起点上，我们面对的问题更加错综复杂，同时各种思潮、观点不断涌现、泥沙俱</p><p>【例】面对纷繁多变、鱼龙混杂、泥沙俱下的社会现象，面对学业、情感、职业选择等多方面的考量， 一时有些疑惑、彷徨、失落，是正常的人生经历。</p><blockquote><p>良莠不齐</p><p>莠：狗尾草，比喻坏人。指好人、坏人混在一起。同“良莠不一”。（现在用法不止形容人，也可形</p><p>容事物）</p></blockquote><p>【例】目前，处在发展初期的微短剧内容良莠不齐，在带来大流量的同时，也存在剧情套路化、部分 内容低俗化的问题。</p><p>【例】随着留学之风愈刮愈烈，留学生队伍也更显良莠不齐。有相当一批小留学生，甚至是在中国高 考落榜的情况下出国的。</p><p>【例】政府应该重视专业人才的培养，夏令营负责人必须能对活动设计、组织、实施的全过程进行足 够的专业性规划，否则很难治理目前夏令营市场中良莠不齐的现象。</p><blockquote><p>鱼龙混杂</p><p>比喻坏人和好人混在一起。同“龙蛇混杂”。（现在用法不止形容人，也可形容事物）</p></blockquote><p>【例】以前的艺术批评，地位非常高，也很自律；现在的批评界则是鱼龙混杂、门槛太低。</p><p>【例】市场火爆、利润丰厚，但当前整个行业显然呈现出野蛮生长的状态，真正有经验的规划师明显 供不应求，从业人员鱼龙混杂、服务水平参差不齐。</p><p>【例】在利益驱动下，越来越多人“入局”医疗美容特别是“轻医美”行业，但其中不乏无资质人员 鱼龙混杂。</p><blockquote><p>鱼目混珠</p><p>用鱼眼睛假冒珍珠。比喻以假乱真。（也可指以次充好）</p></blockquote><p>【例】近年来，中药材市场价格不断上涨，少数不法分子铤而走险：用染色的玉米须冒充藏红花；用 苹果皮代替牡丹片，甚至用淬取后的药渣“鱼目混珠”，让消费者防不胜防。</p><p>【例】选人用人应坚持正确导向，激扬浊清，褒优戒劣，谨防各种投机分子、官场油子、蜕化变质分 子混入地方各级领导班子。尤其是对那种肆意妄为、横冲直闯的“三敢干部”，应引起高度警惕，谨防他 们鱼目混珠、招摇过市、带病提拔、高调“入阁”。</p><p>【例】有些博主发布的内容真假难辨，质量良莠不齐，甚至有鱼目混珠、恶意误导之嫌。</p><blockquote><p>滥竽充数</p><p>滥：失实，不真实。竽：一种簧管乐器。比喻没有真实本领的人，混在行家队伍里充数。也比喻以次 充好。（可用于自谦）</p></blockquote><p>【例】教学是一个综合性的技能，没有专业资格证的人代表水平达不到，一旦遇到外籍教师中的滥竽。</p><p>【例】文具盲盒和其他盲盒一样，已经形成了热潮，但要警惕三无产品滥竽充数。</p><p>【例】打造经久不衰的国货“潮品”，要有文化传承的定力，避免一哄而上的冲动；要引领和创造新 需求，避免空有符号没有内涵；要有对传统文化的敬畏，避免滥竽充数。</p><table><thead><tr><th>不分好坏</th><th>买椟还珠</th></tr></thead></table><blockquote><p>买椟还珠</p><p>椟：木匣子。还：退还。比喻没有眼光，不识货，取舍失当。</p></blockquote><p>【例】很多消费者有这样的经历：花高价购买包装精美的产品，拆开“里三层外三层”的包装，商品 本身没多少价值。有人认为，过度包装是现代的“买椟还珠”。</p><p>【例】买椟还珠式的包装与茶文化尚俭的意蕴背道而驰，与绿色低碳的新发展理念格格不入。</p><p>【例】据报道，有消费者为“求娃”购买“代吃”服务，雇人代买代吃套餐获得盲盒，上演了“买椟还珠“。</p>',1899)])])}const c=p(d,[["render",b]]),k=JSON.parse('{"path":"/xc/chengyu800s/","title":"高频成语800词(上册)","lang":"zh-CN","frontmatter":{"title":"高频成语800词(上册)","createTime":"2026/01/05 22:52:00","permalink":"/xc/chengyu800s/"},"readingTime":{"minutes":285.13,"words":85539},"git":{"createdTime":1767625344000,"updatedTime":1767625344000},"filePathRelative":"xc/4.花生十三成语800词/1.高频成语800词.md","headers":[]}');export{c as comp,k as data};
