import{_ as l,c as n,a as e,o as a}from"./app-BQSCDfLE.js";const r={};function s(i,t){return a(),n("div",null,[...t[0]||(t[0]=[e("div",{style:{"text-align":"center"}},[e("p",null,"以舍得智慧构筑美丽中国")],-1),e("p",null,"  长江禁渔政策让珍稀鱼类得到保护，让母亲河重现水清鱼跃；“双碳”国家战略推进节能减排，让绿水青山变为金山银山；鼓励发展新能源推动生产变革，新质生产力成为经济强劲动能……诚然，有舍才有得，先舍后得，不舍难得。舍弃的是落后的发展方式，得到的是绿色的光明未来。为此，要将舍得智慧应用环境保护，守护蓝天碧水。",-1),e("p",null,"  “舍”是自我变革，摒弃落后的生产生活方式。“人不负青山，青山定不负人”。当前，我国经济已由高速增长阶段转向高质量发展阶段。高质量意味着更低的能耗、更小的污染和更环保的发展方式。这就需要我们改变原有的发展方式，这样才能够减少对化石能源的依赖，减少污染物和碳排放，提升生产的效率和质量，这样才能够实现永续发展。正如我国政府推进供给侧结构性改革，以壮士断腕的勇气淘汰落后产能，推动产业转型升级，守护良好的生态环境。在生活中，舍就是要自觉改变错误的生活方式，这样就能够节约宝贵的资源，养成良好的生活习惯，用个人的点滴努力汇聚成保护环境的磅礴力量。",-1),e("p",null,"  “得”是积极进取，开创绿色的转型升级道路。当前，前沿技术快速发展，人工智能、大数据、云计算等新兴产业方兴未艾。对我国而言既是机遇，也是挑战。在摒弃落后产业同时，要积极拥抱新产业，这样才能够实现在关键领域中的技术突破，提升国家整体的科技竞争力，让我国能够引领科技发展的方向，拥有国际话语权。同样，通过开发光伏、风电等新能源，能够实现能源的可持续利用和新旧动能转换，保护好生态环境和人民群众的身体健康，实现人与自然和谐共生。践行绿色低碳的生活方式也能够让每个人参与环境保护，形成热爱自然、保护自然的环保意识，提升社会的文明水平。",-1),e("p",null,"  用好舍得智慧，需要各方共同参与，绵绵用力，久久为功。政府要以政策引领，完善环境保护法律法规，对污染环境的现象零容忍，坚决治理污染，同时对新产业给与政策支持，鼓励新兴产业发展。企业要坚持创新驱动发展，加强技术研发，培养技术人才，加强与高校、科研院所合作，推动科技成果转化，力争在核心关键技术领域取得突破。公众要改变生活方式，践行低碳、绿色的生活理念，在生活中主动养成绿色出行、节约资源、垃圾分类等良好习惯，为环境保护事业做出自己的一份贡献。",-1),e("p",null,"  沉舟侧畔千帆过，病树前头万木春。站在第二个一百年奋斗目标的征程上，让我们将舍得的古老智慧应用在时代发展中，不断提升发展的质量，让绿色的底色更加明亮，打造蓝天白云、繁星闪烁、清水绿岸、鱼翔浅底的美丽中国。",-1)])])}const d=l(r,[["render",s]]),m=JSON.parse('{"path":"/sl/pdm35w3e/","title":"以舍得智慧构筑美丽中国","lang":"zh-CN","frontmatter":{"title":"以舍得智慧构筑美丽中国","createTime":"2025/12/12 15:38:48","permalink":"/sl/pdm35w3e/"},"readingTime":{"minutes":3.12,"words":936},"git":{"createdTime":1765528551000,"updatedTime":1765528551000},"filePathRelative":"sl/2.省市/1.以舍得智慧构筑美丽中国.md","headers":[]}');export{d as comp,m as data};
