import{_ as i,c as a,e,o as n}from"./app-DUT9ae1g.js";const o={};function h(s,l){return n(),a("div",null,[...l[0]||(l[0]=[e('<h2 id="一、33555-264111" tabindex="-1"><a class="header-anchor" href="#一、33555-264111"><span>一、33555,264111</span></a></h2><h3 id="三种文段" tabindex="-1"><a class="header-anchor" href="#三种文段"><span>三种文段</span></a></h3><ol><li>说理类文段 <ol><li>有对策，对策优选。（对策要务实一点）</li><li>无对策，概括问题，有针对问题的对策也可以选。</li></ol></li><li>科普类文段：客观地介绍： <ol><li>总分总，分总，重点在总。</li><li>分分，分分分分，重点在全面概括。（A且B,D=B+C）</li><li>中性表达。</li></ol></li><li>新闻类文段 <ol><li>重点在新。</li></ol></li></ol><h3 id="三种关键信息" tabindex="-1"><a class="header-anchor" href="#三种关键信息"><span>三种关键信息</span></a></h3><ol><li><p>高频词</p></li><li><p>主谓宾</p></li><li><p>首局（主体+主题）（除了背景和背景知识）</p></li></ol><h3 id="五种的对策表达方式-说理类文段" tabindex="-1"><a class="header-anchor" href="#五种的对策表达方式-说理类文段"><span>五种的对策表达方式（说理类文段）</span></a></h3><ol><li>祈使句\\建议句：应该、需要、必须、要求、可以、不妨...</li><li>禁止型对策：不能、禁止、避免...</li><li>逻辑关联词表对策：只有...才...、只要...就...、如果...就...、必须...否则...</li><li>必要条件表对策：基础，前提，关键，离不开，必不可少，不可或缺...</li><li>语义表对策：时代命题，重要议题，负有...义务，长远目标，必经之路，必由之路，必然趋势，有助于。</li></ol><h3 id="五种结构" tabindex="-1"><a class="header-anchor" href="#五种结构"><span>五种结构</span></a></h3><ol><li>后对策：引出话题+分析论证+对策\\观点。</li><li>前观点：引出话题/问题/+对策/观点+论证\\详细介绍对策。</li><li>总分、分总、总分总。</li><li>分分：（A且B）、分分分：概括全文。</li><li>二层结构 <ol><li>转折：转折后一般为重点，但有可能转折前后均为重点。 <ol><li>对比：时间对比：当今为重点。</li><li>对比：国家对比：我国为重点。</li></ol></li><li>因果\\果因：“原因类&quot;选项逆推。</li><li>背景+正文（新）：正文加新。<mark>我国</mark> <mark>新</mark></li><li>设问+回答：问题引出+正文对此问题回答。（重点回答）</li></ol></li></ol><h3 id="五种话题引出方式" tabindex="-1"><a class="header-anchor" href="#五种话题引出方式"><span>五种话题引出方式</span></a></h3><ol><li>开门见山。</li><li>转折对比引出：”前对策“结构经常用转折对比引出话题。</li><li>背景\\背景知识引出。</li><li>由大引小，大集合（所有）引出小集合（有些，特殊）。</li><li>设问引出。</li></ol><h3 id="错误选项俩出处" tabindex="-1"><a class="header-anchor" href="#错误选项俩出处"><span>错误选项俩出处</span></a></h3><ol><li>背景，例子，分析论证中的一般不选。</li><li>但是\\因此之前的不选、不是...而是...的不是部分、被指代部分一般不选。</li></ol><h3 id="六有误" tabindex="-1"><a class="header-anchor" href="#六有误"><span>六有误</span></a></h3><ol><li>杂糅：拼好句。（并列文段除外）</li><li>片面：并列文段中常见的错误选项。</li><li>绝对化：文段未提及的一般不选。</li><li>下定义：说理类文段一般不选；中性表达；一般为科普类文段选项。</li><li>本身有误：本身不符合事实；不符合主流价值观；逻辑错误。</li><li>无中生有：原文未提及，但存在原文同义替换一定要重点关注。</li></ol><h3 id="四大类" tabindex="-1"><a class="header-anchor" href="#四大类"><span>四大类</span></a></h3><ol><li>原因类： <ol><li>文段为因果\\果因结构。（既成事实）</li><li>文段为原因，选项为结果。</li></ol></li><li>历程类：时间轴。</li><li>具体类：1、2、3...</li><li>区别比较联系类：双主语。（地位，篇幅相当）</li></ol><h3 id="一正确" tabindex="-1"><a class="header-anchor" href="#一正确"><span>一正确</span></a></h3><ol><li>选项为A且B。</li><li>D=A+C</li></ol><h3 id="一比较" tabindex="-1"><a class="header-anchor" href="#一比较"><span>一比较</span></a></h3><ol><li>宽泛VS具体：选具体。</li><li>要VS不要：选要。</li><li>宏观VS微观：先宏观后微观。</li></ol><h3 id="一方法-验证方法" tabindex="-1"><a class="header-anchor" href="#一方法-验证方法"><span>一方法：验证方法</span></a></h3><ol><li>根据选项逆推文段进行验证。</li><li>揣摩出题老头的意图。</li></ol><h2 id="二、标题拟定题" tabindex="-1"><a class="header-anchor" href="#二、标题拟定题"><span>二、标题拟定题</span></a></h2><ol><li>属于主旨变形题。</li><li>标题中需要包含关键信息。（关键词可适当同义替换，主语正确，体现作者的观点或对策）</li><li>有可读性，有趣味性，有通俗易懂，引人入胜的感觉。</li><li>“ ”、？正确选项概率很高。</li></ol><h2 id="三、下文推断题" tabindex="-1"><a class="header-anchor" href="#三、下文推断题"><span>三、下文推断题</span></a></h2><ol><li>文末新内容——下文解释新内容，尾句起承上启下的作用。</li><li>上文提出问题——下文先分析问题，再解决问题。</li><li>上文给出宏观对策\\原因\\问题——下文具体展开。</li><li>时间对比，过去不行——下文：现在行。</li><li>总分：总，分1，分2，——下文：分3.</li><li>转折。</li><li>尾句比较——下文一定选择比较的内容。</li></ol><div class="hint-container info"><p class="hint-container-title">注意</p><p>不确定选项时候，纠结选项时候，一定要注意文段首句或第二句，但背景和背景知识除外。</p></div><h2 id="四、语句填入题" tabindex="-1"><a class="header-anchor" href="#四、语句填入题"><span>四、语句填入题</span></a></h2><ol><li>既要把握宏观整体，又要把握微观细节，具体情境具体择优而选。</li><li>开头 <ol><li>前对策，后文为论证。（总分）</li><li>提出问题，尾句肯定是对策或观点。</li><li>总概括：后文分1，分2，分3。</li></ol></li><li>中间 <ol><li>承上启下</li><li>背景，问题，分析论证，对策\\观点。</li></ol></li><li>总结： <ol><li>分分总，总是总结句。</li><li>对策句；作者的观点。</li></ol></li></ol><h2 id="五、语句排序题" tabindex="-1"><a class="header-anchor" href="#五、语句排序题"><span>五、语句排序题</span></a></h2><ol><li>从选项入手，从首句入手，想其他。</li><li>宏观把握： <ol><li>结构 <ol><li>说理类：前对策，后对策。</li><li>科普类：分1，分2，分3。</li><li>新闻类：背景+新。</li></ol></li><li>分堆</li><li>定义</li><li>引用</li><li>固定顺序（时间轴）</li></ol></li><li>微观把握 <ol><li>首句：下定义、由大引小、时间、背景。</li><li>尾句：总结、展望未来。</li><li>首位呼应：</li><li>捆绑\\组合：指代词，相同词。（可以在捆绑之中找顺序）</li></ol></li></ol><div class="hint-container info"><p class="hint-container-title">注意</p><p>找顺序，不应先考虑捆绑！！！</p></div><h2 id="六、细节判断题" tabindex="-1"><a class="header-anchor" href="#六、细节判断题"><span>六、细节判断题</span></a></h2><ol><li>偷换概念。</li><li>混淆时态。</li><li>杂糅。</li><li>本身有误。（不符合实际）</li><li>绝对化。</li><li>无中生有。</li></ol>',35)])])}const d=i(o,[["render",h]]),c=JSON.parse('{"path":"/xc/0zmicamv/","title":"言语总结","lang":"zh-CN","frontmatter":{"title":"言语总结","createTime":"2025/12/12 15:35:19","permalink":"/xc/0zmicamv/"},"readingTime":{"minutes":4.53,"words":1360},"git":{"createdTime":1765528551000,"updatedTime":1768055320000},"filePathRelative":"xc/1.总结/1.言语总结.md","headers":[]}');export{d as comp,c as data};
